import React from "react";

var ranges = [
  { divider: 1e9, suffix: "B" },
  { divider: 1e6, suffix: "M" },
  { divider: 1e3, suffix: "k" },
];
function formatNumber(n) {
  for (var i = 0; i < ranges.length; i++) {
    if (n >= ranges[i].divider) {
      let newNumber = (n / ranges[i].divider).toFixed(2);
      let roundOffNumber = Math.abs(newNumber);
      return roundOffNumber.toString() + ranges[i].suffix;
    }
  }
  return n;
}

const InventoryInfo = (props) => {
  return (
    <div>
      <h5 className="font-size-14 font-bold" style={{ paddingLeft: "12px" }}>
        Inventory Info
      </h5>{" "}
      <div className="row inventory-info-row">
        <div className="col-md-4">
          <div className="info-container">
            <div className="info-icon">
              <i className="inventory-icon"></i>
            </div>

            <div className="info-data ml-2">
              <p className="font-size-13 mb-0 font-normal">
                Inventory Selected
              </p>
              <div className="info-numbers">
                {formatNumber(props.currentInventory)}/
                <span className="font-size-14 font-bold">
                  {formatNumber(props.totalInventory)}
                </span>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="info-container">
            <div className="info-icon">
              <i className="store-icon"></i>
            </div>

            <div className="info-data ml-2">
              <p className="font-size-13 mb-0 font-normal ">Stores Selected</p>
              <div className="info-numbers ">
                {formatNumber(props.selectedStores)}/
                <span className="font-size-14 font-bold">
                  {formatNumber(props.totalStores)}
                </span>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-4" style={{ marginTop: "8px" }}>
          <div className="d-flex">
            <p
              className="font-size-13 mb-0 font-600"
              style={{ lineHeight: "20px" }}
            >
              {" "}
              Limit clearance <br />
              <span className="font-i">to few Stores</span>
            </p>
            <button
              className="btn btn-primary"
              style={{ height: "30%", marginLeft: "12px" }}
              onClick={() => props.onExcludeStores()}
            >
              Exclude Stores
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
export default InventoryInfo;
