import axios from 'axios';
import { config } from '../utils/config';
import { toast } from 'react-toastify';

const storeConfigActions = {
  fetchStoreGroups: () => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/get-storesgroups`,
        method: 'GET',
      })
        .then((resp) => {
          console.log(resp.data.data, 'storesgroups');
          dispatch({
            type: 'SET_STORE_GROUPS',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  getStoreGroupTableData: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/get-stores/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          console.log(resp.data.data, 'get-stores');
          dispatch({
            type: 'SET_STORE_GROUP_TABLE_DATA',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  createStoreGroup: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/get-stores-create/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Store Group Created Successfully', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(resp, 'get-stores-create');
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err);
        });
    };
  },
};

export default storeConfigActions;
