import React, { Component } from 'react';
import Tippy from '@tippy.js/react';
import Select from 'react-select';
import AnalysisTable from '../MarkdownAnalysis/AnalysisTable';
import moment from 'moment';
import 'tippy.js/dist/tippy.css';
import BarChart from '../support/Charts/Barchart';
import './index.css';
import _ from 'lodash';
const options2 = {
  tooltips: {
    callbacks: {
      label: function (tooltipItem, data) {
        let label = data.labels[tooltipItem.index];
        let value = data.datasets[tooltipItem.datasetIndex].data[tooltipItem.index];
        let labelTip = 'Week' + ':' + label;
        let valueTip = 'Margin%' + ':' + value;
        return [labelTip, valueTip];
      },
    },
  },
  plugins: {
    datalabels: {
      display: false,
    },
  },
  legend: {
    display: false,
    position: 'bottom',
    labels: {
      boxWidth: 12,
    },
    fontFamily: 'Helvetica',
    fontSize: '12px',
  },
  maintainAspectRatio: false,
  scales: {
    yAxes: [
      {
        stacked: true,
        gridLines: {
          display: true,
        },
        ticks: {
          fontSize: 13,
          suggestedMin: 0,
        },
        scaleLabel: {
          show: true,
          display: true,
          fontSize: 14,
          fontStyle: 'bold',
          fontColor: '#000',
          labelString: 'Margin %',
        },
      },
    ],
    xAxes: [
      {
        id: 'x-axis-0',
        categoryPercentage: 1.0,
        barPercentage: 1.0,
        stacked: true,
        gridLines: {
          display: false,
        },
        ticks: {
          autoSkip: true,
          autoSkipPadding: 40,
          maxRotation: 0,
          suggestedMin: 0,
        },
        scaleLabel: {
          display: true,
          labelString: 'Time (week)',
          fontWeight: 900,
          fontStyle: 'bold',
        },
      },
    ],
  },
};
const options = {
  tooltips: {
    callbacks: {
      label: function (tooltipItem, data) {
        let label = data.labels[tooltipItem.index];
        let value = data.datasets[tooltipItem.datasetIndex].data[tooltipItem.index];
        let labelTip = 'Week' + ':' + label;
        let valueTip = 'ST%' + ':' + value;
        return [labelTip, valueTip];
      },
    },
  },
  plugins: {
    datalabels: {
      display: false,
    },
  },
  legend: {
    display: false,
    position: 'bottom',
    labels: {
      boxWidth: 12,
    },
    fontFamily: 'Helvetica',
    fontSize: '12px',
  },
  maintainAspectRatio: false,
  scales: {
    yAxes: [
      {
        stacked: true,
        gridLines: {
          display: true,
        },
        // ticks: {
        //   fontSize: 13,
        //   suggestedMin: 0,
        // },
        scaleLabel: {
          show: true,
          display: true,
          fontSize: 14,
          fontStyle: 'bold',
          fontColor: '#000',
          labelString: 'Sell Through %',
        },
      },
    ],
    xAxes: [
      {
        id: 'x-axis-0',
        categoryPercentage: 1.0,
        barPercentage: 1.0,
        gridLines: {
