import axios from "axios";
import { config } from "../../../../utils/config";
import {
  ADGROUP_DATA,
  GET_ERRORS,
  GET_ADGROUP_DATA,
  GET_SHOP_DATA,
  LOADER,
  GET_MERCHCAT,
  GET_DISTINCT_ADGROUP,
  GET_CAMPAIGN_DM,
  UPDATE_PRODUCTS_ADGROUP,
  PROMO_FILTERS_SHOP,
  PROMO_FILTERS_MERCH,
  PROMO_FILTERS_ADGROUP,
  PROMO_FILTERS_CUSTGROUP,
  PROMO_FILTERS_BUSINESS,
  PROMO_FILTERS_CATEGORY,
  PRODUCT_DETAILS_ID
} from "./promoTypes";
import setAuthToken from "../../../../utils/setAuthToken";
//config.baseUrl = config.joBaseUrl;
//console.log(config.joToken);
export const getAdgroupData = reqObject => dispatch => {
  setAuthToken(localStorage.getItem("token"),true);
  dispatch({
    type: LOADER,
    payload: true
  });
  axios
    .get(`${config.joBaseUrl}/adgroup/listadgroups?searchkey=${reqObject}`)
    .then(res => {
      dispatch({
        type: ADGROUP_DATA,
        payload: res.data.data
      });
      dispatch({
        type: LOADER,
        payload: false
      });
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err
      });
    });
};

export const updateAdgroup = reqObject => dispatch => {
  setAuthToken(localStorage.getItem("token"),true);

  dispatch({
    type: LOADER,
    payload: true
  });
  axios
    .post(`${config.joBaseUrl}/adgroup/editadgroup`, reqObject)
    .then(res => {
      if (res.data.status) {
        dispatch({
          type: GET_ADGROUP_DATA,
          payload: res.data.data
        });
        dispatch({
          type: LOADER,
          payload: false
        });
      } else {
        let payload = {
          message: res.data.msg
        };
        dispatch({
          type: GET_ERRORS,
          payload: payload
        });
        dispatch({
          type: LOADER,
          payload: false
        });
      }
    })
    .catch(err => {
      dispatch({
        type: GET_ERRORS,
        payload: err
      });
    });
};
