import React, { Component } from "react";
import CalendarWrapperDataDiv from "./calendarWrapperDataDiv";

class calendarWrapperBlocks extends Component {
  constructor(props) {
    super(props);

    this.state = {
      styleMargin: []
    };
  }

  componentDidUpdate(old) {
    console.log("props in didupdate", old);
    if (old.styleMargin !== this.props.styleMargin) {
      this.setState({
        styleMargin: this.props.styleMargin
      });
    }
  }

  render() {
    let renderBlocks = this.props.calendarDataArr &&
                        this.props.calendarDataArr.length && this.props.calendarWrapperArr.length &&
                        this.state.styleMargin && this.state.styleMargin.length &&
                        this.props.calendarDataArr.length === this.state.styleMargin.length;

    return (
      <div>
        <div
          style={{
            maxHeight: "1rem",
            borderBottom: "1px solid #E0E1E2",
            marginTop: "5px"
          }}
        />
        <div style={{ maxHeight: "14.9rem", overflow: "auto", width: "100%" }}>
          <div
            className="scrollCalender"
            style={{ maxHeight: "14.9rem", overflow: "auto", width: "100%" }}
          >
            {/* -----------------number of rows per data-------------- */}
            {renderBlocks ? (
              this.props.calendarDataArr.map((data, indexRow) => {
                return (
                  <div
                    key={indexRow}
                    className={
                      this.state.styleMargin[indexRow].flexBasisInt &&
                      this.state.styleMargin[indexRow].flexBasisInt > 0
                        ? "RowWrapper"
                        : ""
                    }
                  >
                    <div className="RowBodyWrapper" style={{ width: "100%" }}>
                      {/* --------------number of weeks per row------------- */}
                      <div className="BgWrapper">
                        {this.props.calendarWrapperArr.map((data, indextr) => {
                          return (
                            <div
                              key={indextr}
                              style={{ width: "25%" }}
                              className="CellWrapper row-cell-wrapper"
                            >
                              <p className="calendarWeekDate"></p>
                            </div>
                          );
                        })}
                      </div>
                      {/* --------------------Promotion Div----------------------- */}
                      {this.state.styleMargin[indexRow].flexBasisInt &&
                      this.state.styleMargin[indexRow].flexBasisInt > 0 ? (
                        <div className="RowContent" style={{ width: "100%" }}>
                          <div>
                            {this.state.styleMargin[indexRow].flexBasisInt &&
                            this.state.styleMargin[indexRow].flexBasisInt >
                              0 ? (
                              <div>
                                <CalendarWrapperDataDiv
                                  indexRow={indexRow}
                                  calendarDataArr={this.props.calendarDataArr}
                                  styleMargin={this.state.styleMargin[indexRow]}
                                  showRecommendedPromoToolTip={
                                    this.props.showRecommendedPromoToolTip
                                  }
                                  tooltipId={this.props.tooltipId}
                                  toogleRecommendedPromoToolTip={
                                    this.props.toogleRecommendedPromoToolTip
                                  }
                                  markdownlinkShow={this.props.markdownlinkShow}
                                  showPromotionMain={
                                    this.props.showPromotionMain
                                  }
                                  showPromotionMainWithValues={
                                    this.props.showPromotionMainWithValues
                                  }
                                  info={this.props.calendarDataArr[indexRow]}
                                  onTabClick={this.props.onTabClick}
                                  getRecommendedData={
                                    this.props.getRecommendedData
                                  }
                                  toogleRecommendedPromoMarkdownToolTip={
                                    this.props
                                      .toogleRecommendedPromoMarkdownToolTip
                                  }
                                  getRecommendedMarkData={
                                    this.props.getRecommendedMarkData
                                  }
                                  promoButtonClick={this.props.promoButtonClick}
                                  promoDetailsHandler={this.props.promoDetailsHandler}
                                  showRecommendedPromoMarkToolTip={this.props.showRecommendedPromoMarkToolTip}
                                  tooltipMarkId={this.props.tooltipMarkId}
                                />
                              </div>
                            ) : (
                              <div className="Row" height="200"></div>
                            )}
                          </div>
                        </div>
                      ) : null}
                    </div>
                  </div>
                );
              })
            ) : this.props.showLoading === true ? (
              <div
                className="Row"
                height="200"
                align="middle"
                style={{ justifyContent: "center", margin: "2rem 0" }}
              >
                Please wait while data is loading...
              </div>
            ) : (
              <div
                className="Row"
                height="200"
                align="middle"
                style={{ justifyContent: "center", margin: "2rem 0" }}
              >
                No data to show!
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default calendarWrapperBlocks;
