import React, { Component } from 'react';
import _ from 'underscore';
import DataTable from '../../services/reactTable';
import { ExportToCsv } from 'export-to-csv';
import { toUnit, toDollar, toPercentage } from '../../helper/formatter';
import './index.css';

export class SKUCocktpit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      checkedSku: [],
      loader: false,
      showAlertWarning: false,
      tableData: this.props.cockpitData.length ? this.props.cockpitData : [],
    };
  }

  downloadTable(columns) {
    const options = {
      fieldSeparator: ',',
      quoteStrings: '"',
      decimalSeparator: '.',
      showLabels: true,
      showTitle: true,
      title: 'Sub Category Data',
      useTextFile: false,
      useBom: true,
      useKeysAsHeaders: true,
      // headers: ['Column 1', 'Column 2', etc...] <-- Won't work with useKeysAsHeaders present!
    };

    const csvExporter = new ExportToCsv(options);

    csvExporter.generateCsv(this.props.cockpitData.data);
  }

  roundedMedian = (leafValues) => {
    let min = leafValues[0] || 0;
    let max = leafValues[0] || 0;

    leafValues.forEach((value) => {
      min = Math.min(min, value);
      max = Math.max(max, value);
    });

    return Math.round((min + max) / 2);
  };

  updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    this.setState((old) => {
      let val = old.tableData.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...row,
            [columnId]: value,
          };
        }
        return row;
      });
      return { tableData: val };
    });
  };
  header = [
    {
      Header: 'Style-Color',
      columns: [
        {
          Header: 'color',
          columns: [
            {
              Header: '',
              accessor: 'style_number',
              groupByID: 'style_number',
              disableGroupBy: true,
              align: 'left',
              showFilter: true,
            },
          ],
        },
      ],
    },
    {
      Header: 'SEll',
      columns: [
        {
          Header: 'Sell through %',
          columns: [
            {
              Header: 'TY',
              accessor: 'sell_through_ty',
              aggregate: 'sum',
              disableGroupBy: true,
              align: 'left',
              Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
            },
            {
              Header: 'LY',
              accessor: 'sell_through_ly',
              disableGroupBy: true,
              align: 'left',
              Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
            },
          ],
        },
      ],
    },

    {
      Header: 'Margin Metrics',
      columns: [
        {
          Header: 'Margin %',
          columns: [
            {
              Header: 'TY',
              accessor: 'margin_ty',
              disableGroupBy: true,
              aggregate: this.roundedMedian,
              Aggregated: ({ value }) => `${value}`,
              align: 'left',
              Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
            },
            {
              Header: 'LY',
              accessor: 'margin_ly',
              disableGroupBy: true,
              align: 'left',
              Cell: (props) => <div> {toPercentage({ value: props.value })} </div>,
            },
            {
              Header: 'Margin ($)',
              accessor: 'margin$',
              disableGroupBy: true,
              Aggregated: ({ value }) => `${value}`,
              align: 'centre',
              Cell: (props) => <div> {toDollar(props)} </div>,
            },

            {
              Header: 'AUM',
              accessor: 'aum',
              disableGroupBy: true,
              Aggregated: ({ value }) => `${value}`,
              align: 'centre',
              Cell: (props) => <div> {toDollar(props)} </div>,
            },
          ],
        },
      ],
    },

    {
      Header: 'Sales Metrics',
      columns: [
        {
          Header: 'sales',
          columns: [
            {
              Header: 'Actual sales (units)',
              accessor: 'units_sold',
              aggregate: 'average',
              disableGroupBy: true,
              Aggregated: ({ value }) => `${value}`,
              align: 'centre',
              Cell: (props) => <div> {toUnit(props)} </div>,
            },
            {
              Header: 'Revenue ($)',
              accessor: 'revenue$',
              disableGroupBy: true,
              Aggregated: ({ value }) => `${value}`,
              align: 'centre',
              Cell: (props) => <div> {toDollar(props)} </div>,
            },
            {
              Header: 'AUR',
              accessor: 'aur',
              disableGroupBy: true,
              Aggregated: ({ value }) => `${value}`,
              align: 'centre',
              Cell: (props) => <div> {toDollar(props)} </div>,
            },
            {
              Header: "Last week's sales",
              accessor: 'last_week_sales',
              disableGroupBy: true,
              Aggregated: ({ value }) => `${value}`,
              align: 'centre',
            },
          ],
        },
      ],
    },
    {
      Header: 'Current inventory',
      columns: [
        {
          Header: 'inve',
          columns: [
            {
              Header: 'On hand',
              accessor: 'inventory.inventory_on_hand',
              aggregate: 'average',
              disableGroupBy: true,
              align: 'left',
              Cell: (props) => <div> {toUnit(props)} </div>,
            },
            {
              Header: 'In transit',
              accessor: 'inventory.inventory_in_transit',
              aggregate: 'average',
              disableGroupBy: true,
              align: 'left',
              Cell: (props) => <div> {toUnit(props)} </div>,
            },
            {
              Header: 'On order',
              accessor: 'inventory.inventory_on_order',
              aggregate: 'average',
              disableGroupBy: true,
              align: 'left',
              Cell: (props) => <div> {toUnit(props)} </div>,
            },
            {
              Header: 'In DC',
              accessor: 'inventory.inventory_in_dc',
              aggregate: 'average',
              disableGroupBy: true,
              align: 'left',
              Cell: (props) => <div> {toUnit(props)} </div>,
            },
            {
              Header: 'Weeks on hand',
              accessor: 'wos',
              disableGroupBy: true,
              Aggregated: ({ value }) => `${value}`,
              align: 'centre',
              Cell: (props) => <div> {toUnit(props)} </div>,
            },
          ],
        },
      ],
    },

    // {
    //   Header: 'Markdown status',
    //   columns: [
    //     {
    //       Header: '',
    //       columns: [
    //         {
    //           Header: '',
    //           accessor: 'mdStatus',
    //           disableGroupBy: true,
    //           align: 'centre',
    //           showFilter: true,
    //           Cell: (props) => {
    //             switch (props.row.original.mdStatus) {
    //               case 'High':
    //                 return <div className="high-status">High</div>;
    //               case 'Medium':
    //                 return <div className="medium-status">Medium</div>;
    //               case 'Low':
    //                 return <div className="low-status">Low</div>;
    //               default:
    //                 return <div></div>;
    //             }
    //           },
    //         },
    //       ],
    //     },
    //   ],
    // },
  ];

  onRowClick = (rowInfo) => {
    let selectedRow = [];
    if (Object.keys(rowInfo).length) {
      Object.keys(rowInfo).forEach((row) => {
        selectedRow.push(this.props.cockpitData[row].style_number);
      });
      this.setState({
        checkedSku: selectedRow,
      });
    } else {
      this.setState({
        checkedSku: [],
      });
    }
  };
  render() {
    // let count = 0;
    // if (this.props.cockpitData && this.props.cockpitData.data) {
    //   this.props.cockpitData.data.forEach((item) => {
    //     if (item.indicator === 'URGENT') {
    //       count++;
    //     }
    //   });
    // }
    return (
      <div className="promo">
        <div className="card" id="">
          <div className="" style={{ padding: '10px 9px' }}>
            <div className="promolist-header pull-left">
              <label className="pipelineLabel">Style-Color level details</label>
            </div>
            <button
              onClick={() => this.downloadTable(this.header)}
              className="btn btn-primary iconbutton download-btn">
              <span>
                <i className="fa fa-download" aria-hidden="true" />
              </span>
            </button>
            {/* <span style={{ color: 'red' }}>{count + ' SKus are to be Marked Down'}</span> */}
            <button
              disabled={this.state.checkedSku.length === 0 ? true : false}
              type="button"
              className="btn btn-primary pull-right"
              onClick={() => this.props.onCreateMarkdown(this.state.checkedSku)}>
              Create Markdown
            </button>
          </div>

          <div style={{ padding: '1rem' }}>
            {this.props.cockpitData && this.props.cockpitData.length > 0 && (
              <DataTable
                columns={this.header}
                data={this.props.cockpitData}
                updateMyData={this.updateMyData}
                visibleColumns="onlyRowSelection"
                isPagination={this.props.cockpitData.length > 10}
                isSort={this.props.cockpitData.length > 1}
                selectRow={this.onRowClick}
              />
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default SKUCocktpit;
