import React from 'react';
import _ from 'lodash';

export default () => {
  const colDef = [
    {
      // Make an expander cell
      Header: 'MD Plan',
      id: 'expander', // It needs an ID
      Cell: ({ row }) => (
        // Use Cell to render an expander for each row.
        // We can use the getToggleRowExpandedProps prop-getter
        // to build the expander.
        <span {...row.getToggleRowExpandedProps()}>
          {row.original.md_plan}
          {row.isExpanded ? (
            <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
          ) : (
            <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
          )}
        </span>
      ),
    },
    // {
    //   Header: 'MD Plan',
    //   accessor: 'md_plan',
    // },
    {
      Header: 'Scenario',
      accessor: 'scenario',
      // showFilter: true,
      disableGroupBy: true,
      //   enableRowSpan: 'markdown_id',
      width: 50,
    },
    {
      Header: 'Start Date',
      accessor: 'start_date',
      // showFilter: true,
      disableGroupBy: true,
      //   enableRowSpan: 'markdown_id',
      width: 50,
    },
    {
      Header: 'End Date',
      accessor: 'end_date',
      // showFilter: true,
      disableGroupBy: true,
      //   enableRowSpan: 'markdown_id',
      width: 50,
    },
    {
      Header: '# of Style Color',
      accessor: 'total_style_color',
      // showFilter: true,
      disableGroupBy: true,
      //   enableRowSpan: 'markdown_id',
      width: 50,
    },
    {
      Header: '# of Stores',
      accessor: 'total_stores',
      // showFilter: true,
      disableGroupBy: true,
      //   enableRowSpan: 'markdown_id',
      width: 50,
    },
    {
      Header: 'Status',
      accessor: 'status',
      disableGroupBy: true,
      // showFilter: true,
      Cell: StatusCellStyling,
    },
  ];
  return colDef;
};

function StatusCellStyling({ value }) {
  switch (value) {
    case 'rejected':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i className="fa fa-ban fa-lg" aria-hidden="true" style={{ color: '#D84039' }}></i>{' '}
          {value} &nbsp; &nbsp;{' '}
          {/* <i
            className="fa fa-commenting fa-lg"
            aria-hidden="true"
            style={{ cursor: 'pointer' }}></i> */}
        </span>
      );
    case 'draft':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i className="fa fa-edit fa-lg" aria-hidden="true" style={{ color: '#44677B' }}></i>{' '}
          {value} &nbsp; &nbsp;{' '}
        </span>
      );
    case 'pending':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i
            className="fa fa-exclamation-circle fa-lg"
            style={{ color: '#E0D337' }}
            aria-hidden="true"></i>{' '}
          {value}
        </span>
      );
    case 'approved':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i
            className="fa fa-check-circle fa-lg"
            aria-hidden="true"
            style={{ color: '#59C770' }}></i>{' '}
          {value}
        </span>
      );

    default:
      return '';
  }
}
