import React, { Component } from 'react';
import Filters from '../../services/filters';
import SkuCockpit from '../../components/SKUCockpit/SKUCockpit';
import LineChart from '../support/Charts/LineChart';
import BarChart from '../support/Charts/Barchart';
import { connect } from 'react-redux';
import * as chartjs from 'chart.js';
import _ from 'lodash';
import './index.css';
import reportingActions from '../../actions/reportingActions';
import LoadingOverlay from '../../utils/Loader';
import moment from 'moment';
import { decimalFormatter, toUnit, formatThousands, toDollar } from '../../helper/formatter';
import { toast } from 'react-toastify';
import filterActions from '../../actions/filtersAction';
import styled from 'styled-components';
import SkuColDef from '../../../src/components/SKUCockpit/skuColdef.js';

chartjs.pluginService.register({
  beforeDraw: function (chart) {
    if (chart.config.options && chart.config.options.backgroundColor) {
      var ctx = chart.chart.ctx;
      var chartArea = chart.chartArea;
      ctx.save();
      ctx.fillStyle = chart.config.options.backgroundColor;
      ctx.fillRect(
        chartArea.left,
        chartArea.top,
        chartArea.right - chartArea.left,
        chartArea.bottom - chartArea.top
      );
      ctx.restore();
    }
  },
});

function AggregateFunc(subRows, accessor) {
  let value = 0;
  _.map(subRows, (item) => {
    value = item[accessor] ? value + item[accessor] : value;
  });
  return value / subRows.length;
}
function mainAggregateFunc(subRows, accessor) {
  let value = 0;
  _.map(subRows, (item) => {
    let aggregate = AggregateFunc(item.subRows, accessor);
    value = aggregate ? value + aggregate : value;
  });
  return value / subRows.length;
}
function FormatterStyling(props, value, val) {
  let val1 = val || val == 0 ? val : props.value;
  let props1 = val || val == 0 ? { value: val } : props;
  if (
    value == 'units_sold' ||
    value == 'last_week_sales' ||
    value == 'inventory.inventory_on_hand' ||
    value == 'inventory.inventory_in_transit' ||
    value == 'inventory.inventory_on_order' ||
    value == 'inventory.inventory_on_hand' ||
    value == 'inventory.inventory_in_dc' ||
    value == 'wos'
  ) {
    return props === 'downloadFormatter' ? toUnit : val1 || val1 == 0 ? toUnit(props1) : '-';
  }
  if (
    value == 'revenue$' ||
    value == 'aur' ||
    value == 'margin$' ||
    value == 'aum' ||
    value == 'overall_aum' ||
    value == 'overall_revenue$' ||
    value == 'overall_aur' ||
    value == 'overall_margin$'
  ) {
    return props === 'downloadFormatter' ? toDollar : val1 || val1 == 0 ? toDollar(props1) : '-';
  }

  return '-';
}
class MarkdownReporting extends Component {
  constructor() {
    super();
    this.state = {
      columnOptions: [],
      selLabels: [],
      selectedItems: [],
      columns: [],
      buttonClicked: false,
    };
  }
  sellThroughOptions = {
    backgroundColor: '#fff',
    tooltips: {
      callbacks: {
        label: function (tooltipItem) {
          return decimalFormatter({ value: tooltipItem.value });
        },
      },
    },
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Sell through %',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
          },
        },
      ],
    },
  };
  inventoryOptions = {
    backgroundColor: '#fff',
    tooltips: {
      callbacks: {
        label: function (tooltipItem) {
          return toUnit({ value: tooltipItem.value });
        },
      },
    },
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Units',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
            callback: (value) => {
              return formatThousands(value);
            },
          },
        },
      ],
    },
  };
  marginOptions = {
    backgroundColor: '#fff',
    tooltips: {
      callbacks: {
        label: function (tooltipItem) {
          return decimalFormatter({ value: tooltipItem.value });
        },
      },
    },
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Margin %',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
          },
        },
      ],
    },
  };
  salesComparisonOptions = {
    legend: { display: false },
    backgroundColor: '#fff',
    tooltips: {
      callbacks: {
        label: function (tooltipItem) {
          return toUnit({ value: tooltipItem.value });
        },
      },
    },
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Period',
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: 'Units',
          },
          ticks: {
            suggestedMin: 0, // minimum will be 0, unless there is a lower value.
            beginAtZero: true, // minimum value will be 0.
            callback: (value) => {
              return formatThousands(value);
            },
          },
        },
      ],
    },
  };
  getDefaultHeaders = () => {
    if (this.props.columnHeaders && Object.keys(this.props.columnHeaders).length) {
      let defaultHeader = Object.keys(this.props.columnHeaders.default);

      let setDefaultColDef = SkuColDef();
      let setDefaultLabel = [];
      let setDefaultColumnOptions = [];
      let setDefaultColumnOptions1 = [];
      let setDefaultItems = [];
      let colHeaders = _.cloneDeep(this.props.columnHeaders);
      let labels = Object.keys(colHeaders);
      labels.splice(labels.indexOf('fixed'), 1);
      labels.splice(labels.indexOf('default'), 1);
      for (const label of labels) {
        let labelArray = Object.keys(this.props.columnHeaders[label]);
        function diff(A = labelArray, B = defaultHeader) {
          return A.filter(function (a) {
            return B.indexOf(a) !== -1;
          });
        }

        if (diff().length) {
          setDefaultColumnOptions.push(...Object.keys(this.props.columnHeaders[label]));
          setDefaultColumnOptions1.push(this.props.columnHeaders[label]);
          setDefaultItems.push(...diff());
          setDefaultLabel.push(label);
        }
      }
      let options = setDefaultColumnOptions.map((label) => {
        let find = null;
        _.map(setDefaultColumnOptions1, (item) => {
          if (item[label]) {
            find = item[label];
          }
        });
        let findKey = _.findKey(this.props.columnHeaders, (item) => {
          return item[label] === find;
        });
        return {
          label: label,
          value: find,
          metricName: findKey,
        };
      });
      let selectedItems = setDefaultItems.map((label, i) => {
        let find = null;
        _.map(setDefaultColumnOptions1, (item) => {
          if (item[label]) {
            find = item[label];
          }
        });
        let findKey = _.findKey(this.props.columnHeaders, (item) => {
          return item[label] === find;
        });

        return {
          label: label,
          value: find,
          metricName: findKey,
        };
      });
      this.setState({
        columns: setDefaultColDef,
        selLabels: setDefaultLabel,
        selectedItems: selectedItems,
        columnOptions: options,
      });
      if (selectedItems) {
        let filteredHeader = _.find(options, { accessor: selectedItems[0].value });
        if (!filteredHeader) {
          this.onHandleDropdownSelect(selectedItems, true, setDefaultColDef);
        }
      }
    }
  };
  mandatoryFiltersVal = () => {
    return (
      this.props.selectedHierarchy1 &&
      this.props.selectedHierarchy2.length &&
      this.props.selectedHierarchy3.length &&
      this.props.selectedHierarchy4.length &&
      this.props.selectedHierarchy5.length &&
      this.props.selectedHierarchy6.length &&
      this.props.selectedHierarchy7.length &&
      this.props.selectedHierarchy8.length &&
      this.props.selectedStores.length &&
      this.props.startDate &&
      this.props.endDate
    );
  };
  onFilter = () => {
    if (!this.mandatoryFiltersVal()) {
      toast.error(`Please select mandatory filters`, {
        autoClose: 15000,
        hideProgressBar: true,
      });
      return;
    }
    this.getReportingData('week_level_aggregated_data');
  };
  onCreateMarkdown = async (items) => {
    await this.props.setRetainFilters(true);
    this.props.history.push({
      pathname: '/vis/Markdown/create-markdown',
      state: { styles: items },
    });
  };
  getReportingData = (reportingType) => {
    let req = {
      filters: {
        hierarchy1: ['WOMENS BAG'],
        hierarchy2: ['WOMENS BAGS'],
        hierarchy3: ['LEATHER', 'LOGO', 'MIXED MATERIAL'],
        hierarchy4: [
          'BACKPACKS',
          'SATCHELS',
          'SPECIAL PROJECT',
          'SWINGPACK',
          'TOTES',
          'CLUTCHES',
          'CARRYALL',
          'DUFFLES',
          'SHOULDER BAG',
          'OTHER',
          'CROSSBODY',
        ],
        hierarchy5: [
          'CAMPBELL',
          'SPECIAL PROJECTS',
          'OTHER',
          'KEY ITEMS',
          'N/A',
          'WHS EXCLUSIVE BRIDGE',
          'COACH 1941',
          'SOFT DUFFLES',
          'KRISTIN',
          'COACH CAPSULE',
          'COACH COLLECTION',
          'SUTTON',
          'STRAW',
          'COACH CLASSICS',
          'COACH ORIGINALS',
          'DOWNTOWN',
          'TAYLOR',
          'SELENA GOMEZ',
          'COACH 1941 ICONS',
          'HAMPTONS WEEKEND',
          'BEST OF GATHERED LEATHER',
          'BLEECKER',
          'LEGACY',
          'COACH PEYTON',
          'MADISON',
          'BEACH',
          'UPTOWN',
          'HADLEY',
          'SIGNATURE STRIPE',
          'GETAWAY',
          'PLAY',
          'BOX PROGRAM',
          'PARK',
          'DAISY',
          'METRO',
          'LEATHER',
          'NON LEATHER',
          'COLETTE',
          'DISPOSITION',
          'CORE',
          'NOVELTY LEATHER',
          'CHELSEA',
          'JOSIE',
          'CHARLIE',
          'ASHLEY',
          'POPPY',
        ],
        hierarchy6: ['SIGNATURE PVC'],
        hierarchy7: [
          'BACKPACKS',
          'CARRYALLS',
          'CROSSBODY',
          'SATCHELS',
          'SHOULDER BAG',
          'SWING PACKS',
          'TOTES',
        ],
        hierarchy8: ['MINI ABBY DUFFLE', 'ABBY DUFFLE'],
        tiers: [
          'FS SOUTH (GKING)',
          'FS WEST (NCONATSER)',
          'FS NORTHEAST (HSALABAY)',
          'FS SOUTHWEST (RBUTLER)',
          'FS CENTRAL (RODY)',
        ],
        customStyleGroup: [59, 81, 85, 86, 49, 50, 88, 66, 60, 82],
        customStoreGroup: [],
      },
      startDate: moment(this.props.startDate).format('YYYY-MM-DD'),
      endDate: moment(this.props.endDate).format('YYYY-MM-DD'),
      week: this.props.weeks,
      reportingType,
    };
    if (!this.props.selectedCustomStoreGroup.length) {
      delete req.filters['customStoreGroup'];
    }
    if (!this.props.selectedCustomStyleGroup.length) {
      delete req.filters['customStyleGroup'];
    }
    this.props.getReportingData(req);
    this.getDefaultHeaders();
  };
  getReportingDataStyleGroup = () => {
    this.getReportingData('style_level_detailed_data');
    this.setState({ buttonClicked: true });
  };
  componentWillUnmount() {
    this.props.onReset();
  }
  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((key) => {
          let columns = this.props.columnHeaders[key];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((item) => {
            let findKey = _.findKey(this.props.columnHeaders, (val) => {
              return val[item] === columns[item];
            });
            return {
              label: item,
              value: columns[item],
              metricName: findKey,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = _.cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          // let findKey = _.findKey(this.props.columnHeaders, (item) => {
          //   return item[label] === columns[label];
          // });
          let options = labelColumns.map((label) => {
            let findKey = _.findKey(this.props.columnHeaders, (val) => {
              return val[label] === columns[label];
            });
            return {
              label: label,
              value: columns[label],
              metricName: findKey,
            };
          });
          tempArr.push(...options);
        });

        let selectedItemsClone = _.cloneDeep(this.state.selectedItems);
        let newSelectedItems = [];
        let labelArr = Object.keys(this.props.columnHeaders[label]);
        selectedItemsClone.forEach((item) => {
          if (!labelArr.includes(item.label)) {
            newSelectedItems.push(item);
          }
        });

        this.onHandleDropdownSelect(newSelectedItems);
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
          selectedItems: newSelectedItems,
        });
      }
    }
  };
  onHandleDropdownSelect = (selOptions, proceed, columns) => {
    if (!_.isEqual(this.state.selectedItems, selOptions) || proceed) {
      let headers = columns ? _.cloneDeep(columns) : _.cloneDeep(this.state.columns);
      _.remove(headers, (item) => item.metric);

      selOptions.forEach((item) => {
        let newTableCols = headers.filter((a) => {
          return a.accessor === item.value;
        });
        if (newTableCols.length == 0) {
          let metricNameCalc =
            item.metricName === 'Inventory' ? 'Current inventory' : item.metricName;
          let spliceAt = headers.length - 1,
            findMetricName = _.find(headers, { Header: metricNameCalc }),
            downloadFormatter = FormatterStyling('downloadFormatter', item.value);

          if (findMetricName) {
            findMetricName.columns.push({
              Header: item.label,
              accessor: item.value,
              Aggregated: ({ row }) => {
                // You can even render the cell differently if it's an aggregated cell
                let aggregate = row.id.includes('.')
                  ? AggregateFunc(row.original.subRows, item.value)
                  : mainAggregateFunc(row.original.subRows, item.value);

                return <span>{aggregate}</span>;
              },
              Cell: (props) => FormatterStyling(props, item.value),
              downloadFormatter,
            });
          } else {
            headers.splice(spliceAt, 0, {
              Header: item.metricName === 'Inventory' ? 'Current inventory' : item.metricName,
              metric: true,
              columns: [
                {
                  Header: item.label,
                  accessor: item.value,
                  Aggregated: ({ row }) => {
                    // You can even render the cell differently if it's an aggregated cell
                    let aggregate = row.id.includes('.')
                      ? AggregateFunc(row.original.subRows, item.value)
                      : mainAggregateFunc(row.original.subRows, item.value);

                    return <span>{aggregate}</span>;
                  },
                  Cell: (props) => FormatterStyling(props, item.value),
                  downloadFormatter,
                },
              ],
            });
          }
        }
      });
      this.setState({
        columns: headers,
        selectedItems: selOptions,
      });
    }
  };

  render() {
    return (
      <React.Fragment>
        <LoadingOverlay loader={this.props.overlayLoaderState} spinner>
          <div className="card">
            <div>
              <h4 className="font-size-14  pad-10 markdown-heading">
                MarkSmart /<span className="font-bold color-blue">Reporting Section</span>
              </h4>
            </div>
            <hr
              style={{
                marginTop: '-4px',
              }}
            />
            <Filters
              drop
              stores
              customStoreGroup
              dateRange
              hierarchy={[
                {
                  type: 'hierarchy1',
                  isMandatory: true,
                  isMultiple: false,
                },
                {
                  type: 'hierarchy2',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy3',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy4',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy5',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy6',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy7',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy8',
                  isMandatory: true,
                  isMultiple: true,
                },
              ]}
              onFilter={this.onFilter}
              onReset={this.props.onReset}
              customStyleGroups
              // allowInactiveProducts={true} // no need for reporting now
            />
            <hr className="hr-style mt-4 mb-4" />
            {this.props.reportingData && this.props.reportingData.message ? (
              <div className="no-data">{this.props.reportingData.message}</div>
            ) : (
              <React.Fragment>
                {!!Object.keys(this.props.reportingData).length ? (
                  <div className="row justify-content-center">
                    <div className="card col-md-5 ml-4 mb-4 mr-4 reporting-chart-container">
                      Sell through
                      <div>
                        <LineChart
                          data={this.props.reportingData.sellThroughData}
                          options={this.sellThroughOptions}></LineChart>
                      </div>
                    </div>
                    <div className="card col-md-5 ml-4 mb-4 reporting-chart-container">
                      Inventory
                      <div>
                        <LineChart
                          data={this.props.reportingData.inventoryData}
                          options={this.inventoryOptions}></LineChart>
                      </div>
                    </div>
                    <div className="card col-md-5 ml-4 mb-4 mr-4 reporting-chart-container">
                      Margin
                      <div>
                        <LineChart
                          data={this.props.reportingData.marginData}
                          options={this.marginOptions}></LineChart>
                      </div>
                    </div>
                    <div className="card col-md-5 ml-4 mb-4 reporting-chart-container">
                      Sales comparison
                      <div>
                        <BarChart
                          data={this.props.reportingData.salesData}
                          options={this.salesComparisonOptions}></BarChart>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="no-data">Please select filters</div>
                )}
              </React.Fragment>
            )}
          </div>
          {!!Object.keys(this.props.reportingData).length && !this.props.reportingData.message && (
            <div className="show-detail-button">
              <button
                className="btn btn-primary mt-3"
                onClick={() => this.getReportingDataStyleGroup()}>
                Show Style Group level data
              </button>
            </div>
          )}
          {this.mandatoryFiltersVal() && this.state.buttonClicked ? (
            this.props.cockpitData.length ? (
              <div className="card mt-4">
                <SkuCockpit
                  columnHeaders={this.props.columnHeaders}
                  onHandleSelect={this.onHandleDropdownSelect}
                  selectedItems={this.state.selectedItems}
                  onHandleCheckBox={this.onHandleCheckBox}
                  columnOptions={this.state.columnOptions}
                  selLabels={this.state.selLabels}
                  columns={this.state.columns}
                  cockpitData={this.props.cockpitData}
                  onCreateMarkdown={this.onCreateMarkdown}
                />
              </div>
            ) : (
              <div style={{ textAlign: 'center' }}>
                No data found. Please try with other filters.
              </div>
            )
          ) : null}
        </LoadingOverlay>
      </React.Fragment>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    overlayLoaderState: store.loaderReducer.overlayLoaderState,
    reportingData: store.reportingReducer.reportingData,
    cockpitData: store.reportingReducer.cockpitData,
    columnHeaders: store.reportingReducer.columnHeaders,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
