  metricHandler = (columns, selOptions, val) => {
    // val=detailed for detailed table, val=overall for agg-parent col and val=value for subcomponent cols
    let headers = _.cloneDeep(columns);
    _.remove(headers, (item) => item.metric || item.hideColumn); //removing metric:true and hideColumn: true cols
    _.forEach(selOptions, (item) => {
      let newTableCols = _.filter(headers, (a) => {
        return a.accessor === item[val];
      });
      let found = _.find(columns, { accessor: item[val] });
      if (newTableCols.length == 0) {
        let spliceAt = headers.length - 0; //modify here if the new col needs to be added at a specific index
        headers.splice(spliceAt, 0, found);
      }
    });

    return headers;
  };
  onHandleSelect = (selOptions) => {
    let headers, weekToBinHeader, binToWeekHeader;
    if (this.state.switchedPlans) {
      //detailed table case
      let newDetailedColDef = this.addDetailedAction();
      headers = this.metricHandler(newDetailedColDef, selOptions, 'detailed');
      this.setState({
        columns: headers,
        selectedItems: selOptions,
      });
    } else {
      //agg-table case: needs to update 3 coldefs,
      //imported coldefs already has all of the required columns,
      //we are just filtering out needed cols(filtering is done in metricHandler method)
      let newAggColDef = this.addMDPlanColumnHandler();
      headers = this.metricHandler(newAggColDef, selOptions, 'overall');
      weekToBinHeader = this.metricHandler(
        weekToBinColDef(this.handleInputChange),
        selOptions,
        'value'
      );
      binToWeekHeader = this.metricHandler(
        binToWeekColDef(this.handleInputChange),
        selOptions,
        'value'
      );

      this.setState({
        aggColDef: headers,
        weekToBinColDef: weekToBinHeader,
        binToWeekColDef: binToWeekHeader,
        selectedItems: selOptions,
      });
    }
  };
  handleInputChange = (value, markdown_id, bin, week) => {
    //do not use setState in this method, bcs it'll cause table to rerender
    let { aggTableData } = this.state;
    //if state is not cloned this will update the state without rendering the component

    _.map(Object.keys(aggTableData), (level) => {
      let index = _.findIndex(aggTableData[level], { markdown_id: markdown_id });
      let plan = aggTableData[level][index];

      if (level === 'week_to_bin_agg_data') {
        if (value === 'copyRefreshed') plan.overall_offer_value = plan.overall_refreshed_md;

        for (let i = 0; i < plan.week_level_data.length; i++) {
          let item = plan.week_level_data[i];
          if (value === 'copyRefreshed')
            item.overall_week_offer_value = item.overall_week_refreshed_md;

          if (value === 'copyRefreshed' || item.week === week) {
            for (let j = 0; j < item.bin_level_data.length; j++) {
              let itm = item.bin_level_data[j];

              if (value === 'copyRefreshed') itm.offer_value = itm.refreshed_md;
              else if (itm.bin_num === bin) {
                itm.offer_value = value;
                return;
              }
            }
          }
        }
      } else if (level === 'bin_to_week_agg_data') {
        if (value === 'copyRefreshed') plan.overall_offer_value = plan && plan.overall_refreshed_md;

        for (let i = 0; i < plan.bin_level_data.length; i++) {
          let item = plan.bin_level_data[i];
          if (value === 'copyRefreshed')
            item.overall_bin_offer_value = item.overall_bin_refreshed_md;

          if (value === 'copyRefreshed' || item.bin === bin) {
            for (let j = 0; j < item.week_level_data.length; j++) {
              let itm = item.week_level_data[j];

              if (value === 'copyRefreshed') {
                itm.offer_value = itm.refreshed_md;
                this.updateCadenceData(markdown_id, item.bin, itm.week_num, itm.refreshed_md);
              } else if (itm.week_num === week) {
                itm.offer_value = value;
                //update cadence data for respective plan
                this.updateCadenceData(markdown_id, item.bin, itm.week_num, value);
                return;
              }
            }
          }
        }
      }
    });

    return aggTableData;
  };

