  addMDPlanColumnHandler = () => {
    let newAggColDef = _.cloneDeep(aggColDef);
    newAggColDef.splice(0, 0, {
      id: 'selection',
      groupByBoundary: true,
      Header: 'Actions',
      // fixed: 'left',
      Cell: (props) => {
        let { row, rows } = props;
