                  className="btn btn-secondary"
                  onClick={async () => {
                    await this.props.setRetainFilters(true);
                    this.props.updateSidebar('/vis/Markdown/create-markdown');
                    this.props.history.push({
                      pathname: `/vis/Markdown/create-markdown`,
                      state: { isEdit: true, planId: row.original.markdown_id, status: 'approved' },
                    });
                    // this.setState({ showEditModal: true, selectedPlanDetails: row.original });
                  }}>
                  <span>
                    <i className="fa fa-pencil color-blue">Edit</i>
                  </span>
                </button>
              ) : (
                ''
              )}
            </div>
          </div>
        );
      },
      align: 'center',
      downloadValue: 'plan',
    });
    return newAggColDef;
  };
  getSummary = async (payload) => {
    await this.props.getSummaryAggregated(payload).then((resp) => {
      if (_.isEmpty(resp)) return;
      let newAggColDef = this.addMDPlanColumnHandler();
      let newDetailedColDef = this.addDetailedAction();
      let planDetails = {};
      _.map([...resp.markdown_details], (plan) => {
        planDetails[plan.promo_code] = {
          cadenceData: plan.request_obj.cadenceData,
          validateDiscount: {
            min: plan.request_obj.min_discount,
            max: plan.request_obj.max_discount,
            interval: plan.request_obj.interval_discount,
          },
        };
      });
      this.setState({
        columns: _.filter(newDetailedColDef, (item) => !item.hideColumn),
        aggColDef: _.filter(newAggColDef, (item) => !item.hideColumn),
        weekToBinColDef: _.filter(
          weekToBinColDef(this.handleInputChange),
          (item) => !item.hideColumn
        ),
        binToWeekColDef: _.filter(
          binToWeekColDef(this.handleInputChange),
          (item) => !item.hideColumn
        ),
        tableLoader: false,
        aggTableData: {
          bin_to_week_agg_data: resp && resp.bin_to_week_agg_data,
          week_to_bin_agg_data: resp && resp.week_to_bin_agg_data,
        },
        planDetails,
      });
    });
    return true;
  };
  renderRowSubComponent = ({ row }) => {
    let { expandedRowDetails } = this.state; //contains all the expanded rows details
    let foundLevel = _.filter(expandedRowDetails, {
      //check if this row is expanded
      markdown_id: row.original.markdown_id,
    });
    let data = [];
    if (!foundLevel[0]) return; //if not expanded then return

    if (foundLevel[0] && foundLevel[0].level === 'week_to_bin') {
      //if row is expanded on week_to_bin level, directly take the data from row
      let newData = _.cloneDeep(row.original.week_level_data);
      if (!newData) return;
      _.map(newData, (item) => {
        item.subRows = item.bin_level_data; //changing bin_level_data key into subRows, to acquire Expandable feature from reatc-table
        item.markdown_id = row.original.markdown_id;
        delete item.bin_level_data;
        return item;
      });
      data = newData;
    }
    if (foundLevel[0] && foundLevel[0].level === 'bin_to_week') {
      //if row is expanded on bin_to_week level, then find row data using markdown_id from the reducer aggregatedSummary
      let newData = _.find(this.state.aggTableData.bin_to_week_agg_data, {
        markdown_id: row.original.markdown_id,
      });
      if (!newData) return;
      newData = _.cloneDeep(newData);
      _.map(newData.bin_level_data, (item) => {
        item.subRows = item.week_level_data; //changing name to subRows to get Expandable feature from react-table
        item.start_date = newData.start_date;
        item.end_date = newData.end_date;
        item.markdown_id = row.original.markdown_id;
        delete item.week_level_data;
      });

      data = newData.bin_level_data;
    }
    let columns =
      foundLevel[0] && foundLevel[0].level === 'week_to_bin' && row.original.week_level_data
        ? this.state.weekToBinColDef
        : foundLevel[0] && foundLevel[0].level === 'bin_to_week'
        ? this.state.binToWeekColDef
        : []; //selecting colfdef according to the level user has choosen
    return (
      <div className="child-table">
        <DataTable
          columns={columns}
          data={data}
          isHeaderHidden={true}
          isChildComp={true} //true for only subComponents, added to make some styling changes
        />
      </div>
    );
  };
  expandedRowDetailsHandler = (row, level) => {
    //called whenever user clicks on a ">" icon in the cell
    //this is to track expanded row details, and on which level its expanded(week to bin/bin to week)
    let { expandedRowDetails } = this.state;
    if (_.find(expandedRowDetails, { markdown_id: row.markdown_id })) {
      _.remove(expandedRowDetails, { markdown_id: row.markdown_id });
    } else {
      expandedRowDetails.push({
        markdown_id: row.markdown_id,
        level,
      });
    }
    this.setState({ expandedRowDetails });
  };
  nextPageHandler = async () => {
    let payload = _.cloneDeep(this.state.reqObj);
    payload.page_num = this.state.pageNum;
    this.setState({ pageNum: this.state.pageNum + 1 });
    await this.getSummary(payload);
    return true;
  };
  updateCadenceData = (markdownId, binOrSku, week, value) => {
    let cadenceData = this.state.planDetails[markdownId].cadenceData;
    let cadence1 = cadenceData[Object.keys(cadenceData)[0]];
    let findCadence = _.find(cadence1, { bins: binOrSku.toString() });
    findCadence[`W${week}`] = value;
  };
  validateCadenceValue = () => {
    let { planDetails, selectedRows } = this.state;
    let markdownIdList = selectedRows.map((item) => item.markdown_id);

    for (let id of markdownIdList) {
      let planData = planDetails[id];
      let array =
        planData && planData.cadenceData && _.cloneDeep(Object.values(planData.cadenceData)[0]);
      if (_.isEmpty(array) || _.isEmpty(array[0])) return false;
      for (let item of array) {
        let breakLoop = false;
        for (const prop in item) {
          let val = item[prop];
          let isValid =
            val >= parseInt(planData.validateDiscount.min) &&
            val <= parseInt(planData.validateDiscount.max) &&
            val % parseInt(planData.validateDiscount.interval) == 0;

          if (prop.includes('W') && !isValid) {
            toast.error(
              `Please enter values between ${planData.validateDiscount.min} and ${planData.validateDiscount.max} in intervals of ${planData.validateDiscount.interval}`,
              {
                autoClose: 15000,
                hideProgressBar: true,
              }
            );
            breakLoop = true;
            return;
          }
        }
        if (breakLoop) return false;
      }
      return true;
    }
  };
  onSaveAndResimulate = () => {
    let cadenceValidation = this.validateCadenceValue();
    if (!cadenceValidation) return;

    let { selectedRows, planDetails } = this.state;
    let markdown_idList = selectedRows.map((item) => item.markdown_id);
    let markdown_cadence_details = [];
    _.map(markdown_idList, (item) => {
      let details = _.find(this.props.markdownDetails, {
        promo_code: item,
      });
      markdown_cadence_details.push({
        cadenceData: planDetails[item] && planDetails[item].cadenceData,
        promo_code: details.promo_code,
      });
    });
    this.props.sendResimulateAndSave({ markdown_cadence_details }).then((resp) => {
      let status = Object.values(resp.data.data).map((a) => a.status);
      let payload = _.cloneDeep(this.state.reqObj);
      if (status) {
        this.getSummary(payload);
      }
      this.setState({
        selectedRows: [],
      });
    });
  };
  onCopyRefreshed = () => {
    let markdownIdList = _.map(this.state.selectedRows, (data, i) => {
      return data.markdown_id;
    });

    let { aggTableData, planDetails, selectedRows } = this.state; //_.cloneDeep(this.state.aggTableData);
    //if state is not cloned this will update the state without rendering the component
    _.map(markdownIdList, (id) => {
      aggTableData = this.handleInputChange('copyRefreshed', id);
    });
    this.setState({ aggTableData, planDetails });
  };
  render() {
    const imageArray = [
      card1Image,
      card2Image,
      card3Image,
      card4Image,
      card1Image,
      card2Image,
      card3Image,
      card4Image,
      card1Image,
      card2Image,
    ];
    const cardStyle = [
      'card-border1',
      'card-border2',
      'card-border3',
      'card-border4',
      'card-border1',
      'card-border2',
      'card-border3',
      'card-border4',
      'card-border1',
      'card-border2',
    ];

    return (
      //Added conditions to enable loader for KPIs only.
      <LoadingOverlay
        loader={
          this.state.KPILoader ? false : this.props.getOverlayLoaderState || this.state.tableLoader
        }
        spinner>
        <div className="createMd pl-2 pr-2 summary-comp">
          <div className="card CalendarMain promoCalendarDiv1 mt-3">
            <div>
              <h4 className="font-size-14  pad-10 markdown-heading">
                MarkSmart /<span className="font-bold color-blue">Summary</span>
              </h4>
            </div>
            <hr
              className="mb-2"
              style={{
                marginTop: '-4px',
              }}
            />
            <div className="filterCol mb-4">
              <Filters
                storeClusterDisable={true}
                onReset={(e) => {
                  this.setState({
                    textStr: 'Please select filters to show Markdown plans',
                    showKPIS: false,
                  });
                  this.props.onResetSummaryTable();
                }}
                hierarchy={this.state.filters}
                onFilter={this.onFilter}
                dateRange
                filtersOptions={[
                  { type: 'country', isMandatory: true, isMultiple: false },
                  { type: 'channel', isMandatory: true, isMultiple: false },
                  { type: 'storeCluster', isMandatory: false, isMultiple: true },
                  { type: 'customStoreGroup', isMandatory: false, isMultiple: true },
                  { type: 'store', isMandatory: false, isMultiple: true },
                  {
                    type: 'hierarchy1',
                    isMandatory: true,
                    isMultiple: false,
                    isHierarchy: true,
                  },
                  {
                    type: 'hierarchy2',
                    isMandatory: true,
                    isMultiple: false,
                    isHierarchy: true,
                  },
                  {
                    type: 'hierarchy3',
                    isMandatory: true,
                    isMultiple: false,
                    isHierarchy: true,
                  },
                  {
                    type: 'hierarchy4',
                    isMandatory: true,
                    isMultiple: false,
                    isHierarchy: true,
                  },
                  {
                    type: 'hierarchy5',
                    isMandatory: true,
                    isMultiple: false,
                    isHierarchy: true,
                  },
                  { type: 'season', isMandatory: false, isMultiple: true },
                  { type: 'matrix', isMandatory: false, isMultiple: true },
                  { type: 'exclusionList', isMandatory: false, isMultiple: true },
                  { type: 'clearance', isMandatory: false, isMultiple: true },
                  { type: 'customStyleGroup', isMandatory: false, isMultiple: true },
                  { type: 'styleColor', isMandatory: false, isMultiple: true },
                ]}
              />
            </div>
          </div>

          {this.props.summaryKPIData && this.state.showKPIS ? (
            // <div className="cardContainerstyle" style={{ marginTop: '10px' }}>
            <LoadingOverlay loader={this.state.KPILoader} spinner>
              <div className="cardContainerstyle">
                {this.props.summaryKPIData &&
                  this.props.summaryKPIData.columnMapping &&
                  Array.isArray(Object.keys(this.props.summaryKPIData.columnMapping)) &&
                  Object.keys(this.props.summaryKPIData.columnMapping).map((key, index) => (
                    // <div className="col-md-3 col-sm-3 col-xs-6 p-2">

                    <div>
                      <CardComponent
                        cardImage={imageArray[index]}
                        //item.type && item.type.charAt(0).toUpperCase() + item.type.slice(1);
                        //cardText={key && key.charAt(0).toUpperCase() + key.slice(1)}
                        cardText={
                          this.props.summaryKPIData.columnMapping &&
                          this.props.summaryKPIData.columnMapping[key] &&
                          this.props.summaryKPIData.columnMapping[key].charAt(0) +
                            this.props.summaryKPIData.columnMapping[key].slice(1).toLowerCase()
                        }
                        cardValue={
                          this.props.summaryKPIData.KPIS.lw &&
                          this.roundOffNumber(this.props.summaryKPIData.KPIS.lw[key], key)
                        }
                        cardValueSecond={
                          this.props.summaryKPIData.KPIS.tw &&
                          this.roundOffNumber(this.props.summaryKPIData.KPIS.tw[key], key)
                        }
                        cardValueThird={
                          this.props.summaryKPIData.KPIS.total &&
                          this.roundOffNumber(this.props.summaryKPIData.KPIS.total[key], key)
                        }
                        cardWeekText={this.state.cardWeekText}
                        cardMonthText={this.state.cardMonthText}
                        cardTotalText={this.state.cardTotalText}
                        cardStyles={{
                          cardClass: cardStyle[index],
                          //cardValue: 'card1-value',
                          //cardImg: summaryKPIData.cardStyleImg,
                          //cardValueSecond: 'card-value-second',
                          //cardSubHeading: 'card-subHeading',
                        }}
                      />
                    </div>
                  ))}
              </div>{' '}
            </LoadingOverlay>
          ) : null}
          {!_.isEmpty(this.props.detailedSummary) || !_.isEmpty(this.state.aggTableData) ? (
            <div className="card pt-3 px-3 mt-3 summary-card">
              <ColSelBtn>
                {((this.state.switchedPlans && !_.isEmpty(this.state.columns)) ||
                  (!this.state.switchedPlans && !_.isEmpty(this.state.aggColDef))) && (
                  <ColumnSelection
                    onHandleSelect={this.onHandleSelect}
                    columnOptions={this.state.columnOptions}
                    selectedItems={this.state.selectedItems}
                    selLabels={this.state.selLabels}
                  />
                )}
                <div className="switchCss">
                  <Switch
                    checked={this.state.switchedPlans}
                    onSwitchHandler={this.isSwitchedPaln}
                    leftLabel="Aggregated"
                    rightLabel="Detailed"
                  />
                </div>
                <div style={{ padding: '1rem' }}>
                  <button
                    className="btn btn-primary"
                    disabled={!this.state.selectedRows.length}
                    onClick={this.onCopyRefreshed}>
                    <span>
                      <i className="fa fa-copy"></i>&nbsp; Copy refreshed
                    </span>
                  </button>
                  &nbsp;
                  <button
                    disabled={!this.state.selectedRows.length}
                    className="btn btn-primary"
                    onClick={this.onSaveAndResimulate}>
                    <span>
                      <i className="fa fa-save"></i>&nbsp; Save
                    </span>
                  </button>
                </div>
              </ColSelBtn>
              {this.state.switchedPlans ? (
                <DataTable
                  columns={this.state.columns}
                  isSort={true}
                  showExport={true}
                  fileName="Summary detailed data"
                  data={this.props.detailedSummary}
                  updateMyData={this.updateMyData}
                  isPagination={true}
                  rowsPerPage={5}
                  getCellProps={(cellInfo) => ({
                    style: {
                      backgroundColor: this.bgmCol(cellInfo),
                      color: this.txtCol(cellInfo),
                      margin: '4px 4px',
                      borderRadius: '2px',
                    },
                  })}
                />
              ) : (
                <DataTable
                  columns={this.state.aggColDef}
                  isSort={true}
                  data={this.state.aggTableData.week_to_bin_agg_data}
                  updateMyData={this.updateMyData}
                  isPagination={true}
                  rowsPerPage={5}
                  renderRowSubComponent={this.renderRowSubComponent}
                  nextPageHandler={this.nextPageHandler}
                  getCellProps={(cellInfo) => ({
                    style: {
                      backgroundColor: this.bgmCol(cellInfo),
                      color: this.txtCol(cellInfo),
                      margin: '4px 4px',
                      borderRadius: '2px',
                    },
                    onClick: (e) => {
                      //added to track on which cell user has clicked
                      let classList = e.target.classList;
                      if (
                        classList &&
                        !(
                          classList.contains('expanded-icon-size') ||
                          classList.contains('summary-table-icon')
                        )
                      )
                        //user clicks on div(i.e, row is not expanded)  then return
                        return;

                      if (cellInfo.column.id === 'expander')
                        //expander is Date Range cell
                        this.expandedRowDetailsHandler(cellInfo.row.original, 'week_to_bin');
                      else if (cellInfo.column.id === 'expander1')
                        //expander1 is Bins cell
                        this.expandedRowDetailsHandler(cellInfo.row.original, 'bin_to_week');
                    },
                  })}
                  RowCheck={({ row, column }) => {
                    if (!(column.id === 'expander' || column.id === 'expander1')) return;
                    //disables sibling cell within a row, when a particular row is expanded
                    let foundLevel = _.filter(this.state.expandedRowDetails, {
                      markdown_id: row.original.markdown_id,
                    });
                    if (!foundLevel[0]) return;

                    return (foundLevel[0].level === 'week_to_bin' && column.id === 'expander1') ||
                      (foundLevel[0].level === 'bin_to_week' && column.id === 'expander')
                      ? 'tdtd disable-summary-cell'
                      : null;
                  }}
                />
              )}
            </div>
          ) : (
            <NoData description={this.state.textStr} />
          )}
        </div>
      </LoadingOverlay>
    );
  }
}

