  return {
    finalizeMarkdownData: store.draftStatusReducer.markdowns,
    getOverlayLoaderState: store.loaderReducer.overlayLoaderState,
    getOverlayLoaderText: store.loaderReducer.loaderText,
    getColumnHeaders: store.draftStatusReducer.columnHeaders,
    getMetricOptions: store.finalizeReducer.metricOptions,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    startDate: store.filtersReducer.startDate,
    endDate: store.filtersReducer.endDate,
    weeks: store.filtersReducer.weeks,
    markdownDetails: store.summaryMDReducer.markdownDetails,
    selectedSeason: store.filtersReducer.selectedSeason,
    selectedClearance: store.filtersReducer.selectedClearance,
    selectedMatrix: store.filtersReducer.selectedMatrix,
    selectedExclusionList: store.filtersReducer.selectedExclusionList,
    selectedStyleColor: store.filtersReducer.selectedStyleColor,
    selectedCountry: store.filtersReducer.selectedCountry,
    selectedStoreCluster: store.filtersReducer.selectedStoreCluster,
    selectedStore: store.filtersReducer.selectedStore,
    selectedChannel: store.filtersReducer.selectedChannel,
    summaryKPIData: store.summaryMDReducer.summaryKPIData,
    detailedSummary: store.summaryMDReducer.detailedSummary,
    aggregatedSummary: store.summaryMDReducer.aggregatedSummary,
  };
};

const mapActionToProps = {
  getSummaryDetails: SummaryAction.getSummaryDetails,
  getSummaryAggregated: SummaryAction.getSummaryAggregated,
  onResetSummaryTable: SummaryAction.onResetSummaryTable,
  getKPIdata: SummaryAction.getKPIdata,
  updateSidebar: plannedReportsAction.updateSidebar,
  setRetainFilters: filterActions.setRetainFilters,
  sendResimulateAndSave: SummaryAction.sendResimulateAndSave,
};
export default connect(mapStateToProps, mapActionToProps)(withRouter(Summary));
const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;
