  getDrops: () => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getSeasonFilters`,
        method: 'GET',
      })
        .then((resp) => {
          dispatch({
            type: 'SET_DROPS',
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },

  getHierarchyOptions: (data) => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getHierarchyFilterData/`,
        method: 'POST',
        data: {
          filters: data.filters,
          hierarchyType: data.hierarchyType,
          // allowOnlyActiveProducts: data.allowInactiveProducts ? data.allowInactiveProducts : false,
        },
      })
        .then((resp) => {
          dispatch({
            type: `SET_${data.hierarchyType}`,
            payload: resp.data.data,
          });
        })
        .catch((err) => {
          console.log(err);
        });
    };
  },
  getExtraFiltersOptions: (data) => {
    return (dispatch) => {
      axios({
        url: `${config.createMarkdownURL}/getExtraFilters/`,
        method: 'POST',
        data: {
          filters: data.filters,
          filterType: data.filterType,
          allowOnlyActiveProducts: data.allowInactiveProducts ? data.allowInactiveProducts : false,
          // allowInactiveProducts: data.allowInactiveProducts,
        },
      })
        .then((resp) => {
          dispatch({
            type: `SET_${data.filterType}`,
