import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import Filters from '../../services/filters';
import './finalizeMarkdownStyle.css';
import styled from 'styled-components';
import LoadingOverlay from '../../utils/Loader';
import { toast } from 'react-toastify';
import NoData from '../noTableData';
import FinalizeAction from '../../actions/draftStausFinalizeAction';
import plannedReportsAction from '../../actions/plannedReportsAction';
// import Coldef from './coldef';
import Coldef from '../draftStatus/draft/coldef';
import _ from 'lodash';
import cloneDeep from 'clone-deep';
import DataTable from '../../services/reactTable';
import ColumnSelection from '../columnSelection';
import AlertWarning from '../../utils/AlertMessage/alertDanger';
import AlertSuccess from '../../utils/AlertMessage/alertSuccess';
import moment from 'moment';
import card1Image from '../../images/card1Image.png';
import card2Image from '../../images/card2Image.png';
import card3Image from '../../images/card3Image.png';
import card4Image from '../../images/card4Image.png';
import IndeterminateCheckbox from '../../components/cellRenders/inderterminateCheckbox';
import {
  toPercentage,
  toDollarWithlDraft,
  toUnitDraft,
  toDollarWithDecimal,
  toPercWithOneDecimal,
} from '../../helper/formatter';
import Switch from '../../services/toggleSwitch';
import EditMDModal from '../editMDModal';

function FormatterStyling(props, value, val) {
  let val1 = val || val == 0 ? val : props.value;
  let props1 = val || val == 0 ? { value: val } : props;
  if (
    value == 'planned_units' ||
    value == 'markdown_units' ||
    value == 'overall_planned_units' ||
    value == 'overall_markdown_units'
  ) {
    return val1 || val1 == 0 ? toUnitDraft(props1) : '-';
  } else if (value == 'margin_perc' || value == 'overall_margin_perc') {
    return val1 || val1 == 0 ? toPercWithOneDecimal(props1) : '-';
  } else if (
    value == 'revenue$' ||
    value == 'margin$' ||
    value == 'overall_revenue$' ||
    value == 'overall_margin$'
  ) {
    return val1 || val1 == 0 ? toDollarWithlDraft(props1) : '-';
  } else if (value == 'overall_aum' || value == 'overall_aur' || value == 'aur' || value == 'aum') {
    return val1 || val1 == 0 ? toDollarWithDecimal(props1) : '-';
  }
  return '-';
}
class FinalizeMarkdown extends Component {
  constructor(props) {
    super(props);
    this.state = {
      switchedPlans: false,
      textStr: 'Please select filters to show Markdown plans',
      firstCardText: 'KPIs to be decided',
      firstCardFirstValue: '12%',
      firstCardSecondValue: '24%',
      secondCardFirstValue: '16%',
      secondCardSecondValue: '20%',
      thirdCardFirstValue: '6%',
      thirdCardSecondValue: '10%',
      fourthCardFirstValue: '345',
      fourthCardSecondValue: '3,457',
      cardWeekText: 'this Week',
      cardMonthText: 'this Month',
      selectedItems: [],
      columnOptions: [],
      selLabels: [],
      columns: [],
      selectedRows: [],
      tableData: [],
      showEditModal: false,
      selectedPlanDetails: null,
    };
  }

  componentWillUnmount() {
    this.props.setMarkDowns([]);
    this.props.setMDColumnHeaders({});
  }
  componentDidMount() {
    this.updateTableData(this.state.switchedPlans);
  }
  componentDidUpdate(prevProps) {
    let { finalizeMarkdownData } = this.props;
    if (
      !_.isEmpty(finalizeMarkdownData) &&
      !_.isEqual(finalizeMarkdownData, prevProps.finalizeMarkdownData)
    ) {
      this.updateTableData(this.state.switchedPlans);
    }
  }
  updateTableData = (isEdited) => {
    let { finalizeMarkdownData } = this.props;
    let newTableData = _.filter(finalizeMarkdownData, (item) => item.is_edited === isEdited);

    if (!_.isEqual(this.state.tableData, newTableData)) this.setState({ tableData: newTableData });
  };
  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };
  sendForReject = () => {
    let msg = [];
    let payload = {
      markdown_data: this.state.selectedRows.map((item, i) => {
        let arrLength = this.state.selectedRows.length;
        let msgStr = `Scenario '${item.scenario}' of Plan '${item.md_plan}'${
          i === arrLength - 2 ? ' and ' : i === arrLength - 1 ? '' : ', '
        }`;
        msg.push(msgStr);
        return {
          id: item.markdown_id,
          scenario: item.scenario,
        };
      }),
      update_status: 'rejected',
    };
    this.props
      .sendForApproval(payload)
      .then((resp) => {
        if (resp && resp.data.status == true) {
          let successMsg = `${msg.join('')} has been Rejected`;
          toast.info(successMsg, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          let filtersData = this.props.lastSavedFilters && this.props.lastSavedFilters.filters;
          let req = {
            filters: filtersData.filters,
            startDate: filtersData.dateRange && filtersData.dateRange.startDate,
            endDate: filtersData.dateRange && filtersData.dateRange.endDate,
            status: 'pending',
          };
          // if (this.props.selectedChannel[0] === 'Web') req.filters['ecom_flag'] = [1];
          // if (this.props.selectedChannel[0] === 'Outlet') req.filters['retail_flag'] = [1];

          this.props.getMarkdowns(req);
          this.setState({
            selectedRows: [],
          });
        }
      })
      .catch((err) => {
        toast.error(err.message, {
          autoClose: 15000,
          hideProgressBar: true,
        });
      });
  };
  sendForApprove = () => {
    let msg = [];
    let payload = {
      markdown_data: this.state.selectedRows.map((item, i) => {
        let arrLength = this.state.selectedRows.length;
        let msgStr = `Scenario '${item.scenario}' of Plan '${item.md_plan}'${
          i === arrLength - 2 ? ' and ' : i === arrLength - 1 ? '' : ', '
        }`;
        msg.push(msgStr);
        return {
          id: item.markdown_id,
          scenario: item.scenario,
        };
      }),
      update_status: 'approved',
    };

    this.props
      .sendForApproval(payload)
      .then((resp) => {
        if (resp && resp.data.status == true) {
          let successMsg = `${msg.join('')} has been Finalized Successfully`;
          toast.info(successMsg, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          let filtersData = this.props.lastSavedFilters && this.props.lastSavedFilters.filters;
          let req = {
            filters: filtersData.filters,
            startDate: filtersData.dateRange && filtersData.dateRange.startDate,
            endDate: filtersData.dateRange && filtersData.dateRange.endDate,
            status: 'pending',
          };
          // if (this.props.selectedChannel[0] === 'Web') req.filters['ecom_flag'] = [1];
          // if (this.props.selectedChannel[0] === 'Outlet') req.filters['retail_flag'] = [1];

          this.props.getMarkdowns(req);
          this.setState({
            selectedRows: [],
          });
        }
      })
      .catch((err) => {
        toast.error(err.message, {
          autoClose: 15000,
          hideProgressBar: true,
        });
      });
  };
  onActionSelection = (rowData) => {
    let { selectedRows } = this.state;
    let foundEle = _.find(selectedRows, { markdown_id: rowData.original.markdown_id });
    if (foundEle) {
      _.remove(selectedRows, (item) => item.markdown_id === rowData.original.markdown_id);
    } else {
      selectedRows.push(rowData.original);
    }
    this.setState({
      selectedRows,
    });
  };

  onScenarioSelection = (rowData) => {
    let { selectedRows } = this.state;
    let foundEle = _.find(selectedRows, { markdown_id: rowData.original.markdown_id });
    if (foundEle) {
      _.remove(selectedRows, (item) => item.markdown_id === rowData.original.markdown_id);
    } else {
      selectedRows.push(rowData.original);
    }
    this.setState({
      selectedRows,
    });
  };
  onHandleSelect = (selOptions) => {
    let headers = _.cloneDeep(this.state.columns);
    _.remove(headers, (item) => item.metric);
    selOptions.forEach((item) => {
      let newTableCols = headers.filter((a) => {
        return a.accessor === item.value;
      });
      if (newTableCols.length == 0) {
        let spliceAt = headers.length - 1;
        headers.splice(spliceAt, 0, {
          Header: item.label,
          accessor: item.value,
          Aggregated: (props) => {
            let accessorName = `overall_${item.value}`;
            let val = props.row.original[accessorName];
            return FormatterStyling(props, accessorName, val);
          },

          Cell: (props) => {
            let parentId = Math.floor(props.row.id);
            let parentRow = _.find(props.rows, { id: parentId.toString() });
            return FormatterStyling(props, item.value);
          },
          metric: true,
        });
      }
    });
    this.setState({
      columns: headers,
      selectedItems: selOptions,
    });
  };

  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.getColumnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = _.cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.getColumnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });

        let selectedItemsClone = _.cloneDeep(this.state.selectedItems);
        let newSelectedItems = [];
        let labelArr = Object.keys(this.props.getColumnHeaders[label]);
        selectedItemsClone.forEach((item) => {
          if (!labelArr.includes(item.label)) {
            newSelectedItems.push(item);
          }
        });

        this.onHandleSelect(newSelectedItems);
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
          selectedItems: newSelectedItems,
        });
      }
    }
  };
  mandatoryFiltersVal = () => {
    return (
      this.props.selectedHierarchy1 &&
      this.props.selectedHierarchy2.length &&
      this.props.startDate &&
      this.props.endDate
    );
  };
  isSwitchedPaln = (val) => {
    this.updateTableData(val);
    this.setState({
      switchedPlans: val,
    });
  };
  onFilter = (filtersData) => {
    let payload = {
      filters: filtersData.filters,
      startDate: filtersData.dateRange && filtersData.dateRange.startDate,
      endDate: filtersData.dateRange && filtersData.dateRange.endDate,
      status: 'pending',
    };
    // if (this.props.selectedChannel[0] === 'Web') payload.filters['ecom_flag'] = [1];
    // if (this.props.selectedChannel[0] === 'Outlet') payload.filters['retail_flag'] = [1];
    this.getMarkdowns(payload);
  };

  getMarkdowns = (payload) => {
    this.props.getMarkdowns(payload).then((resp) => {
      if (resp && resp.columnHeaders && Object.keys(resp.columnHeaders).length) {
        let defaultHeader = Object.keys(resp.columnHeaders.default);

        let setDefaultColDef = Coldef().filter(
          (col) =>
            defaultHeader.includes(col.Header) ||
            col.Header === 'Time Period' ||
            col.Header === 'Actions' ||
            col.id === 'expander' ||
            // col.accessor === 'sell_through' ||
            (this.state.toggle && col.accessor === 'status')
        );
        let setDefaultLabel = [];
        let setDefaultColumnOptions = [];
        let setDefaultColumnOptions1 = [];
        let setDefaultItems = [];
        let colHeaders = _.cloneDeep(resp.columnHeaders);
        let labels = Object.keys(colHeaders);
        labels.splice(labels.indexOf('fixed'), 1);
        labels.splice(labels.indexOf('default'), 1);
        for (const label of labels) {
          let labelArray = Object.keys(resp.columnHeaders[label]);
          function diff(A = labelArray, B = defaultHeader) {
            return A.filter(function (a) {
              return B.indexOf(a) !== -1;
            });
          }

          if (diff().length) {
            setDefaultColumnOptions.push(...Object.keys(resp.columnHeaders[label]));
            setDefaultColumnOptions1.push(resp.columnHeaders[label]);
            setDefaultItems.push(...diff());
            setDefaultLabel.push(label);
          }
        }
        let options = setDefaultColumnOptions.map((label) => {
          let find = setDefaultColumnOptions1 && setDefaultColumnOptions1[0][label];
          return {
            label: label,
            value: find,
          };
        });
        let selectedItems = setDefaultItems.map((label) => {
          let find = setDefaultColumnOptions1 && setDefaultColumnOptions1[0][label];
          return {
            label: label,
            value: find,
          };
        });
        setDefaultColDef.push({
          id: 'selection',
          groupByBoundary: true,
          Header: 'Actions',
          // fixed: 'left',
          Cell: (props) => {
            let { row, rows } = props;
            let mdPlan = row.original.markdown_id,
              mainGrp = _.find(rows, {
                isSelected: true,
                original: { markdown_id: mdPlan },
              }),
              checkboxDisabled = mainGrp && !row.isSelected ? true : false;

            return row.canExpand ? (
              <div>
                <IndeterminateCheckbox
                  row={row.id}
                  onClick={() => {
                    this.onScenarioSelection(row);
                  }}
                  disabled={checkboxDisabled}
                  {...row.getToggleRowSelectedProps()}
                />
                {/* <span className="ml-3 align-top">
                  <i
                    onClick={() =>
                      this.setState({ showEditModal: true, selectedPlanDetails: row.original })
                    }
                    className="fa fa-pencil color-blue"
                  />
                </span> */}
              </div>
            ) : null;
          },
        });
        setDefaultColDef.splice(0, 0, {
          Header: 'MD Plan',
          accessor: 'md_plan',
          disableGroupBy: true,
          enableRowSpan: 'markdown_id',
          filter: (rows, id, filterType) =>
            rows.filter((row) => {
              return row.original.md_plan ? row.original.md_plan.startsWith(filterType) : null;
            }),
          Cell: ({ row }) => (
            <div>
              <span>{row.original.md_plan ? row.original.md_plan : null}</span>
              <div className="mt-1 mb-2">
                {row.original.md_plan ? (
                  <button
                    className="btn btn-secondary"
                    onClick={() => {
                      // this.props.updateSidebar('/vis/Markdown/create-markdown');
                      // this.props.history.push({
                      //   pathname: `/vis/Markdown/create-markdown`,
                      //   state: { isEdit: true, planId: row.original.markdown_id },
                      // });
                      this.setState({ showEditModal: true, selectedPlanDetails: row.original });
                    }}>
                    <span>
                      <i className="fa fa-pencil color-blue">Edit</i>
                    </span>
                  </button>
                ) : (
                  ''
                )}
              </div>
            </div>
          ),
          align: 'center',
          downloadValue: 'plan',
        });
        this.setState({
          columns: setDefaultColDef,
          selLabels: setDefaultLabel,
          selectedItems: selectedItems,
          columnOptions: options,
        });
      }

      if (resp && !resp.markdown_data.length) {
        this.setState({
          textStr: 'No data found. Please try with different filters.',
        });
      }
    });
  };

  showEditModalHandler = (val) => {
    if (!val) document.body.setAttribute('style', '');
    this.setState({ showEditModal: val });
  };

  render() {
    return (
      <LoadingOverlay loader={this.props.getOverlayLoaderState} spinner>
        <div className="createMd pl-2 pr-2 finalize-comp">
          <div className="card CalendarMain promoCalendarDiv1 mt-3">
            <div>
              <h4 className="font-size-14  pad-10 markdown-heading">
                MarkSmart /<span className="font-bold color-blue">Finalize</span>
              </h4>
            </div>
            <hr
              className="mb-2"
              style={{
                marginTop: '-4px',
              }}
            />

            <div className="filterCol mb-4">
              <Filters
                onReset={(e) => {
                  this.setState({
                    textStr: 'Please select filters to show Markdown plans',
                  });
                  this.props.onResetFinalizeTable(e);
                }}
                onFilter={this.onFilter}
                dateRange
                from="finalize"
                // filtersOptions={[
                //   {
                //     type: 'hierarchy1',
                //     isMandatory: true,
                //     isMultiple: false,
                //     isHierarchy: true,
                //   },
                //   {
                //     type: 'hierarchy2',
                //     isMandatory: true,
                //     isMultiple: true,
                //     isHierarchy: true,
                //   },
                //   {
                //     type: 'hierarchy3',
                //     isMandatory: false,
                //     isMultiple: true,
                //     isHierarchy: true,
                //   },
                //   {
                //     type: 'hierarchy4',
                //     isMandatory: false,
                //     isMultiple: true,
                //     isHierarchy: true,
                //   },
                //   {
                //     type: 'hierarchy5',
                //     isMandatory: false,
                //     isMultiple: true,
                //     isHierarchy: true,
                //   },
                //   { type: 'customStyleGroup', isMandatory: false, isMultiple: true },

                //   { type: 'hierarchy7', isMandatory: false, isMultiple: true, isHierarchy: true },

                //   { type: 'hierarchy9', isMandatory: false, isMultiple: true, isHierarchy: true },
                //   { type: 'hierarchy10', isMandatory: false, isMultiple: true, isHierarchy: true },
                //   { type: 'hierarchy11', isMandatory: false, isMultiple: true, isHierarchy: true },
                //   { type: 'hierarchy12', isMandatory: false, isMultiple: true, isHierarchy: true },
                //   { type: 'stores', isMandatory: false, isMultiple: true },
                //   { type: 'customStoreGroup', isMandatory: false, isMultiple: true },
                // ]}
              />
            </div>
          </div>
          {/* <div className="row" style={{ marginTop: '15px' }}>
          <div className="col-md-3 col-sm-3 col-xs-6 ">
            <CardComponent
              cardImage={card1Image}
              cardText={this.state.firstCardText}
              cardValue={this.state.firstCardFirstValue}
              cardValueSecond={this.state.firstCardSecondValue}
              cardWeekText={this.state.cardWeekText}
              cardMonthText={this.state.cardMonthText}
              cardStyles={{
                cardClass: 'card-border1',
                cardValue: 'card1-value',
                cardImg: 'card1-img',
                cardValueSecond: 'card-value-second',
                cardSubHeading: 'card-subHeading',
              }}
            />
          </div>
          <div className="col-md-3 col-sm-3 col-xs-6">
            <CardComponent
              cardImage={card2Image}
              cardText={this.state.firstCardText}
              cardValue={this.state.secondCardFirstValue}
              cardValueSecond={this.state.secondCardSecondValue}
              cardWeekText={this.state.cardWeekText}
              cardMonthText={this.state.cardMonthText}
              cardStyles={{
                cardClass: 'card-border2',
                cardValue: 'card1-value',
                cardImg: 'card2-img',
                cardValueSecond: 'card-value-second',
                cardSubHeading: 'card-subHeading',
              }}
            />
          </div>
          <div className="col-md-3 col-sm-3 col-xs-6 ">
            <CardComponent
              cardImage={card3Image}
              cardText={this.state.firstCardText}
              cardValue={this.state.thirdCardFirstValue}
              cardValueSecond={this.state.thirdCardSecondValue}
              cardWeekText={this.state.cardWeekText}
              cardMonthText={this.state.cardMonthText}
              cardStyles={{
                cardClass: 'card-border3',
                cardValue: 'card1-value',
