import markDownData from "../components/Finalize/data"

const finalizeMarkDownDefaultState = {
  finalizeTableData: markDownData,
  metricOptions: [
    { label: "sell through", value: "1" },
    { label: "margin %", value: "2" },
    { label: "mark down %", value: "3" },
  ]
}

export default (state = finalizeMarkDownDefaultState, action) => {
  switch (action.type) {
    case "SET_FINALIZE_DATA":
      return {
        ...state,
        finalizeTableData: action.value
      };
    case "SET_FINALIZE_METRIC_OPTIONS":
      return {
        ...state,
        metricOptions: action.value
      };
    default:
      return {
        ...state
      }
  }
}
