import React from "react";
import "./custom-card.css";
import { Row, Col } from "react-bootstrap";
import ReactTooltip from "react-tooltip";

class PopUp extends React.Component {
  // eslint-disable-next-line no-useless-constructor
  constructor(props) {
    super(props);
    this.state = {
      graphs: [
        {
          name: "bar-chart",
          active: 0
        },
        {
          name: "pie-chart",
          active: 0
        },
        {
          name: "area-chart",
          active: 0
        },
        {
          name: "line-chart",
          active: 0
        },
        {
          name: "table",
          active: 0
        },
        {
          name: "circle-o",
          active: 0
        }
      ]
    };
  }

  componentDidMount() {
    var graphdata = this.state.graphs;
    this.props.graphs.forEach(item => {
      graphdata.forEach((elem, index) => {
        if (elem.name === item) {
          elem.active = 1;
        }
      });
    });
    this.setState({
      graphs: graphdata
    });
  }
  render() {
    var data = this.props.data;
    var graphs = this.state.graphs;
    console.log(this.props.selectedGraph);
    return (
      <div
        className=""
        style={{
          width: "250px",
          height: "320px",
          border: "1px solid  #A0A0A0",
          borderRadius: "4px",
          position: "absolute",
          zIndex: 999,
          right: "3%",
          top: "3%",
          boxShadow: "0 0 2px 0",
          background: "white",
          fontSize: "14px",
          fontWeight: "bold"
        }}
      >
        {data.map(function(item, index) {
          if (item === "zoom") {
            var icon = "fa-search";
          } else if (item === "download") {
            // eslint-disable-next-line no-redeclare
            var icon = "fa-download";
          } else {
            // eslint-disable-next-line no-redeclare
            var icon = "fa-envelope";
          }
          return (
            <Row>
              <Col md={12}>
                <div
                  style={{
                    marginRight: "10px",
                    marginLeft: "10px",
                    marginTop: index === 0 ? "10px" : ""
                  }}
                >
                  <span style={{ textTransform: "capitalize" }}>{item}</span>
                  <i
                    style={{ float: "right" }}
                    className={"fa " + icon + " popup"}
                    aria-hidden="true"
                  ></i>
                  <hr style={{ marginTop: "8px", marginBottom: "10px" }} />
                </div>
              </Col>
            </Row>
          );
        })}
        <Row>
          <span style={{ marginLeft: "25px" }}>View As:</span>
        </Row>
        <Row style={{ marginLeft: "10px" }}>
          {graphs.map((item, index) => {
            return (
              <Col md={4} style={{ marginTop: "10px" }}>
                {/* <i onClick={()=>{this.props.onSelect(item.name);this.props.showPop()}} data-tip data-for={item.name} style={{color:(item.active)?'#007AFF':'#b4bab3',cursor:(item.active)?'pointer':'not-allowed',pointerEvents:(item.active)?'auto':'none',backgroundImage:"url('../shared/img/'+"+(item.name||'')+"'.svg')"}}  aria-hidden="true" title="graphs" ></i> */}
                <img
                  onClick={() => {
                    this.props.onSelect(item.name);
                    this.props.showPop();
                    this.props.onGraphChange(item.name);
                  }}
                  title="graphs"
                  src={require("./img/" + (item.name || "") + ".svg")}
                  style={{
                    opacity: item.active ? "1" : "0.2",
                    cursor: item.active ? "pointer" : "not-allowed",
                    pointerEvents: item.active ? "auto" : "none",
                    height: "60%",
                    width: "60%",
                    marginBottom: "2px"
                  }}
                  alt="chart"
                />
                <br />
                <span
                  style={{
                    textTransform: "capitalize",
                    marginTop: "3px",
                    color:
                      item.name === this.props.selectedGraph
                        ? "#007AFF"
                        : "black"
                  }}
                >
                  {item.name.split("-")[0]}
                </span>
                <br />
                <br />
                <ReactTooltip id={item.name} type="dark" effect="float">
                  <span style={{ textTransform: "capitalize" }}>
                    {item.name}
                  </span>
                </ReactTooltip>
              </Col>
            );
          })}
        </Row>
      </div>

      // <div className="popup" style={{width:'250px',height:'auto',border: '1px solid  #A0A0A0',borderRadius:'4px',position:'absolute',zIndex:999,right:'3%',boxShadow:'0 0 4px 0',background:'white' }}>
      //     <Row>
      //         <Col md={12}>
      //             <div style={{marginRight:'10px',marginLeft:'10px',marginTop:'10px'}}>
      //                 <span>Zoom</span>
      //                 <i style={{float:'right'}} className="fa fa-search" aria-hidden="true"></i>
      //                 <hr/>
      //             </div>
      //         </Col>
      //     </Row>
      //     <Row>
      //         <Col md={12}>
      //         <div style={{marginRight:'10px',marginLeft:'10px'}}>
      //             <span>Download</span>
      //             <i style={{float:'right'}} className="fa fa-download" aria-hidden="true"></i>
      //             <hr/>
      //         </div>
      //         </Col>
      //     </Row>
      //     <Row>
      //         <Col md={12}>
      //         <div style={{marginRight:'10px',marginLeft:'10px'}}>
      //             <span>Email to</span>
      //             <i style={{float:'right'}} className="fa fa-envelope" aria-hidden="true"></i>
      //             <hr/>
      //         </div>
      //         </Col>
      //     </Row>
      //     <Row>
      //         <Col md={12}>
      //         <span style={{marginLeft:'10px'}}>View As:</span><br/>
      //         </Col>
      //     </Row>
      //     {/* <Row style={{marginLeft:'10px'}}>

      //             <Col md={4}>
      //                 <i className="fa fa-bar-chart fa-2x" aria-hidden="true" ></i>
      //             </Col>
      //             <Col md={4}>
      //                 <i className="fa fa-line-chart fa-2x" aria-hidden="true" ></i>
      //             </Col>
      //             <Col md={4}>
      //                 <i className="fa fa-pie-chart fa-2x" aria-hidden="true" ></i>
      //             </Col>
      //             <Col md={4}>
      //                 <i className="fa fa-area-chart fa-2x" aria-hidden="true" ></i>
      //             </Col>

      //     </Row> */}
      // </div>
    );
  }
}
/**
 * GraphPopOver : Pop Over which contains actions to perform on each graph
 */
class GraphPopOver extends React.Component {
  /**
   * @typedef {Object} GraphPop-props
   *
   * @property {Function} onSelect() change the graph type on click
   * @property {Array} data list of operations which can be performed like zoom,download,etc
   * @property {Array} graphs list of types of graphs that can be shown (bar-chart,line-chart,etc)
   */
  /**
   *
   * @param {GraphPop-props} props
   */
  constructor(props) {
    super(props);
    this.state = {
      show: false,
      selectedGraph: this.props.selectedGraph
    };
    this.renderPop = this.renderPop.bind(this);
    this.handleClickOutside = this.handleClickOutside.bind(this);
    this.graphChange = this.graphChange.bind(this);
  }

  componentDidMount() {
    document.addEventListener("mousedown", this.handleClickOutside);
  }
  /**
   * Function to hid the card when clicked outside
   * @param {Object} event click event
   */
  handleClickOutside(event) {
    if (event.target.title === "More" || event.target.title === "graphs") {
      return;
    }

    this.setState({
      show: false
    });
  }

  /**
   * Function to toogle the view of the card
   */

  renderPop() {
    this.setState({
      show: !this.state.show
    });
  }

  graphChange(name) {
    this.setState({
      selectedGraph: name
    });
  }
  render() {
    var toShow = this.state.show;
    console.log();
    return (
      <div>
        <div class="toggle-switch" onClick={() => this.renderPop()}>
          <i
            className="fa fa-ellipsis-v"
            title="More"
            style={{
              fontSize: "20px",
              color: toShow ? "black" : "#2d65fc",
              marginTop: "5px",
              cursor: "pointer"
            }}
          />
        </div>
        {toShow && this.props.data ? (
          <PopUp
            data={this.props.data}
            showPop={this.renderPop}
            onSelect={this.props.onSelect}
            selectedGraph={this.state.selectedGraph}
            graphs={this.props.graphs}
            onGraphChange={this.graphChange}
          />
        ) : null}
      </div>
    );
  }
}

export default GraphPopOver;
