function unitCheck(value, type) {
  if (!value && value !== 0 || !type) return '-';
  if (type === 'percent_off') return toPercentage({
    value: value
  });
  if (type === 'fixed_price') return toDollar({
    value: value
  });
}

function findNextWeek(row, rows) {
  var value;
  var id = row.id.split('.');

  var found = _.find(rows, {
    id: "".concat(parseInt(id[0]) + 1)
  });

  if (!_.isEmpty(found) && _.isEmpty(row.subRows) && row.original.offer_value) {
    value = found.originalSubRows && found.originalSubRows[parseInt(id[1])] && found.originalSubRows[parseInt(id[1])].offer_value;
  }

  if (!_.isEmpty(found) && !_.isEmpty(row.subRows) && row.original.overall_week_offer_value) {
    value = found.original && found.original.overall_week_offer_value;
  }

  return value;
}

function currentRunningMd(row, rows) {
  var value = null;
  var date = moment().year(row.original.year).week(row.original.week_num).day('monday').format('YYYY-MM-DD');

  if (moment().isSameOrBefore(date, 'week')) {
    var lastWeekNum = moment().subtract(1, 'week').week();

    var found = _.find(rows, {
      original: {
        week: lastWeekNum
      }
    });

    var found1 = _.find(found && found.originalSubRows && found.originalSubRows, {
      bin_num: row.original.bin_num
    });

    value = found1 && found1.refreshed_md ? found1.refreshed_md : null;
  }

  return value;
}

function currentRunningMdBin(row, rows) {
  var value = null;
  var date = moment().year(row.original.year).week(row.original.week_num).day('monday').format('YYYY-MM-DD');

  if (moment().isSameOrBefore(date, 'week')) {
    var id = row.id.split('.');

    var found = _.find(rows, {
      id: id[0]
    });

    if (!_.isEmpty(found) && found.originalSubRows.length) {
      var lastWeekNum = moment().subtract(1, 'week').week();

      var find = _.find(found.originalSubRows, {
        week_num: lastWeekNum
      });

      if (!find) return null;
      value = find.refreshed_md;
    }
  }

  return value;
}

