function unitCheck(value, type) {
  if ((!value && value !== 0) || !type) return '-';
  if (type === 'percent_off') return toPercentage({ value });
  if (type === 'fixed_price') return toDollar({ value });
}
function findNextWeek(row, rows) {
  let value;
  let id = row.id.split('.');
  let found = _.find(rows, { id: `${parseInt(id[0]) + 1}` });

  if (!_.isEmpty(found) && _.isEmpty(row.subRows) && row.original.offer_value) {
    value =
      found.originalSubRows &&
      found.originalSubRows[parseInt(id[1])] &&
      found.originalSubRows[parseInt(id[1])].offer_value;
  }
  if (!_.isEmpty(found) && !_.isEmpty(row.subRows) && row.original.overall_week_offer_value) {
    value = found.original && found.original.overall_week_offer_value;
  }
  return value;
}

function currentRunningMd(row, rows) {
  let value = null;
  let date = moment()
    .year(row.original.year)
    .week(row.original.week_num)
    .day('monday')
    .format('YYYY-MM-DD');
  if (moment().isSameOrBefore(date, 'week')) {
    let lastWeekNum = moment().subtract(1, 'week').week();
    let found = _.find(rows, { original: { week: lastWeekNum } });
    let found1 = _.find(found && found.originalSubRows && found.originalSubRows, {
      bin_num: row.original.bin_num,
    });
    value = found1 && found1.refreshed_md ? found1.refreshed_md : null;
  }
  return value;
}
function currentRunningMdBin(row, rows) {
  let value = null;
  let date = moment()
    .year(row.original.year)
    .week(row.original.week_num)
    .day('monday')
    .format('YYYY-MM-DD');
  if (moment().isSameOrBefore(date, 'week')) {
    let id = row.id.split('.');
    let found = _.find(rows, { id: id[0] });
    if (!_.isEmpty(found) && found.originalSubRows.length) {
      let lastWeekNum = moment().subtract(1, 'week').week();
      let find = _.find(found.originalSubRows, { week_num: lastWeekNum });
      if (!find) return null;
      value = find.refreshed_md;
    }
  }
  return value;
}
