import React, { Component } from 'react';
import DataTable from '../../services/reactTable';
import cloneDeep from 'clone-deep';
import { toPercentage } from '../../helper/formatter';

class MarkdownDetailedTable extends Component {
  constructor() {
    super();
    this.state = {
      column: {
        Header: '',
        columns: [
          {
            Header: 'ST %',
            accessor: 'sellThrough',
            disableGroupBy: true,
            // minWidth: 600,
            width: 500,
            Cell: (props) => <div> {toPercentage(props)} </div>,
          },
          {
            Header: 'Margin %',
            accessor: 'margin',
            // minWidth: 600,
            width: 500,
            Cell: (props) => <div> {toPercentage(props)} </div>,
            disableGroupBy: true,
          },
        ],
      },
      headers: [
        {
          Header: 'Style/Style Group',
          accessor: 'SKU_Id',
          width: 300,
          disableGroupBy: true,
        },
      ],
    };
  }
  render() {
    let headers = cloneDeep(this.state.headers);
    console.log(headers, 'heda');
    this.props.data.weeks.forEach((week) => {
      let column = cloneDeep(this.state.column);
      column.Header = week.toUpperCase();
      console.log(week, 'week');
      column.columns[0].accessor = `${week}${column.columns[0].accessor}`;
      column.columns[1].accessor = `${week}${column.columns[1].accessor}`;
      headers.push(column);
    });
    console.log('headers::::', headers, this.props.data);

    return (
      <div style={{ overflowX: 'auto', width: '100%', paddingBottom: '28px' }}>
        <DataTable columns={headers} data={this.props.data.data} isPagination={true} />
      </div>
    );
  }
}

export default MarkdownDetailedTable;
