function setVisibleColumns(hooks, visibleColumns) {
  switch (visibleColumns) {
    case 'onlyRowSelection':
      hooks.visibleColumns.push((columns) => {
        return [
          {
            id: 'selection',
            // Make this column a groupByBoundary. This ensures that groupBy columns
            // are placed after it
            groupByBoundary: true,
            // The header can use the table's getToggleAllRowsSelectedProps method
            // to render a checkbox
            Header: ({ getToggleAllRowsSelectedProps }) => (
              <div>
                <IndeterminateCheckbox {...getToggleAllRowsSelectedProps()} />
              </div>
            ),
            // The cell can use the individual row's getToggleRowSelectedProps method
            // to the render a checkbox
            Cell: ({ row }) => (
              <div>
                <IndeterminateCheckbox {...row.getToggleRowSelectedProps()} />
              </div>
            ),
          },
          ...columns,
        ];
      });
      break;
    case 'markDownDrafts':
      hooks.visibleColumns.push((columns) => {
        return [
          ...columns,
          {
            id: 'selection',
            // Make this column a groupByBoundary. This ensures that groupBy columns
            // are placed after it
            groupByBoundary: true,
            // The header can use the table's getToggleAllRowsSelectedProps method
            // to render a checkbox
            Header: 'Actions1',
            // The cell can use the individual row's getToggleRowSelectedProps method
            // to the render a checkbox
            Cell: ({ row }) => {
              return (
                <div>
                  <IndeterminateCheckbox row={row.id} {...row.getToggleRowSelectedProps()} />
                </div>
              );
            },
          },
          {
            // Make this column a groupByBoundary. This ensures that groupBy columns
            // are placed after it
            groupByBoundary: true,
            // The header can use the table's getToggleAllRowsSelectedProps method
            // to render a checkbox
            Header: 'Actions2',
            // The cell can use the individual row's getToggleRowSelectedProps method
            // to the render a checkbox
            Cell: ({ row }) => (
              <img
                width="20"
                src="./../../img/analysisMarkdown.png"
                alt="promoAnalysis"
                className="pointer"
              />
            ),
          },
        ];
      });
      break;
    case 'markDownStatus':
      hooks.visibleColumns.push((columns) => {
        return [
          ...columns,
          {
            // Make this column a groupByBoundary. This ensures that groupBy columns
            // are placed after it
            groupByBoundary: true,
            // The header can use the table's getToggleAllRowsSelectedProps method
            // to render a checkbox
            Header: 'Actions',
            // The cell can use the individual row's getToggleRowSelectedProps method
            // to the render a checkbox
            Cell: ({ row }) => (
              <img
                width="20"
                src="./../../img/analysisMarkdown.png"
                alt="promoAnalysis"
                className="pointer"
              />
            ),
          },
        ];
      });
      break;

    default:
      hooks.useInstance.push(useInstance);
      break;
  }
}
function DefaultColumnFilter({ column: { filterValue, preFilteredRows, setFilter } }) {
  return (
    <div className="filterInput">
      <input
        value={filterValue || ''}
        onChange={(e) => {
          setFilter(e.target.value || undefined); // Set undefined to remove the filter entirely
        }}
        placeholder={`Search`}
      />
      <span>
        <i className="fa fa-filter filterIcon"></i>
      </span>
    </div>
  );
}

function fuzzyTextFilterFn(rows, id, filterValue) {
  return matchSorter(rows, filterValue, { keys: [(row) => row.values[id]] });
}
// Let the table remove the filter if the string is empty
fuzzyTextFilterFn.autoRemove = (val) => !val;

// Create a default prop getter
const defaultPropGetter = () => ({});

function useInstance(instance) {
  const { allColumns } = instance;

  let rowSpanHeaders = [];

  allColumns.forEach((column) => {
    const { id, enableRowSpan } = column;

    if (enableRowSpan !== undefined) {
      rowSpanHeaders = [...rowSpanHeaders, { id: id, topCellValue: null, topCellIndex: 0 }];
    }
  });

  Object.assign(instance, {
    rowSpanHeaders: rowSpanHeaders,
  });
}
function getExportFileBlob({ columns, data, fileType, fileName }) {
  console.log(columns, 'columnscolumns', data, 'datadata');
  if (fileType === 'csv') {
    // CSV example
    const headerNames = columns.map((col) => col.exportValue);
    const csvString = Papa.unparse({ fields: headerNames, data });
    return new Blob([csvString], { type: 'text/csv' });
  } else if (fileType === 'xlsx') {
    // XLSX example

    const newCols = _.filter(columns, (item) => !item.hideInDownload); //hideInDownload:true if the column needs to b hidden in downloaded file
    const header = _.map(newCols, (c) => (c.downloadHeader ? c.downloadHeader : c.exportValue)); //downloadHeader is to show different headername in downloaded file,
    const cellFormatter = _.map(newCols, (c) => c.downloadFormatter); //downloadFormatter is formatter func for the download val
    const compatibleData = _.map(data, (row) => {
      const obj = {};
      _.forEach(header, (col, index) => {
        let val = row[index];
        let newVal =
          (val || val === 0) && cellFormatter[index] ? cellFormatter[index]({ value: val }) : val;
        obj[col] = newVal || newVal === 0 ? newVal : '-';
        console.log('czsdlfeopppppp', val, newVal);
      });
      return obj;
    });

    let wb = XLSX.utils.book_new();
    let ws1 = XLSX.utils.json_to_sheet(compatibleData, {
      header,
    });
    XLSX.utils.book_append_sheet(wb, ws1, 'Data');
    XLSX.writeFile(wb, `${fileName}.xlsx`);

    // Returning false as downloading of file is already taken care of
    return false;
  }

  // Other formats goes here
  return false;
}
function Table({
  columns,
  data,
  updateMyData,
  visibleColumns,
  isColumnFilter,
  isCellEdit,
  defCols,
  isPagination,
  rowsPerPage,
  showExport,
  getHeaderProps = defaultPropGetter,
  getColumnProps = defaultPropGetter,
  getRowProps = defaultPropGetter,
  getCellProps = defaultPropGetter,
  isSort,
  isHeaderHidden,
  selectRow,
}) {
  //Column Filter Configuration
  const filterTypes = React.useMemo(
    () => ({
      // Add a new fuzzyTextFilterFn filter type.
      fuzzyText: fuzzyTextFilterFn,
      // Or, override the default text filter to use
      // "startWith"
      text: (rows, id, filterValue) => {
        return rows.filter((row) => {
          const rowValue = row.values[id];
          return rowValue !== undefined
            ? String(rowValue).toLowerCase().startsWith(String(filterValue).toLowerCase())
            : true;
        });
      },
    }),
    []
  );
  const defaultColumn = React.useMemo(
    () => ({
      // Let's set up our default Filter UI
      Filter: DefaultColumnFilter,
    }),
    []
  );
  // // Set our editable cell renderer as the default Cell renderer
  // const defaultColumn = {};
  // if (isCellEdit) {
  //   defaultColumn["Cell"] = EditableCell;
  // }
  // // Set our editable cell renderer as the default Cell renderer
  // if (isColumnFilter) {
  //   defaultColumn["Filter"] = DefaultColumnFilter;
  // }

  // Use the state and functions returned from useTable to build your UI
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    prepareRow,
    page,
    rows, // Instead of using 'rows', we'll use page, which has only the rows for the active page, The rest of these things are super handy, too ;)
    canPreviousPage,
    canNextPage,
    pageOptions,
    pageCount,
    gotoPage,
    nextPage,
    previousPage,
    setPageSize,
    rowSpanHeaders,
    exportData,
    state: { selectedRowIds, pageIndex, pageSize, sortBy, groupBy, expanded, filters },
  } = useTable(
    {
      columns,
      data,
      defaultColumn, //for setting default view
      filterTypes,
      // updateMyData isn't part of the API, but
      // anything we put into these options will
      // automatically be available on the instance.
      // That way we can call this function from our
      // cell renderer!
      updateMyData,
      getExportFileBlob,
      // We also need to pass this so the page doesn't change
      // when we edit the data.
      disableMultiSort: true,
      initialState: {
        groupBy: [],
        filters: [],
        pageSize: rowsPerPage ? rowsPerPage : 10,
      },
    },
    useFilters,
    useGroupBy,
    useSortBy,
    useExpanded,
    usePagination,
    useRowSelect,
    useExportData,
    // Here we will use a plugin to add our selection column
    (hooks) => {
      setVisibleColumns(hooks, visibleColumns);
    }
  );
  useEffect(() => {
    selectRow && selectRow(selectedRowIds);
  }, [selectedRowIds]);
  var tableData = [];
  if (isPagination) {
    tableData = page;
  } else {
    tableData = rows;
  }
  let perPageArray = rowsPerPage
    ? [rowsPerPage, rowsPerPage * 2, rowsPerPage * 3, rowsPerPage * 4, rowsPerPage * 5]
    : [10, 20, 30, 40, 50];

  // Render the UI for your table
  return (
    <Styles>
      <div className="exportBtn mb-3">
        <button
          className={showExport ? 'btn btn-primary iconbutton download-btn' : 'd-none'}
          onClick={() => {
            exportData('xlsx', false);
          }}>
          <span>
            <i className="fa fa-download" aria-hidden="true" />
          </span>
        </button>
      </div>
      <div className="tableWrap">
        <table {...getTableProps()}>
          {isHeaderHidden ? null : (
            <thead>
              {headerGroups.map((headerGroup) => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                  {headerGroup.headers.map((column) => {
                    return (
                      <th
                        className="thth"
                        {...column.getHeaderProps({
                          className: column.collapse ? 'collapse' : '',
                        })}>
                        <div className="dis_flex_r">
                          {/* {column.canGroupBy ? (
                            // If the column can be grouped, let's add a toggle
                            <div {...column.getGroupByToggleProps()} className="plr">
                              {column.isGrouped ? (
                                ' '
                              ) : (
                                <i className="fa fa-users" aria-hidden="true" />
                              )}
                            </div>
                          ) : null} */}

                          {isSort && !column.columns ? (
                            <div {...column.getSortByToggleProps()}>
                              {column.render('Header')} &nbsp;
                              {!column.isSorted ? (
                                typeof column.Header !== 'string' ? (
                                  ''
                                ) : (
                                  <i className="fa fa-sort" aria-hidden="true" />
                                )
                              ) : column.isSortedDesc ? (
                                <i className="fa fa-sort-desc" aria-hidden="true" />
                              ) : (
                                <i className="fa fa-sort-asc" aria-hidden="true" />
                              )}
                            </div>
                          ) : (
                            column.render('Header')
                          )}
                        </div>
                        {/* Render the columns filter UI */}
                        <React.Fragment>
                          {column.showFilter ? column.render('Filter') : null}
                        </React.Fragment>
                      </th>
                    );
                  })}
                </tr>
              ))}
            </thead>
          )}

          <tbody {...getTableBodyProps()}>
            {/* Row Span */}
            {rowSpanHeaders &&
              tableData.map((row, i) => {
                prepareRow(row);
                for (let j = 0; j < row.allCells.length; j++) {
                  let cell = row.allCells[j];
                  let rowSpanHeader = rowSpanHeaders.find((x) => x.id === cell.column.id);
                  if (rowSpanHeader !== undefined) {
                    if (
                      rowSpanHeader.topCellValue === null ||
                      rowSpanHeader.topCellValue !== cell.value
                    ) {
                      cell.isRowSpanned = false;
                      rowSpanHeader.topCellValue = cell.value;
                      rowSpanHeader.topCellIndex = i;
                      cell.rowSpan = 1;
                    } else {
                      page[rowSpanHeader.topCellIndex].allCells[j].rowSpan++;
                      cell.isRowSpanned = true;
                    }
                  }
                }
                return null;
              })}

            {tableData.map((row) => {
              if (!rowSpanHeaders || (rowSpanHeaders && rowSpanHeaders.length === 0)) {
                prepareRow(row);
              }
              return (
                <tr {...row.getRowProps([getRowProps(row)])}>
                  {row.cells.map((cell) => {
                    row.getToggleRowExpandedProps();
                    if (cell.isRowSpanned) return null;
                    else {
                      return (
                        <td className="tdtd" rowSpan={cell.rowSpan} {...cell.getCellProps()}>
                          {cell.isGrouped ? (
                            // If it's a grouped cell, add an expander and row count
                            <>
                              <div className="dis_flex_r">
                                {cell.render('Cell', { editable: false })}
                                <div
                                  {...row.getToggleRowExpandedProps()}
                                  className="expandCollapseArrow">
                                  {row.isExpanded ? (
                                    <i className="fa fa-angle-down fa-lg" aria-hidden="true" />
                                  ) : (
                                    <i class="fa fa-angle-right fa-lg" aria-hidden="true" />
                                  )}
                                </div>{' '}
                              </div>
                            </>
                          ) : cell.isAggregated ? (
                            // If the cell is aggregated, use the Aggregated
                            // renderer for cell
                            cell.render('Aggregated')
                          ) : cell.isPlaceholder ? null : ( // For cells with repeated values, render null
                            // Otherwise, just render the regular cell
                            <div
                              {...cell.getCellProps([
                                {
                                  className: cell.column.className,
                                  style: cell.column.style,
                                },
                                getColumnProps(cell.column),
                                getCellProps(cell),
                              ])}>
                              {cell.render('Cell', { editable: true })}
                            </div>
                          )}
                        </td>
                      );
                    }
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      {tableData && tableData.length ? '' : <div align="center"></div>}
      {/*
        Pagination can be built however you'd like.
        This is just a very basic UI implementation:
      */}

      {isPagination && data && data.length ? (
        <div className="pagination dis_flex_r">
          <div>
            Showing{' '}
            <strong>
              {pageIndex > 0
                ? `${pageSize * pageIndex + 1} to ${
                    pageSize * (pageIndex + 1) > rows.length
                      ? rows.length
                      : pageSize * (pageIndex + 1)
                  } of ${rows.length}`
                : `${pageIndex + 1} to ${pageSize > rows.length ? rows.length : pageSize} of ${
                    rows.length
                  }`}
            </strong>{' '}
          </div>
          <div className="dis_flex_r">
            <button className="pages" onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
              {'<<'}
            </button>{' '}
            <button className="pages" onClick={() => previousPage()} disabled={!canPreviousPage}>
              {'<'}
            </button>{' '}
            {/* {pageOptions.map((page) => {
              return (
                <div
                  className="pagination-options"
                  onClick={() => {
                    gotoPage(page);
                  }}>
                  {pageIndex + 1 === page + 1 ? (
                    <div className="pageCountActive">{page + 1}</div>
                  ) : (
                    <div className="pageCount">{page + 1}</div>
                  )}
                </div>
              );
            })} */}
            <button className="pages" onClick={() => nextPage()} disabled={!canNextPage}>
              {'>'}
            </button>{' '}
            <button
              className="pages"
              onClick={() => gotoPage(pageCount - 1)}
              disabled={!canNextPage}>
              {'>>'}
            </button>
          </div>
          {/* <span>
          | Go to page:{" "}
          <input
            type="number"
            defaultValue={pageIndex + 1}
            onChange={e => {
              const page = e.target.value ? Number(e.target.value) - 1 : 0;
              gotoPage(page);
            }}
            style={{ width: "100px" }}
          />
        </span>{" "} */}
          <div>
            Per page
            <select
              value={pageSize}
              onChange={(e) => {
                setPageSize(Number(e.target.value));
              }}>
              {perPageArray.map((pageSize) => (
                <option key={pageSize} value={pageSize}>
                  Show {pageSize}
                </option>
              ))}
            </select>
          </div>
        </div>
      ) : (
        ''
      )}
    </Styles>
  );
}

