import React, { Component } from 'react';
import { connect } from 'react-redux';
import DraftAction from '../../../actions/draftStausFinalizeAction';
import Filters from '../../../services/filters';
import DataTable from '../../../services/reactTable';
import ColumnSelection from '../../columnSelection';
import Coldef from './coldef';
import styled from 'styled-components';
import _ from 'lodash';
import cloneDeep from 'clone-deep';
import RowData from './data';
import moment from 'moment';
import NoData from '../../noTableData';
import IndeterminateCheckbox from '../../../components/cellRenders/inderterminateCheckbox';

class Draft extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedItems: [],
      tableData: RowData,
      columnOptions: [],
      selLabels: [],
      columns: [],
      selectedRows: [],
    };
  }

  onCellClick = (row) => {
    console.log(row, 'cell clicked .....');
  };

  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };
  sendForApproval = () => {
    let payload = {
      markdown_data: this.state.selectedRows.map((item) => {
        return {
          id: item.markdown_id,
          scenario: item.scenario,
        };
      }),
      update_status: 'pending',
    };

    console.log(payload, 'anananannana');
    this.props.sendForApproval(payload);
  };
  updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    this.setState((old) => {
      let val = old.tableData.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...row,
            [columnId]: value,
          };
        }
        return row;
      });
      return { tableData: val };
    });
  };

  onFilter = () => {
    // let payload = { ...selectedFilters };
    // payload.startDate = payload.startDate && moment(payload.startDate).format('YYYY-MM-DD');
    // payload.endDate = payload.endDate && moment(payload.endDate).format('YYYY-MM-DD');
    // payload['week'] = '';
    // payload['status'] = 'draft';
    let payload = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        hierarchy7: this.props.selectedHierarchy7,
        hierarchy8: this.props.selectedHierarchy8,
      },
    };
    console.log(payload, 'drfatpayload');
    this.props.getMarkDowns(payload);
  };

  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
        });
      }
    }
  };
  onHandleSelect = (selOptions) => {
    console.log('option:', selOptions);
    let headers = cloneDeep(this.state.columns);
    console.log(headers, 'selheader');
    selOptions.forEach((item) => {
      console.log(item, 'itemitemitemitemd');
      let newTableCols = headers.filter((a) => {
        return a.accessor === item.value;
      });
      console.log(newTableCols, 'neww');
      if (newTableCols.length == 0) {
        headers.push({
          Header: item.label,
          accessor: item.value,
        });
      }
    });
    console.log(headers, 'headeererererer');
    this.setState({
      columns: headers,
    });
  };
  componentDidMount() {
    this.props.getMarkdowns({ status: 'draft' }); //{ status: 'draft' }
    let cols = Coldef();
    cols.push({
      id: 'selection',
      groupByBoundary: true,
      Header: 'Actions',
      Cell: (props) => {
        let { row, rows } = props;
        let mdPlan = row.values.md_plan,
          mainGrp = _.find(rows, { isSelected: true, values: { md_plan: mdPlan } }),
          checkboxDisabled = mainGrp && !row.isSelected ? true : false;

        return row.canExpand ? (
          <div>
            <IndeterminateCheckbox
              row={row.id}
              onClick={() => {
                this.onScenarioSelection(row);
              }}
              disabled={checkboxDisabled}
              {...row.getToggleRowSelectedProps()}
            />
          </div>
        ) : null;
      },
    });
    this.setState({
      columns: cols,
    });
  }

  onScenarioSelection = (rowData) => {
    let { selectedRows } = this.state;
    let foundEle = _.find(selectedRows, { markdown_id: rowData.original.markdown_id });
    if (foundEle) {
      _.remove(selectedRows, (item) => item.markdown_id === rowData.original.markdown_id);
    } else {
      selectedRows.push(rowData.original);
    }
    console.log(selectedRows, 'asdfmasdf', rowData);

    this.setState({
      selectedRows,
    });
  };

  render() {
    console.log(this.props.columns, 'collll');
    console.log(this.props.markdowns, 'markdowns');
    return (
      <div className="createMd pl-2 pr-2">
        <div className="card">
          <div>
            <h4 className="font-size-14  pad-10 markdown-heading">
              Markdown Optimization /<span className="font-bold color-blue">Draft</span>
            </h4>
          </div>
          <hr
            className="mb-2"
            style={{
              marginTop: '-4px',
            }}
          />
          <div className="filterCol mb-4">
            <Filters
              onFilter={this.onFilter}
              drop
              stores
              dateRange
              hierarchy={[
                {
                  type: 'hierarchy1',
                  isMandatory: true,
                  isMultiple: false,
                },
                {
                  type: 'hierarchy2',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy3',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy4',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy5',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy6',
                  isMandatory: false,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy7',
                  isMandatory: false,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy8',
                  isMandatory: false,
                  isMultiple: true,
                },
              ]}
            />
          </div>
        </div>
        {this.props.markdowns && this.props.markdowns.length ? (
          <div className="card pl-3 pr-3 mt-3">
            <ColSelBtn>
              {this.props.columnHeaders && Object.keys(this.props.columnHeaders).length && (
                <ColumnSelection
                  onHandleCheckBox={this.onHandleCheckBox}
                  columnHeaders={this.props.columnHeaders}
                  onHandleSelect={this.onHandleSelect}
                  columnOptions={this.state.columnOptions}
                  selectedItems={this.state.selectedItems}
                  selLabels={this.state.selLabels}
                />
              )}
              <div style={{ padding: '1rem' }}>
                <button onClick={this.sendForApproval} className="btn btn-primary">
                  Send for Approval
                </button>
                &nbsp;
              </div>
            </ColSelBtn>
            <div className="tablel">
              <DataTable
                columns={this.state.columns}
                data={this.props.markdowns}
                isSort={true}
                isExpanded={true}
                updateMyData={this.updateMyData}
                isPagination={true}
                NoDataComponent="This is the dynamic title"
                getCellProps={(cellInfo) => ({
                  onClick: () => this.onCellClick(cellInfo),
                  style: {
                    backgroundColor: this.bgmCol(cellInfo),
                    color: this.txtCol(cellInfo),
                    margin: '4px 4px',
                    borderRadius: '2px',
                  },
                })}
              />
            </div>
          </div>
        ) : (
