import React from 'react';
import { toPercentage, toUnit } from "../../../helper/formatter"


export default () => {
  const colDef = [
    {
      Header: "MD plan",
      accessor: "md_plan",
      disableGroupBy: false,
      width: 50,
    },
    {
      Header: "Scenario",
      accessor: "scenario",
      disableGroupBy: true,
      width: 50,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} scenario`,
    },
    {
      Header: "SKU Description",
      accessor: "sku_desc",
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `-`
    },
    {
      Header: "SKU ID",
      accessor: "sku_id",
      disableGroupBy: true,
      width: 50,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} SKU ID's`
    },
    {
      Header: "Store ID",
      accessor: "store_id",
      disableGroupBy: true,
      width: 55,
      aggregate: 'uniqueCount',
      Aggregated: ({ value }) => `${value} Store ID's`
    },
    {
      Header: "Markdown %",
      accessor: "markdown_perc",
      disableGroupBy: true,
      width: 55,
      Cell: props => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: "Sell through %",
      accessor: "sell_through_perc",
      disableGroupBy: true,
      width: 50,
      Cell: props => <div> {toPercentage(props)} </div>,
      // aggregate: 'sum',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: "Margin %",
      accessor: "margin_perc",
      disableGroupBy: true,
      width: 70,
      Cell: props => <div> {toPercentage(props)} </div>,
      // aggregate: 'average',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: "IOH",
      accessor: "ioh",
      disableGroupBy: true,
      width: 70,
      Cell: props => <div> {toUnit(props)} </div>,
      // aggregate: 'sum',
      Aggregated: ({ value }) => `-`,
    },
    {
      Header: "MD Status",
      accessor: "md_status",
      disableGroupBy: true,
      width: 70,
      style: {
        textAlign: "center"
      }
    },
  ];
  return colDef;
};
