import React, { Component } from 'react';
import { withRouter, Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { logoutUser } from '../../actions/auth';
import './sideBarStyle.css';
import { restrictedUser } from '../../utils/config';
const isLocalNode = function (node, target) {
  // In Internet Explorer (IE), Node.contains() only works on Element nodes,
  // not Text nodes. As such, let's travel up to the nearest Element node.
  // --
  // NOTE: This doesn't necessarily apply to this demo, which only reacts to
  // Element node interactions. However, I'm adding it here as a mental note.
  do {
    if (node === target) {
      return true;
    }

    target = target.parentNode;
  } while (target);

  return false;
};

const findUpTag = function (el, tag) {
  while (el.parentNode) {
    el = el.parentNode;
    if (el === tag) return el;
  }
  return null;
};

const pageLinks = {
  markdownOptimization: [
    '/vis/Markdown/calender',
    '/vis/Markdown/reporting',
    '/vis/Markdown/actual-planned-reports',
    '/vis/Markdown/global-promo',
    '/vis/Markdown/create-markdown',
    '/vis/Markdown/draftStatus',
    '/vis/Markdown/finalize',
    '/vis/Markdown/configuration',
    '/vis/Markdown/configuration/style',
    '/vis/Markdown/configuration/store',
    '/vis/Markdown/configuration/style/group',
    '/vis/Markdown/configuration/style/family',
    '/vis/Markdown/configuration/style/create',
    '/vis/Markdown/configuration/store/group/create',
    '/vis/Markdown/configuration/store/group/edit/4',
  ],
};

class SideBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      class: '',
      display: 'none',
    };
  }

  onHover = () => {
    this.setState({
      class: 'active',
      display: 'inline-block',
    });
  };

  onLeaveHover = () => {
    this.setState({
      class: '',
      display: '',
    });
  };

  handleClickOutside = (event) => {
    if (!findUpTag(event.target, this.wrapperRef)) {
      this.onLeaveHover();
    }
  };

  componentDidMount() {
    document.addEventListener('mousedown', this.handleClickOutside);
  }

  componentWillUnmount() {
    document.removeEventListener('mousedown', this.handleClickOutside);
  }
  componentDidUpdate(prevProps) {
    let pathname =
      this.props.location && this.props.location.pathname
        ? this.props.location.pathname
        : document.location.pathname;
    if (
      pathname === pageLinks.markdownOptimization[4] &&
      this.props.dataLoadStatus !== prevProps.dataLoadStatus &&
      this.props.dataLoadStatus
    ) {
      this.props.history.push('/vis/Markdown/reporting');
    }
  }

  toggleSideBarExpansionHandler = (expand) => {
    if (expand === true || this.state.class !== 'active') {
      this.setState({
        class: 'active',
        display: 'inline-block',
      });
      return;
    }
    if (this.state.class === 'active') {
      this.setState({
        class: '',
        display: '',
      });
    }
  };

  onLogoutClick() {
    this.props.history.push('/login');
  }
  onListIconClick = (index) => {
    if (this.state.class === 'active') {
      this.setState({
        class: '',
        display: '',
      });
      return;
    }
    this.setState({
      class: '',
    });

    let url = pageLinks.markdownOptimization[index];
    this.props.history.push(url);
  };

  handleResize = (event) => {
    setTimeout(() => {
      if (this.wrapperRef && !isLocalNode(this.wrapperRef, event.target)) {
        this.toggleSideBarExpansionHandler();
      }
    }, 300);
  };

  /**
   * Set the wrapper ref
   */
  setWrapperRef = (node) => {
    this.wrapperRef = node;
  };

  render() {
    let pathname =
      this.props.location && this.props.location.pathname
        ? this.props.location.pathname
        : document.location.pathname;

    return (
      <nav
        id="sidebar"
        className={
          this.props.dataLoadStatus ? `${this.state.class} data-load-active` : this.state.class
        }
        ref={this.setWrapperRef}>
        <div className="sidebar-toggle-arrow">
          {this.state.class === 'active' ? (
            <i
              className="fa fa-angle-double-left sidebar-toggle-arrow-icon sidebar-toggle-arrow-left-icon"
              aria-hidden="true"
              onClick={this.toggleSideBarExpansionHandler}
            />
          ) : (
            <i
              className="fa fa-angle-double-right sidebar-toggle-arrow-icon sidebar-toggle-arrow-right-icon"
              aria-hidden="true"
              onClick={this.toggleSideBarExpansionHandler}
            />
          )}
        </div>
        <div className="sidebar-header" />
        <ul
          className={`sub-ul  
                ${pageLinks.markdownOptimization.includes(pathname) ? 'show' : 'collapse'}
              `}
          id="markdownOptimizationOptions">
          <li
            title="Reporting section"
            onClick={() =>
              // this.toggleSideBarExpansionHandler()
              this.onListIconClick(1)
            }
            className={pathname === pageLinks.markdownOptimization[1] ? 'active' : ''}>
            <img
              className="img-icons"
              src={require('../../images/leftPanel/reporting.svg')}
              alt="Reporting Section"
            />
            <Link to={pageLinks.markdownOptimization[1]}>
              <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                Reporting Section
              </span>
            </Link>
          </li>
          <li
            title="Actual vs Planned reports"
            onClick={() =>
              // this.toggleSideBarExpansionHandler()
              this.onListIconClick(2)
            }
            className={pathname === pageLinks.markdownOptimization[2] ? 'active' : ''}>
            <img
              className="img-icons"
              src={require('../../images/leftPanel/planned.svg')}
              alt="Actual vs Planned Reports"
            />
            <Link to={pageLinks.markdownOptimization[2]}>
              <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                Actual vs Planned Reports
              </span>
            </Link>
          </li>
          {/* <li
            title="Calendar"
            onClick={() => this.toggleSideBarExpansionHandler()}
            className={pathname === pageLinks.markdownOptimization[0] ? 'active' : ''}>
            <img
              className="img-icons"
              src={require('../../images/leftPanel/calendar.svg')}
              alt="Markdown Optimization"
            />
            <Link to={pageLinks.markdownOptimization[0]}>
              <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                Calendar
              </span>
            </Link>
          </li> */}
          <li
            title="Create markdown"
            onClick={() =>
              // this.toggleSideBarExpansionHandler()
              this.onListIconClick(4)
            }
            className={pathname === pageLinks.markdownOptimization[4] ? 'active' : ''}>
            <img
              className="img-icons"
              src={require('../../images/leftPanel/create.svg')}
              alt="Create Markdown"
            />
            <Link to={pageLinks.markdownOptimization[4]}>
              <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                Create Markdown
              </span>
            </Link>
          </li>
          <li
            title="Draft/Status"
            onClick={() =>
              // this.toggleSideBarExpansionHandler()
              this.onListIconClick(5)
            }
            className={pathname === pageLinks.markdownOptimization[5] ? 'active' : ''}>
            <img
              className="img-icons"
              src={require('../../images/leftPanel/status.svg')}
              alt="Draft/Status"
            />
            <Link to={pageLinks.markdownOptimization[5]}>
              <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                Draft/Status
              </span>
            </Link>
          </li>
          {localStorage.getItem('id') != restrictedUser && (
            <li
              title="Finalize markdown"
              onClick={() =>
                // this.toggleSideBarExpansionHandler()
                this.onListIconClick(6)
              }
              className={pathname === pageLinks.markdownOptimization[6] ? 'active' : ''}>
              <img
                className="img-icons"
                src={require('../../images/leftPanel/finalize.svg')}
                alt="Finalize Markdown"
              />
              <Link to={pageLinks.markdownOptimization[6]}>
                <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                  Finalize Markdown
                </span>
              </Link>
            </li>
          )}
          <li
            title="Configuration"
            onClick={() =>
              // this.toggleSideBarExpansionHandler()
              this.onListIconClick(7)
            }
            className={pathname.includes(pageLinks.markdownOptimization[7]) ? 'active' : ''}>
            <i class="fa fa-cog config-icon" aria-hidden="true"></i>
            <Link to={pageLinks.markdownOptimization[7]}>
              <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                Configuration
              </span>
            </Link>
          </li>
          {/* <li
              onClick={() => this.toggleSideBarExpansionHandler()}
              className={classNames('link', CONFIGURATION.includes(pathname) ? 'active' : '')}>
              <Link> 
                <div className="link-label">
                  <img className="imgIcons" src={settings} alt="" title="Configuration" />
                  <span className="hidetext fontstyling">Configuration</span>
                </div>
              </Link>
            </li> */}
        </ul>
      </nav>
    );
  }
}

SideBar.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  authReducer: PropTypes.object.isRequired,
};

const mapStateToProps = (state, ownProps) => ({
  authReducer: state.authReducer,
  dataLoadStatus: state.dataLoadReducer.dataLoadStatus,
  updateSideBar: state.plannedReportReducer.updateSideBar,
});

export default connect(mapStateToProps, {
  logoutUser,
})(withRouter(SideBar));
