import React, { Component } from 'react';
import Select from 'react-select';
import Filters from '../../../services/filters';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import DataTable from '../../../services/reactTable';
// import { DateRangePicker } from 'react-dates';
import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';

import './StyleConfigFilter.css';

import MultiSelect from '../../../components/MultiSelect';

const deptData = [
  { label: 'MENS ATHLETIC APPAREL', value: 'MENS ATHLETIC APPAREL' },
  { label: 'GOLF BALLS', value: 'GOLF BALLS' },
  { label: 'SOCKS', value: 'SOCKS' },
  { label: 'MENS OUTERWEAR', value: 'MENS OUTERWEAR' },
];

const subDeptData = [
  { label: 'ALL', value: 'ALL' },
  { label: 'NIKE', value: 'NIKE' },
  { label: 'Adidas', value: 'Adidas' },
];

const classData = [
  { label: 'ALL', value: 'ALL' },
  { label: 'N RUNNING', value: 'N RUNNING' },
  { label: 'A JORDAN', value: 'A JORDAN' },
];

const subClassData = [
  { label: 'ALL', value: 'ALL' },
  { label: 'N RUNNING TOPS', value: 'N RUNNING TOPS' },
  { label: 'A JORDAN SL ACCESSORIES', value: 'A JORDAN SL ACCESSORIES' },
];

const styleFamilyData = [
  { label: 'DRY GFX HOODY', value: 'DRY GFX HOODY' },
  { label: 'B NK THRMA PANT OH', value: 'B NK THRMA PANT OH' },
  { label: 'CLUB HOODIE', value: 'CLUB HOODIE' },
];

const styleData = [
  {
    label: 'M NK BRTHE CHLLGR TOP SS',
    value: 'M NK BRTHE CHLLGR TOP SS',
  },
  {
    label: 'M NK DRY MILER TOP SS SSNL GX',
    value: 'M NK DRY MILER TOP SS SSNL GX',
  },
  {
    label: 'M NK TOP SL HOODIE SSNL GX',
    value: 'M NK TOP SL HOODIE SSNL GX',
  },
  {
    label: 'JORDAN BELL CAP',
    value: 'JORDAN BELL CAP',
  },
  {
    label: 'JORDAN CARBON FIBER FITTED',
    value: 'JORDAN CARBON FIBER FITTED',
  },
];

const styleGroupData = [
  { label: 'HOODIE GROUP', value: 'HOODIE GROUP' },
  { label: 'SHORTS GROUP', value: 'SHORTS GROUP' },
  { label: 'FLEECE GROUP', value: 'FLEECE GROUP' },
];

const edlpData = [
  { label: 'EDLP', value: 'EDLP' },
  { label: 'NON EDLP', value: 'NON EDLP' },
];

const outerwearData = [
  { label: 'ALL', value: 'ALL' },
  { label: 'COLD', value: 'COLD' },
  { label: 'VERY COLD', value: 'VERY' },
];

export class StyleConfigFilter extends Component {
  state = {
    selectedDept: '',
    selectedSubDept: '',
    selectedCategory: '',
    selectedSubCategory: '',
    selectedBrand: '',
    selectedStyleFamily: '',
    selectedStyleGroup: '',
    selectedStyle: '',
    SelectedGeoClimaticCluster: '',
    SelectedEdlp: '',
  };

  // componentDidMount() {
  //   if (
  //     this.props.selectedDept &&
  //     this.props.selectedSubDept &&
  //     this.props.selectedCategory &&
  //     this.props.selectedSubCategory &&
  //     (this.props.selectedStyleFamily || this.props.selectedStyleGroup || this.props.selectedStyle)
  //   ) {
  //     this.setState({
  //       selectedDept: {
  //         label: this.props.selectedDept.replace('%20', ' '),
  //         value: this.props.selectedDept,
  //       },
  //       selectedSubDept: {
  //         label: this.props.selectedSubDept.replace('%20', ' '),
  //         value: this.props.selectedSubDept,
  //       },
  //       selectedCategory: {
  //         label: this.props.selectedCategory.replace('%20', ' '),
  //         value: this.props.selectedCategory,
  //       },
  //       selectedStyleFamily: {
  //         label: this.props.selectedStyleFamily.replace('%20', ' '),
  //         value: this.props.selectedStyleFamily,
  //       },
  //       selectedStyleGroup: {
  //         label: this.props.selectedStyleGroup.replace('%20', ' '),
  //         value: this.props.selectedStyleGroup,
  //       },
  //       selectedStyle: {
  //         label: this.props.selectedStyle.replace('%20', ' '),
  //         value: this.props.selectedStyle,
  //       },
  //     });
  //   }
  // }

  dropDownChangeHandler = (tag, value) => {
    console.log('Vdflgorpppp', tag, value);

    if (tag === 'reset') {
      this.setState({
        selectedDept: '',
        selectedSubDept: [],
        selectedCategory: [],
        selectedSubCategory: [],
        selectedBrand: '',
        selectedStyleFamily: '',
        selectedStyleGroup: '',
        selectedStyle: '',
        SelectedGeoClimaticCluster: '',
      });
      return;
    }
    if (tag === 'DEPARTMENT') this.setState({ selectedDept: value });
    if (tag === 'SUB_DEPARTMENT') this.setState({ selectedSubDept: value });
    if (tag === 'CATEGORY') this.setState({ selectedCategory: value });
    if (tag === 'SUB_CATEGORY') this.setState({ selectedSubCategory: value });
    if (tag === 'BRAND') this.setState({ selectedBrand: value });
    if (tag === 'STYLE_FAMILY') this.setState({ selectedStyleFamily: value });
    if (tag === 'STYLE_GROUP') this.setState({ selectedStyleGroup: value });
    if (tag === 'STYLE') this.setState({ selectedStyle: value });
    if (tag === 'GEO_CLIMATIC_CLUSTER') this.setState({ SelectedGeoClimaticCluster: value });
    if (tag === 'EDLP') this.setState({ SelectedEdlp: value });
    // this.setState((state, props) => ({
    //   selectedDept: tag === 'DEPARTMENT' ? value : state.selectedDept,
    //   selectedSubDept: tag === 'SUB_DEPARTMENT' ? value : state.selectedSubDept,
    //   selectedCategory: tag === 'CATEGORY' ? value : state.selectedCategory,
    //   selectedSubCategory: tag === 'SUB_CATEGORY' ? value : state.selectedSubCategory,
    //   selectedBrand: tag === 'BRAND' ? value : state.selectedBrand,
    //   selectedStyleFamily: tag === 'STYLE_FAMILY' ? value : state.selectedStyleFamily,
    //   selectedStyleGroup: tag === 'STYLE_GROUP' ? value : state.selectedStyleGroup,
    //   selectedStyle: tag === 'STYLE' ? value : state.selectedStyle,
    //   SelectedGeoClimaticCluster:
    //     tag === 'GEO_CLIMATIC_CLUSTER' ? value : state.SelectedGeoClimaticCluster,
    //   SelectedEdlp: tag === 'EDLP' ? value : state.SelectedEdlp,
    // }));
  };

  render() {
    return (
      <>
        <div className={this.props.compType === 'createStyleGroup'}>
          <Filters
            hideStoreClusterFilter
            hidefilterButton
            hierarchy={['hierarchy1', 'hierarchy2', 'hierarchy3', 'hierarchy4', 'hierarchy5']}
            // onFilter={this.onFilter}
          />
          {this.props.compType === 'createStyleGroup' ? (
            <div className="col-md-2  mt-5">
              <button className="col-md-5 btn btn-primary btn-filter">
                <span>
                  <i className="fa fa-filter"></i>&nbsp; Filter
                </span>
              </button>
              <button
                className="col-md-6 offset-1 btn btn-primary btn-filter btn-filter-reset"
                onClick={() => this.dropDownChangeHandler('reset')}>
                <span>
                  <i className="fa fa-repeat"></i>&nbsp; Reset
                </span>
              </button>
            </div>
          ) : null}
        </div>

        {this.props.compType !== 'createStyleGroup' ? (
          <div className="row add-sku-row">
            {/* <div className="col-md-3">
              <label className="input-label">Style Family</label>
              <div className="row">
                <div className="col-md-1">
                  <input
                    type="radio"
                    name="selectStyleType"
                    value="styleFamily"
                    onChange={(e) => this.props.onStyleTypeChangeHandler(e.target.value)}
                    checked={'styleFamily' === this.props.styleType}
                  />
                </div>
                <div className="col-md-10">
                  <Select
                    className="style-config-filter"
                    isDisabled={this.props.loading}
                    name="styleFamily"
                    // allowSelectAll
                    placeholder="Select Style Family"
                    options={styleFamilyData}
                    value={this.state.selectedStyleFamily}
                    onChange={(val) => this.dropDownChangeHandler('STYLE_FAMILY', val)}
                  />
                </div>
              </div>
            </div> */}
            <div className="col-md-3">
              <label className="mb-1 mt-3">Style Group</label>{' '}
              <div className="row">
                <div className="col-md-1">
                  <input
                    type="radio"
                    name="selectStyleType"
                    value="styleGroup"
                    onChange={(e) => this.props.onStyleTypeChangeHandler(e.target.value)}
                    checked={'styleGroup' === this.props.styleType}
                  />
                </div>
                <div className="col-md-10">
                  <Select
                    className="style-config-filter"
                    isDisabled={this.props.loading}
                    name="styleGroup"
                    // allowSelectAll
                    placeholder="Select Style Group"
                    options={styleGroupData}
                    value={this.state.selectedStyleGroup}
                    onChange={(val) => this.dropDownChangeHandler('STYLE_GROUP', val)}
                  />
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <label className="mb-1 mt-3">Style</label>
              <div className="row">
                <div className="col-md-1">
                  <input
                    type="radio"
                    name="selectStyleType"
                    value="style"
                    onChange={(e) => this.props.onStyleTypeChangeHandler(e.target.value)}
                    checked={'style' === this.props.styleType}
                  />
                </div>
                <div className="col-md-10">
                  <Select
                    className="style-config-filter"
                    isDisabled={this.props.loading}
                    name="style"
                    // allowSelectAll
                    placeholder="Select style"
                    options={styleData}
                    value={this.state.selectedStyle}
                    onChange={(val) => this.dropDownChangeHandler('STYLE', val)}
                  />
                </div>
              </div>
            </div>
            <div className="col-md-2">
              <button className="col-md-5 btn btn-primary btn-filter">
                <span>
                  <i className="fa fa-filter"></i>&nbsp; Filter
                </span>
              </button>
              <button
                className="col-md-6 offset-1 btn btn-primary btn-filter btn-filter-reset"
                onClick={() => this.dropDownChangeHandler('reset')}>
                <span>
                  <i className="fa fa-repeat"></i>&nbsp; Reset
                </span>
              </button>
            </div>
          </div>
        ) : null}
      </>
    );
  }
}

export default StyleConfigFilter;
