import React, { Component } from 'react';
import { actionSkuLineData } from '../../actions/markdown';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import AddSkuFilter from '../createMarkdown/AddSkuFilter';
import {
  fetchStoreData,
  simulateMarkdownCadenceAndSkuList,
} from '../../actions/aiAssistedIn_PreSeasonActions';
import AlertDanger from '../../utils/AlertMessage/alertDanger';

class AddSku extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modalShow: false,
      selectedSkus: [],
      showAlertDanger: false,
    };
  }

  componentDidMount() {
    if (this.props.subClassID) {
      this.props.fetchStoreData({
        sku: '',
        category: 276,
        subcategory: 1114,
        classname: 303,
        subclass: this.props.sID,
        brand: this.props.bID,
        // category: data.SelectedCategory ? new String(data.SelectedCategory.value) : "",
        // subcategory: data.SelectedSubCategory ? new String(data.SelectedSubCategory.value) : "",
        // classname: data.SelectedClass ? new String(data.SelectedClass.value) : "",
        // subclass: data.SelectedSubClass ? new String(data.SelectedSubClass.value) : "",
        // brand: data.SelectedBrand ? new String(data.SelectedBrand.value) : "",
      });
    }
    // this.props.fetchCadenceAndSku({
    //   markdown: "",
    //   start_date: "2018-01-01",
    //   end_date: "2019-03-01",
    //   inventory: 10000000,
    //   skus: "",
    //   burn_rate: ".1"
    // });
  }

  componentDidUpdate(prevProps) {
    if (prevProps.selectSku != this.props.selectSku) {
      var skuData = this.props.selectSku;
      this.setState({
        selectedSkus: skuData.split(','),
      });
    }
  }

  setSelectedSku = (data) => {
    this.setState({
      selectedSkus: data,
    });
  };

  onClose = () => {
    this.setState({
      modalShow: !this.state.modalShow,
    });
  };

  onOpen = () => {
    this.setState({
      modalShow: true,
    });
  };

  onFilterClick = (data) => {
    var selectedStr = '';
    // selected sku in string format
    selectedStr += data.selectedSkus.join(',');
    if (data.type == 'home') {
      this.props.fetchStoreData({
        sku: selectedStr,
        category: data.SelectedCategory ? new String(data.SelectedCategory.value) : '',
        subcategory: data.SelectedSubCategory ? new String(data.SelectedSubCategory.value) : '',
        classname: data.SelectedClass ? new String(data.SelectedClass.value) : '',
        subclass: data.SelectedSubClass ? new String(data.SelectedSubClass.value) : '',
        brand: data.SelectedBrand ? new String(data.SelectedBrand.value) : '',
      });
    } else {
      this.props.fetchStoreData({
        skuGroup: selectedStr,
        category: data.SelectedCategory ? new String(data.SelectedCategory.value) : '',
        subcategory: data.SelectedSubCategory ? new String(data.SelectedSubCategory.value) : '',
        classname: data.SelectedClass ? new String(data.SelectedClass.value) : '',
        subclass: data.SelectedSubClass ? new String(data.SelectedSubClass.value) : '',
        brand: data.SelectedBrand ? new String(data.SelectedBrand.value) : '',
      });
    }

    // if (this.props && this.props.message.length>0) {
    // this.setState({
    //   showAlertDanger: true
    // });
    // setTimeout(() => {
    //   this.setState({
    //     showAlertDanger: false
    //   });
    // }, 3000);
    //  }
  };

  render() {
    const { message } = this.props;
    return (
      <div>
        <div className="alertDivs">
          {this.state.showAlertDanger ? (
            <AlertDanger message={message} show={this.state.showAlertDanger} />
          ) : null}
        </div>
        <div className="row ">
          <div className="col-md-12">
            <AddSkuFilter
              subClassID={this.props.subClassID}
              brandID={this.props.brandID}
              category={this.props.category}
              subCategory={this.props.subCategory}
              class={this.props.class}
              showSkuSelect={this.props.showSkuSelect}
              skuAPI={this.props.actionSkuLineData}
              setSelectedSku={this.setSelectedSku}
              modalShow={this.state.modalShow}
              switchSkuModal={this.onClose}
              startDate={this.props.startDate}
              endDate={this.props.endDate}
              nextYear={this.props.nextYear}
              prevYear={this.props.prevYear}
              selectedSkuData={this.state.selectedSkus}
              getSelectedSkuData={this.props.getSelectedSkuData}
              onFilterClick={this.onFilterClick}
            />
          </div>
        </div>
      </div>
    );
  }
}
// export default AddSku
const mapStateToProps = (state) => {
  return {
    loader: state.calendar.loader,
    lineDataCockpit: state.markdown.lineDataCockpit.data,
    errors: state.errors,
    message: state.aiAssitedIn_PreSeason.error,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    actionSkuLineData: (val) => dispatch(actionSkuLineData(val)),
    fetchStoreData: (payload) => dispatch(fetchStoreData(payload)),
    fetchCadenceAndSku: (payload) => dispatch(simulateMarkdownCadenceAndSkuList(payload)),
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
  // mapActionToProps
)(withRouter(AddSku));
