import axios from '../utils/axios';
import { config } from '../utils/config';
import { toast } from 'react-toastify';

const styleConfigActions = {
  onResetStyleColorTable: () => {
    return (dispatch) => {
      dispatch({
        type: 'RESET_STYLE_CONFIG',
      });
    };
  },
  onResetStyleConfigTable: () => {
    return (dispatch) => {
      dispatch({
        type: 'RESET_STYLE_CONFIG_TABLE_DATA',
      });
    };
  },

  fetchStyle: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/getStyleColorTableData/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'SET_STYLE',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return resp.data.data;
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return [];
        });
    };
  },
  getStyleConfigTableData: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/getCustomStyleColorTableData/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          let tableData = [
            {
              style_group: reqObj.style_config_id,
              subRows: resp.data.data.style_color_data,
            },
          ];
          dispatch({
            type: 'STYLE_CONFIG_TABLE_DATA',
            payload: tableData,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  setStyleConfigTableData: (data) => {
    return (dispatch) => {
      dispatch({
        type: 'STYLE_CONFIG_TABLE_DATA',
        payload: data,
      });
    };
  },
  createStyleGroup: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/createCustomStyleGroup/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          if (!resp.data.status) {
            toast.error('Something went wrong! Please try again.', {
              autoClose: 15000,
              hideProgressBar: true,
            });
            return false;
          } else if (resp.data.create_status == true) {
            toast.success('"Custom Style Group created successfully', {
              autoClose: 15000,
              hideProgressBar: true,
            });
            return true;
          }
          return resp.data;
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return false;
        });
    };
  },
  editStyleGroup: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/updateCustomStyleColor/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          if (!resp.data.status) {
            toast.error('Something went wrong! Please try again.', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          } else if (resp.data.create_status == true) {
            toast.success('Custom Style Group Updated Successfully', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return resp.data;
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  mapStyle: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/style-create/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Style mapping is Successful', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  editStyle: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/style-edit/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Style Updated Successfully', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
};
export default styleConfigActions;
