import React, { Component } from 'react';
import Select from 'react-select';
import { connect } from 'react-redux';
import Filters from '../../../services/filters';
import '../../Configuration/StoreConfigContainers/StoreConfig.css';
import './StyleConfigFilter.css';
import StyleConfigActions from '../../../actions/styleConfigActions';
const styleGroupData = [
  { label: 'HOODIE GROUP', value: 'HOODIE GROUP' },
  { label: 'SHORTS GROUP', value: 'SHORTS GROUP' },
  { label: 'FLEECE GROUP', value: 'FLEECE GROUP' },
];

export class StyleConfigFilter extends Component {
  state = {
    selectedStyleGroup: '',
    selectedStyle: '',
  };
  onStyleGroupChangeHandler = (val) => {
    this.setState({
      selectedStyleGroup: val,
    });
  };
  onStyleChangeHandler = (val) => {
    this.setState({
      selectedStyle: val,
    });
  };
  onFilter = () => {
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
      },
      styleType: this.props.styleType,
    };
    console.log(req, 'request');
  };
  render() {
    return (
      <>
        <div className={this.props.compType === 'createStyleGroup'}>
          <Filters
            hideStoreClusterFilter
            hidefilterButton
            hierarchy={['hierarchy1', 'hierarchy2', 'hierarchy3', 'hierarchy4', 'hierarchy5']}
            // onFilter={this.onFilter}
          />
          {this.props.compType === 'createStyleGroup' ? (
            <div className="col-md-2  mt-5">
              <button className="col-md-5 btn btn-primary btn-filter">
                <span>
                  <i className="fa fa-filter"></i>&nbsp; Filter
                </span>
              </button>
              <button
                className="col-md-6 offset-1 btn btn-primary btn-filter btn-filter-reset"
                // onClick={() => this.dropDownChangeHandler('reset')}
              >
                <span>
                  <i className="fa fa-repeat"></i>&nbsp; Reset
                </span>
              </button>
            </div>
          ) : null}
        </div>

        {this.props.compType !== 'createStyleGroup' ? (
          <div className="row add-sku-row">
            <div className="col-md-3">
              <label className="mb-1 mt-3">Style Group</label>{' '}
              <div className="row">
                <div className="col-md-1">
                  <input
                    type="radio"
                    name="selectStyleType"
                    value="styleGroup"
                    onChange={(e) => this.props.onStyleTypeChangeHandler(e.target.value)}
                    checked={'styleGroup' === this.props.styleType}
                  />
                </div>
                <div className="col-md-10">
                  <Select
                    className="style-config-filter"
                    name="styleGroup"
                    // allowSelectAll
                    placeholder="Select Style Group"
                    options={styleGroupData}
                    value={this.state.selectedStyleGroup}
                    onChange={this.onStyleGroupChangeHandler}
                  />
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <label className="mb-1 mt-3">Style</label>
              <div className="row">
                <div className="col-md-1">
                  <input
                    type="radio"
                    name="selectStyleType"
                    value="style"
                    onChange={(e) => this.props.onStyleTypeChangeHandler(e.target.value)}
                    checked={'style' === this.props.styleType}
                  />
                </div>
                <div className="col-md-10">
                  <Select
                    className="style-config-filter"
                    name="styleGroup"
                    // allowSelectAll
                    placeholder="Select Style Group"
                    options={styleGroupData}
                    value={this.state.selectedStyle}
                    onChange={this.onStyleChangeHandler}
                  />
                </div>
              </div>
            </div>
            <div className="col-md-2  mt-5">
              <button onClick={this.onFilter} className="col-md-5 btn btn-primary btn-filter">
                <span>
                  <i className="fa fa-filter"></i>&nbsp; Filter
                </span>
              </button>
              <button
                className="col-md-6 offset-1 btn btn-primary btn-filter btn-filter-reset"
                // onClick={() => this.dropDownChangeHandler('reset')}
              >
                <span>
                  <i className="fa fa-repeat"></i>&nbsp; Reset
                </span>
              </button>
            </div>
          </div>
        ) : null}
      </>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    styleGroups: store.styleReducer.styleGroups,
    style: store.styleReducer.style,
  };
};
const mapActionsToProps = {
  getStyleConfigTableData: StyleConfigActions.getStyleConfigTableData,
  fetchStyleGroups: StyleConfigActions.fetchStyleGroups,
  fetchStyle: StyleConfigActions.fetchStyleGroups,
};
export default connect(mapStateToProps, mapActionsToProps)(StyleConfigFilter);
