import React from 'react';
import "./createPromoStyle.css";
export default class Parameters extends React.Component {
    constructor(props) {
        super(props)
    }
    emptyValueFormatter = (value) => {
        try{
            if (value) {
            var tempValue = String(value).replace(",","");
            var toSting = Math.round(tempValue);
            if (tempValue > 0) {
                return toSting.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
            } else {
                var splitAvalue = Math.abs(toSting);
                return ("-" + splitAvalue.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",") + ""
                // "(" + splitAvalue.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",") + ")"
                );
            }
            } else {
                if (value == 0) {
                    return 0;
                } else {
                    return "-";
                }
            }
        }
        catch(err){
            return err;
        }
      }
    render() {
        let optimumPromo;
        if (this.props.optimumPromoValue > 0) {
            optimumPromo = (
                <React.Fragment>
                <div className="parameter-input-container" style={{position:'relative'}}>
                    <input
                        className='radio-input'
                        type="radio"
                        checked={this.props.paramActiveIndex === -1} onChange={() => this.props.setParamActiveIndex(-1)}
                    ></input>
                    {this.props.promoType && (this.props.promoType.value === 'A' || this.props.promoType.value === 'F') && (
                        <span className='input-logo'>$</span>
                    )}
                    <input type="number" className='form-control' value={this.emptyValueFormatter(this.props.optimumPromoValue)} onChange={(event) => { }} disabled />
                    {this.props.promoType && this.props.promoType.value === 'P' && <span className='input-logo'>%</span>}
                    <div className="optimum-margin">(Optimum for margin)</div>
                </div>

                </React.Fragment>
            )
        }
        let renderInputs;
        renderInputs = this.props.paramValues.map(function (item, index) {
            return (
                <React.Fragment>
                    <div className="parameter-input-container">
                        <input
                            className='radio-input'
                            type="radio"
                            checked={this.props.paramActiveIndex === index} onChange={() => this.props.setParamActiveIndex(index)}
                        ></input>
                        {this.props.promoType && (this.props.promoType.value === 'A' || this.props.promoType.value === 'F') && (
                            <span className='input-logo'>$</span>
                        )}
                        <input type="number" className='form-control' value={this.emptyValueFormatter(item)} onChange={(event) => this.props.editParamValues(index, event.target.value)} />
                        {this.props.promoType && this.props.promoType.value === 'P' && <span className='input-logo'>%</span>}
                    </div>
                    {/* <input type='radio' checked={this.props.paramActiveIndex === index} onChange={() => this.props.setParamActiveIndex(index)}/>
                    <input className='' type='number' value={item} onChange={(event) => this.props.editParamValues(index,event.target.value)} /> */}
                </React.Fragment>
            )
        }.bind(this));
        return (
            <div>
                <h5 className="font-size-14 font-bold">Parameters</h5>
                <div className="col-md-6 parameter-container" style={{ margin: "0 auto" }}>
                    {/* <div className="set-min-height"> */}
                        <div className='row set-min-height'>
                            <div className="parameter-container-header col-md-12">Target Promotion Value</div>

                            {optimumPromo}
                            {renderInputs}

                        </div>
                    {/* </div> */}
                </div>
                <div className="col-md-6" style={{ margin: "0 auto", marginTop: '0.5em' }}>
                    <div className='row'>
                        <div className='col-md-12' style={{ paddingTop: "10px" }}>
                            {/*<button className='btn btn-primary pull-right btn-create-promo' onClick={this.props.suggestParamValue} style={{ marginLeft: "10px" }} 
                                    disabled={!this.props.categories || this.props.categories.length == 0}>Suggest promo value
                            </button>*/}
                            <button className='btn btn-primary pull-right btn-create-promo' onClick={this.props.addParamValues}>+ Add</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
