import React from 'react';

function StatusCellStyling({ value }) {
  switch (value) {
    case 'Rejected':
      return (
        <span>
          <i className="fa fa-ban fa-lg" aria-hidden="true" style={{ color: '#D84039' }}></i>{' '}
          {value} &nbsp; &nbsp;{' '}
          <i
            className="fa fa-commenting fa-lg"
            aria-hidden="true"
            style={{ cursor: 'pointer' }}></i>
        </span>
      );
    case 'Pending':
      return (
        <span>
          <i
            className="fa fa-exclamation-circle fa-lg"
            style={{ color: '#E0D337' }}
            aria-hidden="true"></i>{' '}
          {value}
        </span>
      );
    case 'Approved':
      return (
        <span>
          <i
            className="fa fa-check-circle fa-lg"
            aria-hidden="true"
            style={{ color: '#59C770' }}></i>{' '}
          {value}
        </span>
      );

    default:
      return null;
  }
}

export default () => {
  const colDef = [
    {
      Header: 'MD plan',
      accessor: 'md_plan',
      disableGroupBy: false,
      width: 50,
    },
    {
      Header: 'Scenario',
      accessor: 'scenario',
      disableGroupBy: true,
      width: 50,
    },
    {
      Header: 'SKU Description',
      accessor: 'sku_desc',
      disableGroupBy: true,
      width: 55,
    },
    {
      Header: 'SKU ID',
      accessor: 'sku_id',
      disableGroupBy: true,
      width: 50,
    },
    {
      Header: 'Store ID',
      accessor: 'store_id',
      disableGroupBy: true,
      width: 55,
    },
    {
      Header: 'Markdown %',
      accessor: 'markdown_perc',
      disableGroupBy: true,
      width: 55,
    },
    {
      Header: 'Sell through %',
      accessor: 'sell_through_perc',
      disableGroupBy: true,
      width: 50,
    },
    {
      Header: 'Margin %',
      accessor: 'margin_perc',
      disableGroupBy: true,
      width: 70,
    },
    {
      Header: 'IOH',
      accessor: 'ioh',
      disableGroupBy: true,
      width: 70,
    },
    {
      Header: 'MD Status',
      accessor: 'md_status',
      disableGroupBy: true,
      width: 70,
      align: 'left',
      style: {
        textAlign: 'center',
      },
    },
    {
      Header: 'Status',
      accessor: 'status',
      disableGroupBy: true,
      width: 70,
      Cell: StatusCellStyling,
    },
  ];
  return colDef;
};
