import React from 'react'
import { DateRangePicker } from 'react-dates'
import * as moment from 'moment';


class Constraints extends React.Component {
  state = {
    focusedInput: null
  };

  isOutsideRange = day => day.isBefore(moment().subtract(3, "years"));

  render() {
    return (
      <div style={{ marginBottom: '5px' }}>
        <h5 className="font-size-14 font-bold" style={{ paddingLeft: '12px' }}>
          Constraints
        </h5>
        <div className="row constaints-row">
          <div className="col-md-2">
            <label className="input-label">Target Clearance %</label>
            <input className="form-control" type="text" onChange={this.props.targetClearenceValueHandler}></input>
          </div>
          <div className="col-md-2">
            <label className="input-label">Margin %</label>
            <input className="form-control" type="text" onChange={this.props.marginValueHandler}></input>
          </div>
          <div className="col-md-3">
            <label className="input-label">Date Range</label>
            <DateRangePicker
              startDate={this.props.startDate}
              startDateId="startDate"
              endDate={this.props.endDate}
              endDateId="endDate"
              openDirection="up"
              onDatesChange={({ startDate, endDate }) => this.props.onDatesChange({ startDate, endDate })}
              focusedInput={this.state.focusedInput}
              onFocusChange={focusedInput2 =>
                this.setState({ focusedInput: focusedInput2 })
              }
              // isOutsideRange={this.isOutsideRange}
            />
          </div>
          <div className="col-md-2">
            <label className="input-label">Inverse</label>
            <input
              type="radio"
              className="inverse-radio mr-2"
              name="inverse"
              onChange={() => this.props.inverseValueHandler(true)}
              checked={this.props.inverserVal}
            ></input>
            True
            <input
              type="radio"
              className="inverse-radio mr-2 ml-4"
              name="inverse"
              onChange={() => this.props.inverseValueHandler(false)}
              checked={!this.props.inverserVal}
            ></input>
            False
          </div>
          <div className="col-md-3">
            <label className="input-label">Cadence</label>
            <input
              type="radio"
              className="inverse-radio mr-2"
              name="overall"
              onChange={() => { return !this.props.disableCadenceRadio ? this.props.constraintsCadenceHandler('overall') : null }}
              checked={this.props.cadenceConstraintsVal === 'overall'}
            ></input>
            Overall
            <input
              type="radio"
              className="inverse-radio mr-2 ml-4"
              name="cadence"
              onChange={() => { return !this.props.disableCadenceRadio ? this.props.constraintsCadenceHandler('sku') : null }}
              checked={this.props.cadenceConstraintsVal === 'sku'}
            ></input>
            SKU/SKU Group level
          </div>
        </div>
      </div>
    )
  }
}
export default Constraints;
