import React, { useState, useEffect } from 'react';
import StyleConfiguration from './StyleConfiguration';
import StoreConfiguration from './StoreConfiguration';
import { Route, Switch, Redirect, Link } from 'react-router-dom';
import { RoutedTabs, NavTab } from 'react-router-tabs';
import './react-router-tabs.css';
import ConfigurationStyleGroup from './StylesConfigContainers/CreateStyleGroup';
import ConfigurationStyleFamily from './StylesConfigContainers/CreateStyleFamily';
import ConfigurationStyleCreate from './StylesConfigContainers/CreateStyle';
import ConfigurationStoreGroup from './StoreConfigContainers/CreateStoreGroup';
// import ConfigurationStoreGroupEdit from './StoreConfigContainers/EditStoreGroup'

function ConfigurationContainer(props) {
  const [activeTab, setActiveTab] = useState(0);

  useEffect(() => {
    console.log('asdfhsahiw', props);
    const tabActive = props.history.location.pathname.includes('/vis/Markdown/configuration/store')
      ? 1
      : 0;
    setActiveTab(tabActive);
  }, []);

  // let switchTabs = (tab) => {
  //   Number(tab) === 1 && props.history.push('/vis/Markdown/configuration/store')
  //   Number(tab) === 0 && props.history.push('/vis/Markdown/configuration/style')
  //   setActiveTab(tab);
  // }
  const { path } = props.match;
  return (
    <div className="calendar-container mt-3" id="main-calendar-container">
      <NavTab to={'/vis/Markdown/configuration/style'}>Style Config</NavTab>
      <NavTab to={'/vis/Markdown/configuration/store'}>Store Config</NavTab>

      <Switch>
        <Route
          exact
          path={'/vis/Markdown/configuration'}
          render={() => <Redirect replace to={'/vis/Markdown/configuration/style'} />}
        />
        <Route exact path={'/vis/Markdown/configuration/style'} component={StyleConfiguration} />
        <Route exact path={'/vis/Markdown/configuration/store'} component={StoreConfiguration} />
        <Route
          exact
          path={'/vis/Markdown/configuration/style/group'}
          component={ConfigurationStyleGroup}
        />
        <Route
          exact
          path={'/vis/Markdown/configuration/style/family'}
          component={ConfigurationStyleFamily}
        />
        <Route
          exact
          path={'/vis/Markdown/configuration/style/create'}
          component={ConfigurationStyleCreate}
        />
        <Route
          exact
          path={'/vis/Markdown/configuration/store/group/create'}
          component={ConfigurationStoreGroup}
        />
        {/* <Route path={'/vis/Markdown/configuration/store/group/edit'+'/:id'} component={ConfigurationStoreGroupEdit} /> */}
      </Switch>
    </div>
  );
}

export default ConfigurationContainer;
