import React from 'react';

var ranges = [
  { divider: 1e9, suffix: 'B' },
  { divider: 1e6, suffix: 'M' },
  { divider: 1e3, suffix: 'k' },
];
function formatNumber(n) {
  for (var i = 0; i < ranges.length; i++) {
    if (n >= ranges[i].divider) {
      let newNumber = (n / ranges[i].divider).toFixed(2);
      let roundOffNumber = Math.abs(newNumber);
      return roundOffNumber.toString() + ranges[i].suffix;
    }
  }
  return n;
}

const InventoryInfo = (props) => {
  console.log(props.totalStores, 'totalStores');
  console.log(!props.totalStores, '!totalStores');
  return (
    <div>
      <h5 className="font-size-14 font-bold" style={{ paddingLeft: '12px' }}>
        Inventory Info
      </h5>{' '}
      <div className="d-flex" style={{ justifyContent: ' space-around' }}>
        {/* <div className="col-md-1 offset-md-1"> */}
        <div className="info-icon">
          <i className="inventory-icon"></i>
          {/* </div> */}
        </div>
        {/* <div className="col-md-1"> */}
        <div className="info-data">
          <p className="font-size-14 mb-0 font-normal">In DC</p>
          <div className="info-numbers">{formatNumber(props.totalInDc)}</div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1"> */}
        <div className="info-data">
          <p className="font-size-14 mb-0 font-normal">Store On Hand</p>
          <div className="info-numbers">{formatNumber(props.totalOnHand)}</div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1"> */}
        <div className="info-data">
          <p className="font-size-14 mb-0 font-normal">Store On Order</p>
          <div className="info-numbers">{formatNumber(props.totalOnOrder)}</div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1"> */}
        <div className="info-data ">
          <p className="font-size-14 mb-0 font-normal">Store In Transit</p>
          <div className="info-numbers">{formatNumber(props.totalInTransit)}</div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1 offset-md-1"> */}
        <div className="info-icon">
          <i className="store-icon"></i>
        </div>
        {/* </div> */}
        {/* <div className="col-md-1"> */}
        <div className="info-data ">
          <p className="font-size-14 mb-0 font-normal ">Stores Selected</p>
          <div className="info-numbers ">
            {formatNumber(props.selectedStores)}/
            <span className="font-size-14 font-bold">{formatNumber(props.totalStores)}</span>
          </div>
        </div>
        {/* </div> */}
        {/* <div className="col-md-2"> */}
        <div className="info-data">
          <p className="font-size-14 mb-0 font-normal ">Limit clearance to few stores</p>
          <button
            className="btn btn-primary"
            disabled={!props.totalStores}
            style={{ height: '42%', width: '187px' }}
            onClick={() => props.onExcludeStores()}>
            Exclude Stores
          </button>
        </div>
        {/* </div> */}
      </div>
    </div>
  );
};
export default InventoryInfo;
