import api from 'axios';
import { config } from '../utils/config';

const markDowns = {
  getMarkdowns: (payload) => {
    return (dispatch) => {
      return api({
        url: `${config.createMarkdownURL}/getMarkDowns/`,
        method: 'POST',
        data: payload,
      })
        .then((resp) => {
          const { columnHeaders, markdown_data } = resp.data.data;
          dispatch({
            type: 'SET_MARK_DOWNS',
            value: markdown_data,
          });
          dispatch({
            type: 'SET_MD_COLUMN_HEADERS',
            value: columnHeaders,
          });
          return resp.data.data;
        })
        .catch((err) => {
          console.log(err);
          return err;
        });
    };
  },
  setMarkDowns: (payload) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_MARK_DOWNS',
        value: payload,
      });
    };
  },
  setMDColumnHeaders: (payload) => {
    return (dispatch) => {
      dispatch({
        type: 'SET_MD_COLUMN_HEADERS',
        value: payload,
      });
    };
  },
};

export default markDowns;
