import React, { Component } from 'react';
import { labels } from './config';
import Select from '../select';
import './index.css';
import { connect } from 'react-redux';
import { DateRangePicker } from 'react-dates';
import FilterActions from '../../actions/filtersAction';
import moment from 'moment';

class Filters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      drops: null,
      startDate: null,
      filters: {},
      endDate: null,
      hierarchy1: null,
      hierarchy2: null,
      hierarchy3: null,
      hierarchy4: null,
      hierarchy5: null,
      hierarchy6: null,
      hierarchy7: null,
    };
    props.getDrops();
    props.getHierarchyLevel1();
  }

  getHierarchyOptions = (val, hierarchy) => {
    let resetHierachies = {};
    for (let i = hierarchy + 1; i < this.props.hierarchy.length; i++) {
      resetHierachies[`hierarchy${i}`] = null;
    }
    this.setState({ [`hierarchy${hierarchy}`]: val, ...resetHierachies }, async () => {
      await this.props[`setSelectedHierarchy${hierarchy}`](val.map((e) => e.value));
      let func = hierarchy === 6 ? `getHierarchyLevel7` : `getHierarchyLevel${hierarchy + 1}`;
      let req = {};
      // if (this.props.selectedDrops.length) {
      //   req.drops = this.props.selectedDrops;
      // }
      for (let i = 1; i <= hierarchy; i++) {
        req[`hierarchy${i}`] = this.props[`selectedHierarchy${i}`];
      }
      if (this.props[func]) {
        if (Object.keys(req).length) {
          this.props[func](req);
        } else {
          this.props[func]();
        }
      }
      this.setState({ filters: { ...req } });
    });
  };
  setDrop = (val) => {
    let req = {};
    let selItems = val.map((item) => item.label);
    req['drop'] = selItems;
    this.setState({ filters: { ...req }, drops: val }, () => {
      this.props.setSelectedDrops(val.map((v) => v.value));
    });
  };
  reset = () => {
    this.setState(
      {
        drops: null,
        startDate: null,
        endDate: null,
        hierarchy1: null,
        hierarchy2: null,
        hierarchy3: null,
        hierarchy4: null,
        hierarchy5: null,
        hierarchy6: null,
        hierarchy7: null,
      },
      () => this.props.resetFilters()
    );
  };
  onDatesChange = ({ startDate, endDate }) => {
    this.setState({
      startDate,
      endDate,
    });
    let weeks = [];
    if (endDate) {
      for (let i = moment(startDate).week(); i <= moment(endDate).week(); i++) {
        weeks.push(i);
      }
    }
    this.props.setDateRange({
      startDate: moment(startDate).format('MM-DD-YYYY'),
      endDate: moment(endDate).format('MM-DD-YYYY'),
      weeks,
    });
  };
  render() {
    return (
      <React.Fragment>
        <div className="row ml-4">
          {this.props.drop && (
            <div className="col-md-2 filter-block">
              <label className="mb-1 mt-3">{labels.drop}</label>
              <Select
                options={this.props.drops}
                onChange={(option) => this.setDrop(option)}
                getOptionLabel={(option) => option.label + (option.year ? option.year : '')}
                value={this.state.drops}
                isMulti
                allowSelectAll
                // isDisabled={!(this.state[`hierarchy${i}`] && this.state[`hierarchy${i}`].length)}
              />
            </div>
          )}
          {this.props.hierarchy.map((filter, i) => (
            <div className="col-md-2 filter-block" key={filter}>
              <label className="mb-1 mt-3">{labels[filter]}</label>
              <Select
                options={this.props[filter]}
                onChange={(option) => this.getHierarchyOptions(option, i + 1)}
                value={this.state[`hierarchy${i + 1}`]}
                isMulti
                allowSelectAll
                // isDisabled={!(this.state[`hierarchy${i}`] && this.state[`hierarchy${i}`].length)}
              />
            </div>
          ))}
          <div className="col-md-2 filter-block">
            <label className="mb-1 mt-3">Date range</label>
            <DateRangePicker
              showClearDates
              startDate={this.state.startDate}
              startDateId="startDate"
              endDate={this.state.endDate}
              endDateId="endDate"
              // onDatesChange={({ startDate, endDate }) => this.setState({ startDate, endDate })}
              onDatesChange={this.onDatesChange}
              focusedInput={this.state.focusedInput}
              onFocusChange={(focusedInput) => this.setState({ focusedInput })}
              isOutsideRange={(day) => {
                if (this.state.startDate && this.state.endDate) {
                  return true;
                } else if (this.state.receiptStartDate) {
                  return moment(day).day() !== 6;
                } else {
                  return moment(day).day() !== 0;
                }
              }}
            />
          </div>
          <div className="col-md-2 mt-5">
            <button
              onClick={() =>
                this.props.onFilter({
                  filters: this.state.filters,
                  startDate: this.state.startDate,
                  endDate: this.state.endDate,
                })
              }
              className="col-md-5 btn btn-primary btn-filter">
              <span>
                <i className="fa fa-filter"></i>&nbsp; Filter
              </span>
            </button>
            <button
              className="col-md-6 offset-1 btn btn-primary btn-filter btn-filter-reset"
              onClick={this.reset}>
              <span>
                <i className="fa fa-repeat"></i>&nbsp; Reset
              </span>
            </button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    drops: store.filtersReducer.drops,
    selectedDrops: store.filtersReducer.selectedDrops,
    hierarchy1: store.filtersReducer.hierarchy1,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    hierarchy2: store.filtersReducer.hierarchy2,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    hierarchy3: store.filtersReducer.hierarchy3,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    hierarchy4: store.filtersReducer.hierarchy4,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    hierarchy5: store.filtersReducer.hierarchy5,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    hierarchy6: store.filtersReducer.hierarchy6,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    hierarchy7: store.filtersReducer.hierarchy7,
    selectedHierarchy7: store.filtersReducer.selectedHierarchy7,
    startDate: store.filtersReducer.startDate,
    endDate: store.filtersReducer.endDate,
    weeks: store.filtersReducer.weeks,
  };
};
const mapDispatchToProps = {
  getDrops: FilterActions.getDrops,
  getHierarchyLevel1: FilterActions.getHierarchyLevel1,
  getHierarchyLevel2: FilterActions.getHierarchyLevel2,
  getHierarchyLevel3: FilterActions.getHierarchyLevel3,
  getHierarchyLevel4: FilterActions.getHierarchyLevel4,
  getHierarchyLevel5: FilterActions.getHierarchyLevel5,
  getHierarchyLevel6: FilterActions.getHierarchyLevel6,
  getHierarchyLevel7: FilterActions.getHierarchyLevel7,
  setSelectedDrops: FilterActions.setSelectedDrops,
  setSelectedHierarchy1: FilterActions.setSelectedHierarchy1,
  setSelectedHierarchy2: FilterActions.setSelectedHierarchy2,
  setSelectedHierarchy3: FilterActions.setSelectedHierarchy3,
  setSelectedHierarchy4: FilterActions.setSelectedHierarchy4,
  setSelectedHierarchy5: FilterActions.setSelectedHierarchy5,
  setSelectedHierarchy6: FilterActions.setSelectedHierarchy6,
  setSelectedHierarchy7: FilterActions.setSelectedHierarchy7,
  setDateRange: FilterActions.setDateRange,
  resetFilters: FilterActions.resetFilters,
};
export default connect(mapStateToProps, mapDispatchToProps)(Filters);
