import React, { Component } from 'react';
import styled from 'styled-components';
import cloneDeep from 'clone-deep';
import InputWithPercentageTag from './InputWithPercentageTag';
import _ from 'lodash';
import Select from '../../services/select';

class ComparisonTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      selectedRow: [],
      editedRows: {},
      selectedSku: [],
      rowSpan: '1',
    };
  }
  // componentWillReceiveProps(props) {
  //   this.setState({
  //     data: this.props.data,
  //   });
  // }
  // componentDidMount() {
  //   this.setState({
  //     data: this.props.data,
  //   });
  // }
  onCadenceValue(event, col, row, rowNumber) {
    let data = cloneDeep(this.props.data);
    row[col] = event.target.value;
    data[rowNumber] = row;
    this.setState({
      data: data,
    });
    this.props.onComparisonInputChange(data, this.props.index);
  }
  getHeader() {
    if (this.state.data && this.state.data.length) {
      let headers = Object.keys(this.state.data[0]);
      if (this.props.cadenceConstraintsVal === 'overall') {
        //overall case
        headers = _.filter(Object.keys(this.state.data[0]), (o) => o !== 'bins');
        headers.splice(1, 0, 'bins'); //add bins col at first
        headers.unshift('');
      }
      if (this.props.cadenceConstraintsVal === 'sku') {
        //sku case
        headers = _.filter(Object.keys(this.state.data[0]), (o) => o !== 'SKU_Id');
        headers.splice(0, 0, 'SKU ID'); //add sku_id col at first
        headers.splice(1, 0, '');
      }
      return headers.map((header, index) => {
        return <th key={index}>{header ? header[0].toUpperCase() + header.slice(1) : ''}</th>;
      });
    }
  }
  onCheckboxClick = (e, col, row, index) => {
    let { cadenceConstraintsVal } = this.props;
    let selectedRows = cloneDeep(this.state.selectedRow);
    if (e.target.checked) {
      let selectedItems =
        cadenceConstraintsVal === 'sku'
          ? [row]
          : _.filter(this.props.data, { scenarios: row.scenarios }); //change it to this.state.data if only filterd elements need to be sent to draft
      selectedRows.push(...selectedItems);
    } else {
      selectedRows =
        cadenceConstraintsVal === 'sku'
          ? _.filter(selectedRows, (o) => o.SKU_Id !== row.SKU_Id)
          : _.filter(selectedRows, (o) => o.scenarios !== row.scenarios);
    }
    this.setState({
      selectedRow: selectedRows,
    });
    // let comparisonRowVal = row;
    this.props.comparisonCheckbox(selectedRows, index);
  };
  // getInputWithPercentageTag(col, row, i, rowNumber, index) {
  //   return (
  //     <div className="col" key={i + 1} style={{ width: '140px' }}>
  //       <div className="input-group" style={{ width: '90%' }}>
  //         <input
  //           type="text"
  //           onChange={(event) => this.onCadenceValue(event, col, row, rowNumber, index, i)}
  //           // onChange={(e) => this.onComparisonInputChange(e, col, row, rowNumber, index, i)}
  //           className="form-control border-style"
  //           value={row[col]}
  //           disabled={row.scenarios === 'Optimal' ? true : false}
  //         />
  //         <div className="input-group-append">
  //           <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
  //             <i className="fa fa-percent" />
  //           </button>
  //         </div>
  //       </div>
  //     </div>
  //   );
  // }
  changedRow = (obj) => {
    let data = this.state.data;
    data.splice(obj.rowNumber, 1, obj.row);
    // this.setState({
    //   data: data,
    // });
  };

  componentDidUpdate(preprops) {
    if (this.props.data !== preprops.data) {
      let newData =
        this.props.cadenceConstraintsVal === 'sku'
          ? _.sortBy(this.props.data, ['SKU_Id'])
          : this.props.data;
      this.setState({
        data: newData,
        rowSpan: this.props.rowSpan,
        selectedSku: [],
      });
    }
    if (this.props.rowSpan !== preprops.rowSpan) {
      this.setState({
        rowSpan: this.props.rowSpan,
      });
    }
  }

  componentDidMount(preprops) {
    // if (this.props.data !== preprops.data) {
    let newData =
      this.props.cadenceConstraintsVal === 'sku'
        ? _.sortBy(this.props.data, ['SKU_Id'])
        : this.props.data;
    this.setState({
      data: newData,
      rowSpan: this.props.rowSpan,
    });
    // }
  }

  getTags(col, row, rowNumber) {
    if (col.toLowerCase().startsWith('w')) {
      // return this.getInputWithPercentageTag(col, row, rowNumber);
      return (
        <InputWithPercentageTag
          row={row}
          col={col}
          value={row[col]}
          rowNumber={rowNumber}
          data={this.state.data}
          changedRow={(obj) => {
            let data = cloneDeep(this.state.data);
            data[obj.rowNumber][obj.col] = obj.value;
            let editedRow = cloneDeep(this.state.editedRows);
            editedRow[obj.rowNumber] = data[obj.rowNumber];
            this.setState({ data: data, editedRows: editedRow });
            this.props.updatedData(data, this.state.editedRows);
          }}
        />
      );
    } else if (
      col.toLowerCase().startsWith('scenarios') ||
      col.toLowerCase().startsWith('bins') ||
      col.toLowerCase().startsWith('sku_id')
    ) {
      return row[col];
    } else {
      // let find =
      //   this.props.cadenceConstraintsVal === 'sku'
      //     ? _.find(
      //         this.state.selectedRow,
      //         (o) => o.SKU_Id === row.SKU_Id && o.scenarios !== row.scenarios
      //       )
      //     : _.find(this.state.selectedRow, (o) => o.scenarios !== row.scenarios);
      let selectedScenario =
        this.state.selectedRow && this.state.selectedRow[0] && this.state.selectedRow[0].scenarios;
      return (
        <input
          // disabled={_.isEmpty(find) ? false : true}// this is commented bcs user can save only one sceario per plan
          disabled={selectedScenario && selectedScenario !== row.scenarios ? true : false}
          type="checkbox"
          onChange={(e) => this.onCheckboxClick(e, col, row, rowNumber)}
          checked={_.find(this.state.selectedRow, row) ? true : false}
        />
      );
    }
  }

  onComparisonSkuIdChange = (val) => {
    if (_.isEmpty(val)) {
      let newData =
        this.props.cadenceConstraintsVal === 'sku'
          ? _.sortBy(this.props.data, ['SKU_Id'])
          : this.props.data;
      this.setState({
        data: newData,
        selectedSku: val,
        // rowSpan: this.props.rowSpan,
      });
      return;
    }
    let newData = _.filter(this.props.data, (item) => _.find(val, { value: item.SKU_Id }));
    // let rowSpanCalc = _.filter(newData, { scenarios: 'Optimal' });
    this.setState({
      data: newData,
      selectedSku: val,
      // rowSpan: rowSpanCalc.length !== 0 ? rowSpanCalc.length : '1',
    });
  };
  render() {
    console.log(this.props.offerType, 'offerTypeofferType');
    let scenarioArr = [];
    let { cadenceConstraintsVal, skuDropDownOptions } = this.props;
    return (
      <>
        {cadenceConstraintsVal === 'sku' && (
          <div className="row skuLabelStyle comparison-sku-dropdown pt-1">
            <label className="skuLabel">SKU</label>
            <div className="col-4">
              <Select
                name="skuId"
                isMulti
                value={this.state.selectedSku}
                options={skuDropDownOptions}
                onChange={this.onComparisonSkuIdChange}
              />
            </div>
          </div>
        )}
        <div className="cadencetable">
          <div
            className={
              this.state.data && this.state.data.length >= 10
                ? 'createCadenceTableComparison'
                : 'createCadenceTable'
            }>
            <Styles>
              <tbody>
                <tr>{this.getHeader()}</tr>
                {this.state.data &&
                  this.state.data.length > 0 &&
                  this.state.data.map((row, rowNumber) => {
                    let rows = Object.keys(row);
                    if (cadenceConstraintsVal === 'overall') {
                      //overall case
                      rows = _.filter(Object.keys(row), (o) => o !== 'bins');
                      rows.splice(1, 0, 'bins'); //add bins col at first
                      rows.unshift('');
                    }
                    if (cadenceConstraintsVal === 'sku') {
                      //sku case
                      rows = _.filter(Object.keys(row), (o) => o !== 'SKU_Id');
                      rows.splice(0, 0, 'SKU_Id'); //add sku_id col at first
                      rows.splice(1, 0, '');
                    }

                    return (
                      <tr>
                        {rows.map((col, index) => {
                          if (
                            (cadenceConstraintsVal === 'overall' &&
                              col === 'scenarios' &&
                              scenarioArr.includes(row[col])) ||
                            (cadenceConstraintsVal === 'overall' &&
                              col === '' &&
                              scenarioArr.includes(row.scenarios)) ||
                            (cadenceConstraintsVal === 'sku' &&
                              col === 'SKU_Id' &&
                              scenarioArr.includes(row[col]))
                          )
                            return;
                          if (
                            (cadenceConstraintsVal === 'overall' &&
                              col === 'scenarios' &&
                              !scenarioArr.includes(row[col])) ||
                            (cadenceConstraintsVal === 'sku' &&
                              col === 'SKU_Id' &&
                              !scenarioArr.includes(row[col]))
                          )
                            scenarioArr.push(row[col]);

                          return (
                            <td
                              rowSpan={
                                (cadenceConstraintsVal === 'overall' &&
                                  (col === 'scenarios' || col === '')) ||
                                (cadenceConstraintsVal === 'sku' && col === 'SKU_Id')
                                  ? `${this.state.rowSpan}`
                                  : '1'
                              }>
                              {this.getTags(col, row, rowNumber)}
                            </td>
                          );
                        })}
                      </tr>
                    );
                  })}
              </tbody>
            </Styles>
          </div>
        </div>
      </>
    );
  }
}

export default ComparisonTable;

const Styles = styled.table`
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;

  td,
  th {
    border: 1px solid #dddddd;
    text-align: center;
    padding: 8px;
  }
`;
