import React, { Component } from 'react';
import { connect } from 'react-redux';
import Filters from '../../services/filters';
import styled from 'styled-components';
import _ from 'lodash';
import LoadingOverlay from '../../utils/Loader';
import moment from 'moment';

class dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  onFilter = () => {
    let req = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1.map((e) => e.replace(/'/g, "''")),
        hierarchy2: this.props.selectedHierarchy2.map((e) => e.replace(/'/g, "''")),
        hierarchy3: this.props.selectedHierarchy3.map((e) => e.replace(/'/g, "''")),
        hierarchy4: this.props.selectedHierarchy4.map((e) => e.replace(/'/g, "''")),
        hierarchy5: this.props.selectedHierarchy5.map((e) => e.replace(/'/g, "''")),
        hierarchy6: this.props.selectedHierarchy6.map((e) => e.replace(/'/g, "''")),
        hierarchy7: this.props.selectedHierarchy7.map((e) => e.replace(/'/g, "''")),
        hierarchy8: this.props.selectedHierarchy8.map((e) => e.replace(/'/g, "''")),
        tiers: this.props.selectedStores.map((e) => e.replace(/'/g, "''")),
      },
      startDate: moment(this.props.startDate).format('YYYY-MM-DD'),
      endDate: moment(this.props.endDate).format('YYYY-MM-DD'),
      week: this.props.weeks,
    };
  };
  render() {
    return (
      <div className="planned-report pl-2 pr-2">
        <LoadingOverlay loader={this.props.overlayLoaderState} spinner>
          <div className="card mb-4">
            <div>
              <h4 className="font-size-14  pad-10 markdown-heading">
                MarkSmart /<span className="font-bold color-blue">Dashboard</span>
              </h4>
            </div>
            <hr
              className="mb-2"
              style={{
                marginTop: '-4px',
              }}
            />
            <div className="mb-4">
              <Filters
                drop
                stores
                dateRange
                hierarchy={[
                  {
                    type: 'hierarchy1',
                    isMandatory: true,
                    isMultiple: false,
                  },
                  {
                    type: 'hierarchy2',
                    isMandatory: true,
                    isMultiple: true,
                  },
                  {
                    type: 'hierarchy3',
                    isMandatory: true,
                    isMultiple: true,
                  },
                  {
                    type: 'hierarchy4',
                    isMandatory: true,
                    isMultiple: true,
                  },
                  {
                    type: 'hierarchy5',
                    isMandatory: true,
                    isMultiple: true,
                  },
                  {
                    type: 'hierarchy6',
                    isMandatory: true,
                    isMultiple: true,
                  },
                  {
                    type: 'hierarchy7',
                    isMandatory: true,
                    isMultiple: true,
                  },
                  {
                    type: 'hierarchy8',
                    isMandatory: true,
                    isMultiple: true,
                  },
                ]}
                onFilter={this.onFilter}
                onReset={this.props.onReset}
                // allowInactiveProducts={true} // no need for reporting now
              />
            </div>
          </div>
        </LoadingOverlay>
      </div>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    overlayLoaderState: store.loaderReducer.overlayLoaderState,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    selectedHierarchy7: store.filtersReducer.selectedHierarchy7,
    selectedHierarchy8: store.filtersReducer.selectedHierarchy8,
    selectedStores: store.filtersReducer.selectedStores,
    startDate: store.filtersReducer.startDate,
    endDate: store.filtersReducer.endDate,
    weeks: store.filtersReducer.weeks,
  };
};
const mapActionsToProps = {};

export default connect(mapStateToProps, mapActionsToProps)(dashboard);
const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;
