import React, { Component } from 'react';
import { withRouter, Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { logoutUser } from '../../actions/auth';
import { clearCurrentChartdata } from '../../actions/chart';
import { someAction } from '../../actions/auth';
import './sideBarStyle.css';

const isLocalNode = function (node, target) {
  // In Internet Explorer (IE), Node.contains() only works on Element nodes,
  // not Text nodes. As such, let's travel up to the nearest Element node.
  // --
  // NOTE: This doesn't necessarily apply to this demo, which only reacts to
  // Element node interactions. However, I'm adding it here as a mental note.
  do {
    if (node === target) {
      return true;
    }

    target = target.parentNode;
  } while (target);

  return false;
};

const findUpTag = function (el, tag) {
  while (el.parentNode) {
    el = el.parentNode;
    if (el === tag) return el;
  }
  return null;
};

const pageLinks = {
  createPromo: ['/vis/auto-create-promo', '/vis/history-promo', '/vis/create-promo'],
  planningPromo: [
    '/vis/budgetplanning',
    '/vis/Promo',
    '/vis/planned-promo-pipeline',
    '/vis/finalize-promo',
    '/vis/promosettings',
  ],
  analysisPromos: ['/vis/EDA', '/vis/PromoView', '/vis/analyze-promo', '/vis/analyze-campaign'],
  markdownOptimization: [
    '/vis/Markdown/calender',
    '/vis/Markdown/reporting',
    '/vis/Markdown/actual-planned-reports',
    '/vis/Markdown/global-promo',
    '/vis/Markdown/create-markdown',
    '/vis/Markdown/draftStatus',
    '/vis/Markdown/finalize',
  ],
  createPreSeason: [
    '/vis/Markdown/AI-assisted-pre-season',
    '/vis/manual-pre-season',
    '/vis/Markdown/history?preseason=1',
  ],
  createInSeason: [
    '/vis/Markdown/AI-assisted-In-season',
    '/vis/manual-inseason',
    '/vis/Markdown/history?preseason=0',
  ],
  questionsPromo: ['/vis/QuIQ'],
  pricing: [
    '/vis/pricing/pricing-revision',
    '/vis/pricing/new-product-pricing',
    '/vis/pricing/recommended-testing',
    '/vis/pricing/pipeline',
    '/vis/pricing/early-warning-system',
  ],
};

class SideBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      class: '',
      display: 'none',
    };
  }

  onHover = () => {
    this.setState({
      class: 'active',
      display: 'inline-block',
    });
  };

  onLeaveHover = () => {
    this.setState({
      class: '',
      display: '',
    });
  };

  handleClickOutside = (event) => {
    if (!findUpTag(event.target, this.wrapperRef)) {
      this.onLeaveHover();
    }
  };

  componentDidMount() {
    document.addEventListener('mousedown', this.handleClickOutside);
  }

  componentWillUnmount() {
    document.removeEventListener('mousedown', this.handleClickOutside);
  }

  toggleSideBarExpansionHandler = (expand) => {
    if (expand === true || this.state.class !== 'active') {
      this.setState({
        class: 'active',
        display: 'inline-block',
      });
      return;
    }
    if (this.state.class === 'active') {
      this.setState({
        class: '',
        display: '',
      });
    }
  };

  onLogoutClick() {
    this.props.history.push('/login');
  }

  handleResize = (event) => {
    setTimeout(() => {
      if (this.wrapperRef && !isLocalNode(this.wrapperRef, event.target)) {
        this.toggleSideBarExpansionHandler();
      }
    }, 300);
  };

  /**
   * Set the wrapper ref
   */
  setWrapperRef = (node) => {
    this.wrapperRef = node;
  };

  redirectToCreatePromo() {
    document.location.href = pageLinks.createPromo[2];
  }
  render() {
    let pathname =
      this.props.location && this.props.location.pathname
        ? this.props.location.pathname
        : document.location.pathname;

    return (
      <nav id="sidebar" className={this.state.class} ref={this.setWrapperRef}>
        <div className="sidebar-toggle-arrow">
          {this.state.class === 'active' ? (
            <i
              className="fa fa-angle-double-left sidebar-toggle-arrow-icon sidebar-toggle-arrow-left-icon"
              aria-hidden="true"
              onClick={this.toggleSideBarExpansionHandler}
            />
          ) : (
            <i
              className="fa fa-angle-double-right sidebar-toggle-arrow-icon sidebar-toggle-arrow-right-icon"
              aria-hidden="true"
              onClick={this.toggleSideBarExpansionHandler}
            />
          )}
        </div>
        <div className="sidebar-header" />

        <ul className="list-unstyled components">
          {/* markdown slider code */}

          <li className={pageLinks.markdownOptimization.includes(pathname) ? 'active' : ''}>
            <Link
              to="#"
              onClick={() => this.toggleSideBarExpansionHandler(true)}
              className={pageLinks.markdownOptimization.includes(pathname) ? 'active' : 'collapsed'}
              data-toggle="collapse"
              data-target="#markdownOptimizationOptions">
              <img
                className="imgIcons"
                src={require('./markdown.svg')}
                alt="Markdown Optimization"
              />
              <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                Markdown Optimization
              </span>
              {this.state.display === 'inline-block' ? (
                <div className="sidebar-accordion-icon icon-stylingArrow ml-2" aria-hidden="true" />
              ) : (
                ''
              )}
            </Link>
            {this.state.display === 'inline-block' ? (
              <ul
                className={`sub-ul  
                ${pageLinks.markdownOptimization.includes(pathname) ? 'show' : 'collapse'}
              `}
                id="markdownOptimizationOptions">
                <li
                  onClick={() => this.toggleSideBarExpansionHandler()}
                  className={pathname === pageLinks.markdownOptimization[1] ? 'active' : ''}>
                  <Link to={pageLinks.markdownOptimization[1]}>
                    <i className="fa fa-circle icon-stylingDot" />
                    <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                      Reporting Section
                    </span>
                  </Link>
                </li>
                <li
                  onClick={() => this.toggleSideBarExpansionHandler()}
                  className={pathname === pageLinks.markdownOptimization[2] ? 'active' : ''}>
                  <Link to={pageLinks.markdownOptimization[2]}>
                    <i className="fa fa-circle icon-stylingDot" />
                    <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                      Actual vs Planned Reports
                    </span>
                  </Link>
                </li>
                <li
                  onClick={() => this.toggleSideBarExpansionHandler()}
                  className={pathname === pageLinks.markdownOptimization[0] ? 'active' : ''}>
                  <Link to={pageLinks.markdownOptimization[0]}>
                    <i className="fa fa-circle icon-stylingDot" />
                    <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                      Calendar
                    </span>
                  </Link>
                </li>

                {/* code for global promos */}
                <li
                  onClick={() => this.toggleSideBarExpansionHandler()}
                  className={pathname === pageLinks.markdownOptimization[3] ? 'active' : ''}>
                  <Link to={pageLinks.markdownOptimization[3]}>
                    <i className="fa fa-circle icon-stylingDot" />
                    <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                      Global promos
                    </span>
                  </Link>
                </li>

                <li
                  onClick={() => this.toggleSideBarExpansionHandler()}
                  className={pathname === pageLinks.markdownOptimization[4] ? 'active' : ''}>
                  <Link to={pageLinks.markdownOptimization[4]}>
                    <i className="fa fa-circle icon-stylingDot" />
                    <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                      Create Markdown
                    </span>
                  </Link>
                </li>
                <li
                  onClick={() => this.toggleSideBarExpansionHandler()}
                  className={pathname === pageLinks.markdownOptimization[5] ? 'active' : ''}>
                  <Link to={pageLinks.markdownOptimization[5]}>
                    <i className="fa fa-circle icon-stylingDot" />
                    <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                      Draft/Status
                    </span>
                  </Link>
                </li>
                <li
                  onClick={() => this.toggleSideBarExpansionHandler()}
                  className={pathname === pageLinks.markdownOptimization[6] ? 'active' : ''}>
                  <Link to={pageLinks.markdownOptimization[6]}>
                    <i className="fa fa-circle icon-stylingDot" />
                    <span className="hidetext fontstyling" style={{ display: this.state.display }}>
                      Finalize Markdown
                    </span>
                  </Link>
                </li>
              </ul>
            ) : (
              ''
            )}
          </li>
          {/* end markdown slider code */}

          {/* Pricing nav links */}
        </ul>
      </nav>
    );
  }
}

SideBar.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  authReducer: PropTypes.object.isRequired,
};

const mapStateToProps = (state, ownProps) => ({
  authReducer: state.authReducer,
});

export default connect(mapStateToProps, {
  logoutUser,
  clearCurrentChartdata,
  someAction,
})(withRouter(SideBar));
