import axios from 'axios';
import { config } from '../utils/config';
import { toast } from 'react-toastify';

const storeConfigActions = {
  fetchStoreGroups: () => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/get-storesgroups/`,
        method: 'GET',
      })
        .then((resp) => {
          console.log(resp.data.data, 'storesgroups');
          dispatch({
            type: 'SET_STORE_GROUPS',
            payload: resp.data.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  getStoreGroupTableData: (reqObj) => {
    return async (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      let res = await axios({
        url: `${config.createMarkdownURL}/get-stores/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          console.log(resp.data.data, reqObj, 'get-stores');
          if (reqObj) {
            dispatch({
              type: 'CURRENT_STORE_GROUP_TABLE_DATA',
              payload: resp.data.data,
            });
          } else {
            dispatch({
              type: 'SET_STORE_GROUP_TABLE_DATA',
              payload: resp.data.data,
            });
          }
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return resp && resp.data && resp.data.data ? resp.data.data : [];
        })
        .catch((err) => {
          console.log(err);
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return [];
        });
      return res;
    };
  },
  createStoreGroup: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/get-stores-create/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(resp, 'get-stores-create');
          if (!resp.data.status) {
            toast.error('Something went wrong! Please try again.', {
              autoClose: 15000,
              hideProgressBar: true,
            });
            return false;
          } else {
            toast.success('Store Group Created Successfully', {
              autoClose: 15000,
              hideProgressBar: true,
            });
            return true;
          }
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err);
          return false;
        });
    };
  },
  editStoreGroup: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/get-stores-edit/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          console.log(resp, 'stpreresp');
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          if (!resp.data.status) {
            toast.error('Something went wrong! Please try again.', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          } else {
            toast.success('Store Group Updated Successfully', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          console.log(err);
        });
    };
  },
};

export default storeConfigActions;
