import React, { Component } from 'react';
import Tippy from 'react-tippy';
import Select from 'react-select';
import AnalysisTable from '../MarkdownAnalysis/AnalysisTable';
import moment from 'moment';
import BarChart from '../support/Charts/Barchart';
import './index.css';

const options2 = {
  tooltips: {
    callbacks: {
      label: function (tooltipItem, data) {
        let label = data.labels[tooltipItem.index];
        let value = data.datasets[tooltipItem.datasetIndex].data[tooltipItem.index];
        console.log(label, 'label');
        console.log(value, 'vall');
        let labelTip = 'Week' + ':' + label;
        let valueTip = 'Margin%' + ':' + value;
        return [labelTip, valueTip];
      },
    },
  },
  plugins: {
    datalabels: {
      display: false,
    },
  },
  legend: {
    onHover: function (e, target) {
      console.log('anu');
    },
    display: false,
    position: 'bottom',
    labels: {
      boxWidth: 12,
    },
    fontFamily: 'Helvetica',
    fontSize: '12px',
  },
  maintainAspectRatio: false,
  scales: {
    yAxes: [
      {
        gridLines: {
          display: true,
        },
        ticks: {
          fontSize: 13,
        },
        scaleLabel: {
          show: true,
          display: true,
          fontSize: 14,
          fontStyle: 'bold',
          fontColor: '#000',
          labelString: 'Margin %',
        },
      },
    ],
    xAxes: [
      {
        id: 'x-axis-0',
        categoryPercentage: 1.0,
        barPercentage: 1.0,
        gridLines: {
          display: false,
        },
        ticks: {
          autoSkip: true,
          autoSkipPadding: 40,
          maxRotation: 0,
        },
        scaleLabel: {
          display: true,
          labelString: 'Time (week)',
          fontWeight: 900,
          fontStyle: 'bold',
        },
      },
    ],
  },
};
const options = {
  tooltips: {
    callbacks: {
      label: function (tooltipItem, data) {
        let label = data.labels[tooltipItem.index];
        let value = data.datasets[tooltipItem.datasetIndex].data[tooltipItem.index];
        console.log(label, 'label');
        console.log(value, 'vall');
        let labelTip = 'Week' + ':' + label;
        let valueTip = 'ST%' + ':' + value;
        return [labelTip, valueTip];
      },
    },
  },
  plugins: {
    datalabels: {
      display: false,
    },
  },
  legend: {
    display: false,
    position: 'bottom',
    labels: {
      boxWidth: 12,
    },
    fontFamily: 'Helvetica',
    fontSize: '12px',
  },
  maintainAspectRatio: false,
  scales: {
    yAxes: [
      {
        gridLines: {
          display: true,
        },
        ticks: {
          fontSize: 13,
        },
        scaleLabel: {
          show: true,
          display: true,
          fontSize: 14,
          fontStyle: 'bold',
          fontColor: '#000',
          labelString: 'Sell Through %',
        },
      },
    ],
    xAxes: [
      {
        id: 'x-axis-0',
        categoryPercentage: 1.0,
        barPercentage: 1.0,
        gridLines: {
          display: false,
        },
        ticks: {
          autoSkip: true,
          autoSkipPadding: 40,
          maxRotation: 0,
        },
        scaleLabel: {
          display: true,
          labelString: 'Time (week)',
          fontWeight: 900,
          fontStyle: 'bold',
        },
      },
    ],
  },
};
class MarkdownAnalysis extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    const borderColors = [
      '#44677B',
      '#6F9FC3',
      '#83BE8C',
      '#88ad5f',
      '#b06d58',
      '#86588a',
      '#17a69c',
      '#b0a819',
      '#d97c30',
      '#5d5b85',
    ];
    const backgroundColors = [
      'rgba(68, 103, 123, 0.3)',
      'rgba(166, 206, 227, 0.3)',
      'rgba(131, 190, 140, 0.3)',
      'rgba(136, 173, 95, 0.3)',
      'rgba(176, 109, 88, 0.3)',
      'rgba(134, 88, 138, 0.3)',
      'rgba(23, 166, 156, 0.3)',
      'rgba(89, 86, 25, 0.3)',
      'rgba(217, 124, 48, 0.3)',
      'rgba(93, 91, 133, 0.3)',
    ];
    let uniqueWeek = {};
    this.props.markdownAnalysisGraphData.weekly_markdown.forEach((value, i) => {
      if (!uniqueWeek.hasOwnProperty(value)) {
        uniqueWeek[value] = { backgroundColor: backgroundColors.pop() };
        uniqueWeek[value].borderColor = borderColors.pop();
      }
    });
    let legendPercentages = Object.keys(uniqueWeek);
    const labels = this.props.markdownAnalysisGraphData.weekNum.map((date) => {
      if (this.props.level === 'week') {
        return date;
      } else {
        return moment(date).format('MMM DD');
      }
    });
    let sellThroughData = {
      labels: labels,
      datasets: [
        {
          data: this.props.markdownAnalysisGraphData.sellThrough,
          borderColor: this.props.markdownAnalysisGraphData.weekly_markdown.map(
            (m) => uniqueWeek[m].borderColor
          ),
          backgroundColor: this.props.markdownAnalysisGraphData.weekly_markdown.map(
            (m) => uniqueWeek[m].backgroundColor
          ),
          borderWidth: {
            top: 3,
            right: 0,
            bottom: 0,
            left: 0,
          },
        },
      ],
    };
    let marginData = {
      labels: labels,
      datasets: [
        {
          data: this.props.markdownAnalysisGraphData.margin,
          borderColor: this.props.markdownAnalysisGraphData.weekly_markdown.map(
            (m) => uniqueWeek[m].borderColor
          ),
          backgroundColor: this.props.markdownAnalysisGraphData.weekly_markdown.map(
            (m) => uniqueWeek[m].backgroundColor
          ),
          borderWidth: {
            top: 3,
            right: 0,
            bottom: 0,
            left: 0,
          },
        },
      ],
    };
    let level = this.props.level === 'week' ? 'Week' : 'Month';
    options.scales.xAxes[0].scaleLabel.labelString = level;
    options2.scales.xAxes[0].scaleLabel.labelString = level;
    legendPercentages = Object.keys(uniqueWeek).map((percentage) => `${Number(percentage)}%`);
    return (
      <div>
        <div className="mb-4">
          <div className="row skuLabelStyle">
            <label className="skuLabel">Style/Style Group</label>
            <div className="col-md-2">
              <Select
                placeholder="Style/Style Group"
                value={this.props.selectedSkuId}
                options={this.props.optionSku}
                onChange={this.props.selectedSkuHandler}
              />
            </div>
          </div>

          <div className="row">
            <div className="col-md-8" style={{ marginLeft: '4rem' }}>
              <div className="chartContainermain">
                <label className="sellThroughLabel">Sell Through %</label>
                <div className="chartContainer">
                  <div className="row j-c-c">
                    {legendPercentages.map((percentage, index) => (
                      <div className="col-md-1 d-flex" key={`${Math.random() + index}`}>
                        <p
                          className="colorBox"
                          style={{
                            backgroundColor: Object.keys(uniqueWeek).map(
                              (percentage) => uniqueWeek[percentage].borderColor
                            )[index],
                          }}
                        />
                        <span className="left-space">{percentage}</span>
                      </div>
                    ))}
                  </div>
                </div>
                <BarChart
                  width={'681px'}
                  height={'280px'}
                  data={sellThroughData}
                  options={options}
                />
              </div>
              <div className="chartContainermain">
                <label className="sellThroughLabel">Margin %</label>
                <div className="chartContainer">
                  <div className="row j-c-c">
                    <Tippy
                      placement={'left'}
                      arrow={true}
                      distance={10}
                      content="Don't use the default values. This table spreads the average depth across choices based on LY performance">
                      {legendPercentages.map((percentage, index) => (
                        <div className="col-md-1 d-flex" key={`${Math.random() + index}`}>
                          <p
                            className="colorBox"
                            style={{
                              backgroundColor: Object.keys(uniqueWeek).map(
                                (percentage) => uniqueWeek[percentage].borderColor
                              )[index],
                            }}
                          />
                          <span className="left-space">{percentage}</span>
                        </div>
                      ))}
                    </Tippy>
                  </div>
                </div>
                <BarChart width={'681px'} height={'280px'} data={marginData} options={options2} />
              </div>
            </div>
            <div className="col-md-2" style={{ paddingTop: '2rem', marginLeft: '5em' }}>
              <AnalysisTable tableData={this.props.analysisTable || []} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default MarkdownAnalysis;
