import React, { Component } from 'react';
import { withRouter, Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { logoutUser, someAction } from '../../actions/auth';
import { clearCurrentChartdata } from '../../actions/chart';
import { dataLoadStatusCheck } from '../../actions/dataLoadProcess';
import manuallyCreateMdActions from '../../actions/manuallyCreateMdActions.js';
import fiscalCalendarAction from '../../actions/fiscalCalendarAction';
import './headerStyle.css';

var isApiStillRunning = false;

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showDropdown: false,
    };
  }
  componentDidMount() {
    this.dataLoadStatusCheck();
    this.props.getNotificationList();
    this.props.getFiscalDetails();
  }
  dataLoadStatusCheck = () => {
    this.props.dataLoadStatusCheck().then((res) => {
      isApiStillRunning = false;
    });
  };
  componentDidUpdate(prevProps) {
    if (!isApiStillRunning && this.props.overlayLoaderState && prevProps !== this.props) {
      isApiStillRunning = true;
      this.dataLoadStatusCheck();
    }
  }
  onLogout = (e) => {
    this.props.history.push('/QuIQ');
  };

  onNotificationClick = (eachItem) => {
    let simulationIdArray = this.props.simulationId;
    let notificationIds = simulationIdArray.filter((item) => item !== eachItem);
    this.props.history.push({
      pathname: `/vis/Markdown/create-markdown`,
      state: { fromNotification: true, plan_id: eachItem, isEdit: false, planId: null },
    });

    this.props.setSimulationId(notificationIds);
  };
  showList = (event) => {
    event.preventDefault();

    this.setState({ showDropdown: true }, () => {
      document.addEventListener('click', this.closeList);
    });
  };
  onListIconClick = () => {
    this.props.history.push('/vis/Markdown/create-markdown');
  };

  closeList = (event) => {
    // if (!this.dropdownMenu.contains(event.target)) {
    this.setState({ showDropdown: false }, () => {
      document.removeEventListener('click', this.closeList);
    });
    // }
  };

  render() {
    return (
      <div
        className={this.props.dataLoadStatus ? 'headerContainer batch-process' : 'headerContainer'}>
        {this.props.dataLoadStatus ? (
          <div className="batch-process-notification">
            Data Load process currently underway. Create Markdown will be disabled for time being!
          </div>
        ) : null}
        <nav className="navbar navbar-expand-sm navbar-light bg-light">
          <p className="navbar-brand">
            <span id="titleColor">MarkSmart</span>
          </p>
          <button className="navbar-toggler" data-toggle="collapse" data-target="#navbarMenu">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbar-menu-right">
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <span>
                  <div className="btn HeaderNamebutton">{localStorage.getItem('name')[0]}</div>
                </span>
              </li>
              <li className="nav-item">
                <span>
                  <p className="header-username">Hi {localStorage.getItem('name')}</p>
                </span>
              </li>
              <li
                title="Notifications"
                className="nav-item"
                style={{ color: '#44677b', fontSize: '1.7em' }}>
                <span className="fa fa-layers">
                  <i
                    className="fa fa-bell  notfication-icon"
                    style={{ cursor: 'pointer' }}
                    aria-hidden="true"
                    onClick={this.showList}></i>
                  {this.props.simulationId &&
                  this.props.simulationId.length &&
                  !this.state.showDropdown ? (
                    <span
                      className="fa-layers-top-right"
                      style={{
                        background: 'red',
                        fontSize: '0.6em',
                        color: 'white',
                        borderRadius: '25%',
                        fontWeight: 'bold',
                        float: 'right',
                        textAlign: 'center',
                        padding: '1px',
                      }}>
                      {this.props.simulationId.length}
                    </span>
                  ) : null}
                </span>
              </li>
              <li title="Logout" onClick={this.onLogoutClick} className="nav-item">
                <Link to="/">
                  <i
                    onClick={() => this.props.logoutUser()}
                    className="fa fa-sign-out iconHeader"
                    aria-hidden="true"></i>
                </Link>
              </li>
            </ul>
          </div>
        </nav>
        {this.state.showDropdown && this.props.simulationId && this.props.simulationId.length ? (
          <div
            className="menu"
            // ref={(element) => {
            //   this.dropdownMenu = element;
            //}}
          >
            {this.props.simulationId.map((eachItem) => (
              <ul className="notification-ul">
                {/* <Link to="/vis/Markdown/create-markdown"> */}
                <li
                  className="notification-list"
                  onClick={() => {
                    this.onNotificationClick(eachItem);
                  }}>
                  Generate Cadence for ID:{eachItem}
                </li>
                {/* </Link> */}
              </ul>
            ))}
          </div>
        ) : null}
      </div>
    );
  }
}

Header.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  authReducer: PropTypes.object.isRequired,
  simulationId: PropTypes.object.isRequired,
};

const mapStateToProps = (state, ownProps) => ({
  authReducer: state.authReducer,
  dataLoadStatus: state.dataLoadReducer.dataLoadStatus,
  overlayLoaderState: state.loaderReducer.overlayLoaderState,
  simulationId: state.manuallyCreateMdData.simulationId,
});

export default connect(mapStateToProps, {
  logoutUser,
  setSimulationId: manuallyCreateMdActions.setSimulationId,
  // fetchCadence: manuallyCreateMdActions.fetchCadence,
  getNotificationList: manuallyCreateMdActions.getNotificationList,
  getFiscalDetails: fiscalCalendarAction.getFiscalDetails,
  clearCurrentChartdata,
  someAction,
  dataLoadStatusCheck,
})(withRouter(Header));
