import React, { Component } from 'react';
import DataTable from '../../services/reactTable';
import ColumnSelection from '../columnSelection';
import excludeStoreColdef from './excludeStoreColdef';
import cloneDeep from 'clone-deep';
import styled from 'styled-components';
import Select from '../../utils/reactDropDownSelect';

class ExcludeStoreTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // attributes: ['Product Information', 'Sales', 'Inventory', 'Margin'],
      columnOptions: [],
      selectedLabels: [],
      columns: [],
    };
  }
  limitClearnceHandler = () => {
    let selectedRowId = this.state.selected;
    this.props.excludeStoreValHandler(selectedRowId);
    console.log(selectedRowId, 'selectid');
    this.props.showTablePopup();
  };
  onRowClick = (rowInfo) => {
    // let selectedRow = this.state.selected;
    // if (this.state.selected.includes(rowInfo.index)) {
    //   let findInd = selectedRow.indexOf(rowInfo.index);
    //   selectedRow.splice(findInd, 1);
    // } else {
    //   selectedRow.push(rowInfo.index);
    // }
    // this.setState({
    //   selected: selectedRow,
    // });
  };

  onHandleCheckBox = (e, label, index) => {
    debugger;
    //adding
    if (e.target.checked) {
      if (this.state.selectedLabels.indexOf(label) === -1) {
        let labels = [...this.state.selectedLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selectedLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let columns = this.props.columnHeaders[label];
      let tabelColumns = cloneDeep(this.state.columns);
      columns.forEach((col) => {
        tabelColumns.forEach((tabelCol, index) => {
          if (tabelCol.Header === col) {
            tabelColumns.splice(index, 1);
          }
        });
      });
      this.setState({
        columns: tabelColumns,
      });
      let index = this.state.selectedLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = cloneDeep(this.state.selectedLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selectedLabels: selectedLabel,
          columnOptions: tempArr,
        });
      }
    }
  };
  onHandleSelect = (data) => {
    debugger;
    console.log('data:', data);
    let cols = cloneDeep(this.state.columns);
    console.log(cols, 'exccols');
    let selectedOptions = this.state.selectedOptions;
    data.forEach((da) => {
      let newCols = cols.filter((a) => {
        return a.label == da.Header;
      });
      if (newCols.length == 0) {
        cols.push({
          Header: da.label,
          accessor: da.label,
        });
      }
    });
    console.log(cols, 'cols3');
    this.setState({
      columns: cols,
    });
  };
  componentDidMount() {
    this.setState({
      columns: excludeStoreColdef(),
    });
  }
  render() {
    // let labels = Object.keys(this.props.columnHeaders);
    // labels.splice(labels.indexOf('fixed'), 1);
    // labels.splice(labels.indexOf('default'), 1);
    // console.log(this.props.columnHeaders, labels, 'this.props.columnHeaders');
    return (
      <div className="createMd pl-2 pr-2">
        <div className="card mb-4">
          <ColumnSelection
            onHandleCheckBox={this.onHandleCheckBox}
            columnHeaders={this.props.columnHeaders}
            onHandleSelect={this.onHandleSelect}
            columnOptions={this.state.columnOptions}
            selectedItems={this.state.selectedItems}
          />
          <Styles>
            {/* {labels &&
              labels.length &&
              labels.map((label, i) => {
                return (
                  <div className="custom-control custom-checkbox">
                    <input
                      type="checkbox"
                      className="custom-control-input"
                      id={label === 'status' ? i : label + i}
                      onClick={(e) => this.onChechedHandle(e, label, i)}
                    />
                    <label
                      className="custom-control-label"
                      htmlFor={label === 'status' ? i : label + i}>
                      {label}
                    </label>
                  </div>
                );
              })}
            <label className="ml-4"> Add Metric</label>

            <div className=" ml-2">
              <Select
                multi={true}
                // clearable={true}
                onHandleSelect={this.onHandleSelect}
                options={this.state.columnOptions}
                contentRenderer={this.contentRenderer}
                selectedItems={this.selectedItems}
              />
            </div> */}
          </Styles>
          <div>
            <DataTable
              visibleColumns="onlyRowSelection"
              columns={this.state.columns}
              data={this.props.storeData}
              getRowProps={(rowInfo) => ({
                onClick: () => this.onRowClick(rowInfo),
              })}
            />
          </div>
          <div class="mt-3 mb-3" style={{ display: 'flex', justifyContent: 'center' }}>
            <button
              className="btn btn-primary biconbuttonFilter"
              onClick={this.limitClearnceHandler}>
              Limit Clearance
            </button>
          </div>
        </div>
      </div>
    );
  }
}

const Styles = styled.div`
  padding: 1rem;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;

  .custom-control {
    line-height: 1.5rem;
    margin: 0 1rem;
  }

  .custom-control-label::before {
    top: 0.5rem;
  }
  .custom-control-label::after {
    top: 0.5rem;
  }
  .custom-checkbox label {
    vertical-align: -webkit-baseline-middle;
  }
`;

export default ExcludeStoreTable;
