import React, { Component } from 'react';
import { connect } from 'react-redux';
import Filters from '../../services/filters';
import MarkDownActions from '../../actions/draftStausFinalizeAction';
import Breadcrumbs from '../../services/breadcrumbs';
import Calendar from '../../lib/Calendar';
import LoadingOverlay from '../../utils/Loader';

class PromoCalendar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      filters: [
        {
          type: 'hierarchy1',
          isMandatory: true,
          isMultiple: false,
        },
        {
          type: 'hierarchy2',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy3',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy4',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy5',
          isMandatory: true,
          isMultiple: true,
        },
        {
          type: 'hierarchy6',
          isMandatory: false,
          isMultiple: true,
        },
      ],
    };
  }
  componentDidMount() {
    this.props.getMarkdowns({ filters: {} });
  }
  onCalEventClick = (event) => {
    console.log(event.md_plan);
  };
  onFilter = (selectedFilters) => {
    let { startDate, endDate } = selectedFilters;
    startDate = startDate && startDate.format('YYYY-MM-DD');
    endDate = endDate && endDate.format('YYYY-MM-DD');
    this.props.getMarkdowns({ ...selectedFilters, startDate, endDate });
  };
  render() {
    return (
      <div className="card p-3">
        <Breadcrumbs module="MarkSmart" subModules={['Calendar']} />
        <hr className="hr-style" />
        <Filters drop dateRange hierarchy={this.state.filters} onFilter={this.onFilter} />
        <hr className="hr-style mt-4 mb-4" />
        <LoadingOverlay loader={this.props.overlayLoaderState} spinner>
          <div className="p-3">
            <Calendar onCalEventClick={this.onCalEventClick} eventList={this.props.markdowns} />
          </div>
        </LoadingOverlay>
      </div>
    );
  }
}

const mapStateToProps = (store) => {
  return {
    markdowns: store.markDownReducers.markdowns,
    overlayLoaderState: store.loaderReducer.overlayLoaderState,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    endDate: store.filtersReducer.endDate,
    weeks: store.filtersReducer.weeks,
  };
};
const mapDispatchToProps = {
  getMarkdowns: MarkDownActions.getMarkdowns,
};
export default connect(mapStateToProps, mapDispatchToProps)(PromoCalendar);
