  onHandleSelect = (options) => {
    console.log('option:', options);
    let headers = cloneDeep(this.state.columns);
    console.log(headers, 'exccols');
    // let selectedOptions = this.state.selectedOptions;
    options.forEach((da) => {
      let newCols = headers.filter((a) => {
        return a.accessor === da.value;
      });
      if (newCols.length == 0) {
        headers.push({
          Header: da.label,
          accessor: da.value,
        });
      }
    });
    console.log(headers, 'cols3');
    this.setState({
      columns: headers,
    });
  };
  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let columns = this.props.columnHeaders[label];
      let tabelColumns = cloneDeep(this.state.columns);
      columns.forEach((col) => {
        tabelColumns.forEach((tabelCol, index) => {
          if (tabelCol.Header === col) {
            tabelColumns.splice(index, 1);
          }
        });
      });
      this.setState({
        columns: tabelColumns,
      });
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
