import axios from '../utils/axios';
import { config } from '../utils/config';
import { toast } from 'react-toastify';

const manuallyCreateMdActions = {
  onResetInventoryInfo: () => {
    return (dispatch) => {
      dispatch({
        type: 'RESET_INVENTORY_INFO_DATA',
      });
    };
  },
  onResetEditedInfo: () => {
    return (dispatch) => {
      dispatch({
        type: 'RESET_EDITED_INFO_DATA',
      });
    };
  },
  fetchInventoryInfo: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/getInventoryInfo/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          dispatch({
            type: 'GET_INVENTORY',
            payload: resp.data,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          toast.error(err.message, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
      return resp;
    };
  },
  fetchCadence: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        // url: `${config.createMarkdownURL}/generateCadence/`,
        url: `${config.cadenceUrl}/MarkdownOptimization`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          if (resp.data && resp.data.status !== 'false') {
            let skus = [];
            if (resp.data.skus && resp.data.skus.length) {
              skus = resp.data.skus.map((sku) => {
                return {
                  label: sku,
                  value: sku,
                };
              });
            }
            dispatch({
              type: 'GET_CADENCE_DATA',
              payload: [resp.data.cadenceData],
              csv_names: resp.data.csv_names,
              skus: skus,
              savedData: resp.data.saveData,
            });
            dispatch({
              type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
              payload: resp.data.simulateData,
              isEdit: reqObj.isEdit,
            });
            dispatch({
              type: 'SET_ANALYSIS_TABLE_DATA',
              payload: resp.data.simulateData,
              data: resp.data,
              cadenceType: 'Optimal',
              comparisonData: [],
              isEdit: reqObj.isEdit,
            });
          } else {
            toast.error(resp.data.message, {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          toast.error(err.message, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  fetchSkuAndCsv: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        // url: `${config.createMarkdownURL}/generateCadence/`,
        url: `${config.cadenceUrl}/MarkdownOptimization`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          let skus = [];
          if (resp.data.skus && resp.data.skus.length) {
            skus = resp.data.skus.map((sku) => {
              return {
                label: sku,
                value: sku,
              };
            });
          }
          dispatch({
            type: 'GET_SKUS_AND_CSV',
            csv_names: resp.data.csv_names,
            skus: skus,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        })
        .catch((err) => {
          toast.error(err.message, {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  updateAnalysisData: (req, cadenceType) => {
    return (dispatch) => {
      dispatch({
        type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
        payload: cadenceType === 'Optimal' ? req.simulateData : req,
      });
      // if (this.state.defaultActiveKey === 'Optimal') {
      //   obj.analysisData[key] = this.props.analysisData[this.state.defaultActiveKey]
      //     ? this.props.analysisData[this.state.defaultActiveKey].simulateData
      //     : [];
      // } else {
      //   const { analysisTabledata, detailed, overview } = this.props.analysisData[
      //     this.state.defaultActiveKey
      //   ];
      //   obj.analysisData[key] = { analysisTabledata, detailed, overview };
      // }
      dispatch({
        type: 'SET_ANALYSIS_TABLE_DATA',
        payload: cadenceType === 'Optimal' ? req.simulateData : req,
        data: req,
        cadenceType,
      });
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: false,
        },
      });
    };
  },

  simulate: (reqObj, cadenceType, editData) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.cadenceUrl}/CalculationData`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          let returnStatus = true;
          if (resp.data.status === 'false') {
            toast.error(resp.data.message, {
              autoClose: 15000,
              hideProgressBar: true,
            });
            dispatch({
              type: 'LOADER_STATE',
              payload: {
                status: false,
              },
            });
            returnStatus = false;
          }
          let uniqueSellThroughValue = new Set(resp.data.overview.sellThrough);
          if (
            resp.data &&
            resp.data.overview &&
            uniqueSellThroughValue.size === 1 &&
            uniqueSellThroughValue.has('0.0')
          ) {
            toast.error('Data not found', {
              autoClose: 15000,
              hideProgressBar: true,
            });
          }
          if (reqObj.skus === 'all') {
            dispatch({
              type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
              payload: resp.data,
              isEdit: reqObj.isEdit,
            });
            dispatch({
              type: 'SET_ANALYSIS_TABLE_DATA',
              payload: resp.data,
              data: resp.data,
              cadenceType,
              isEdit: reqObj.isEdit,
            });
          } else {
            dispatch({
              type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA_BY_SKUS',
              payload: resp.data,
              isEdit: reqObj.isEdit,
            });
            dispatch({
              type: 'SET_ANALYSIS_TABLE_DATA_BY_SKUS',
              payload: resp.data,
              data: resp.data,
              cadenceType,
              isEdit: reqObj.isEdit,
            });
          }
          if (editData) {
            dispatch({
              type: 'GET_CADENCE_DATA',
              payload: [reqObj.scenario_data],
              csv_names: reqObj.csv_names,
              skus: editData,
              savedData: resp.data.saveData,
            });
          }
          dispatch({
            type: 'GET_COLUMNHEADERS',
          });
          // dispatch({
          //   type: 'SET_ANALYSIS_TABLE_DATA',
          //   payload: resp.data,
          //   data: resp.data,
          //   cadenceType,
          // });
          // dispatch({
          //   type: 'LOADER_STATE',
          //   payload: {
          //     status: false,
          //   },
          // });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return { status: returnStatus };
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });

          return { status: false };
        });
    };
  },
  savedStatusFalse: (status) => {
    return (dispatch) => {
      dispatch({
        type: 'MARKDOWN_SAVED_STATUS',
        status,
      });
    };
  },
  saveData: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      axios({
        url: `${config.createMarkdownURL}/saveMarkDown/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          toast.success('Data Saved Sucessfully', {
            autoClose: 15000,
            hideProgressBar: true,
          });
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          dispatch({
            type: 'MARKDOWN_SAVED_STATUS',
            status: true,
          });
          // dispatch({
          //   type: 'GET_INVENTORY_INFO',
          //   payload: resp.data,
          // });
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
        });
    };
  },
  fetchEditPlanData: (reqObj) => {
    return (dispatch) => {
      dispatch({
        type: 'LOADER_STATE',
        payload: {
          status: true,
        },
      });
      return axios({
        url: `${config.createMarkdownURL}/markdownDetails/`,
        method: 'POST',
        data: reqObj,
      })
        .then((resp) => {
          if (resp.data && resp.data.status !== 'false') {
            let dataObj = resp.data.data && resp.data.data[0] && resp.data.data[0].request_obj;

            let simulateData = Object.values(dataObj.analysisData);
            let cadenceData = Object.values(dataObj.cadenceData);

            dispatch({
              type: 'GET_EDITPLAN_DETAILS',
              payload: resp.data.data,
            });

            dispatch({
              type: 'GET_CADENCE_DATA',
              payload: cadenceData,
            });

            dispatch({
              type: 'GET_MARKDOWNANALYSIS_GRAPH_DATA',
              payload: simulateData[0],
            });

            dispatch({
              type: 'SET_ANALYSIS_TABLE_DATA',
              payload: simulateData[0],
              cadenceType: 'Optimal',
              comparisonData: [],
            });
          }
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return resp.data.data;
        })
        .catch((err) => {
          dispatch({
            type: 'LOADER_STATE',
            payload: {
              status: false,
            },
          });
          return [];
        });
    };
  },
};
export default manuallyCreateMdActions;
