import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { labels, validations, filterType } from './config';
import Select from '../select';
import './index.css';
import { connect } from 'react-redux';
import DateRangePicker from '../RetailCalendarDatePicker';
import FilterActions from '../../actions/filtersAction';
import { toast } from 'react-toastify';
import _ from 'lodash';

class Filters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      filters: {},
      startDate: null,
      endDate: null,
      hierarchy: [],
      hierarchy1:
        props.retainFilters && props.selectedHierarchy1
          ? props.selectedHierarchy1.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy2:
        props.retainFilters && props.selectedHierarchy2
          ? props.selectedHierarchy2.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy3:
        props.retainFilters && props.selectedHierarchy3
          ? props.selectedHierarchy3.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy4:
        props.retainFilters && props.selectedHierarchy4
          ? props.selectedHierarchy4.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy5:
        props.retainFilters && props.selectedHierarchy5
          ? props.selectedHierarchy5.map((e) => {
              return { value: e, label: e };
            })
          : null,
      // hierarchy6:
      //   props.retainFilters && props.selectedHierarchy6
      //     ? props.selectedHierarchy6.map((e) => {
      //         return { value: e, label: e };
      //       })
      //     : null,
      // hierarchy7:
      //   props.retainFilters && props.selectedHierarchy7
      //     ? props.selectedHierarchy7.map((e) => {
      //         return { value: e, label: e };
      //       })
      //     : null,
      // hierarchy8:
      //   props.retainFilters && props.selectedHierarchy8 && props.location.state
      //     ? props.location.state.styles.map((e) => {
      //         return { value: e, label: e };
      //       })
      //     : props.retainFilters && props.selectedHierarchy8 && this.props.styleGroupEdit
      //     ? props.selectedHierarchy8.map((e) => {
      //         return { value: e, label: e };
      //       })
      //     : null,
      customStyleGroup:
        props.retainFilters && props.selectedCustomStyleGroup
          ? props.selectedCustomStyleGroup.map((e) => {
              return { value: e, label: e };
            })
          : null,
      stores:
        props.retainFilters && props.selectedStores
          ? props.selectedStores.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy7:
        props.retainFilters && props.selectedHierarchy7
          ? props.selectedHierarchy7.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy9:
        props.retainFilters && props.selectedHierarchy10
          ? props.selectedHierarchy10.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy10:
        props.retainFilters && props.selectedHierarchy9
          ? props.selectedHierarchy9.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy11:
        props.retainFilters && props.selectedHierarchy11 && props.location.state
          ? props.location.state.styles.map((e) => {
              return { value: e, label: e };
            })
          : null,
      hierarchy12:
        props.retainFilters && props.selectedHierarchy12
          ? props.selectedHierarchy12.map((e) => {
              return { value: e, label: e };
            })
          : null,
      customStoreGroup:
        props.retainFilters && props.selectedCustomStoreGroup
          ? props.selectedCustomStoreGroup.map((e) => {
              return { value: e, label: e };
            })
          : null,
    };
    // +  !props.retainFilters && props.resetFilters();
    props.getExtraFiltersOptions({
      filters: {},
      filterType: 'TIER',
      allowInactiveProducts: this.props.allowInactiveProducts || false,
    });
    !props.isEdit &&
      props.getHierarchyOptions({
        filters: {},
        hierarchyType: 'HIERARCHY1',
        allowInactiveProducts: this.props.allowInactiveProducts || false,
      });

    if (props.retainFilters && props.location.state) {
      props.setSelectedHierarchy11(props.location.state.styles, true);
      props.setSelectedStores(this.state.stores.map((e) => e.label));
    }
    props.getCustomStores().then((res) => {
      if (
        this.props.isEdit &&
        this.props.editPlanDetails[0] &&
        !_.isEmpty(this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj)
      ) {
        let customStoreGroup =
          this.props.editPlanDetails[0] &&
          this.props.editPlanDetails[0].request_obj &&
          this.props.editPlanDetails[0].request_obj.filters &&
          this.props.editPlanDetails[0].request_obj.filters.customStoreGroup;
        let selectedCustomStore = [];
        _.map(res, (item) => {
          if (customStoreGroup && customStoreGroup.includes(item.value)) {
            selectedCustomStore.push(item);
          }
        });
        this.setCustomStores(selectedCustomStore);
      }
    });

    // props.fetchStyleGroups({
    //   disable_empty_flag: false,
    //   filters: {},
    // });

    // if (props.retainFilters && props.location.state) {
    // props.setSelectedHierarchy7(props.location.state.styles, 'reporting');
    // props.setSelectedStores(this.state.stores.map((e) => e.label));
    // }
    props.setRetainFilters(false);
  }
  componentDidMount() {
    if (this.props.retainFilters && !_.isEmpty(this.props.selectedHierarchy1)) {
      let val = [
        {
          label: this.props.selectedHierarchy1[0],
          value: this.props.selectedHierarchy1[0],
        },
      ];
      this.onHierarchyOneSelection(val);
      return;
    }
    this.setState({ hierarchy: this.props.filtersOptions });
    // this.props.getExtraFilterStore();
  }
  componentDidUpdate(prevProps, prevState) {
    if (this.props.filterReset) {
      this.props.filterResetHandler(false);
      this.reset(); // reset filters from configFilters
    }
    if (
      this.props.fromNotification &&
      _.isEmpty(this.state.hierarchy1) &&
      !_.isEmpty(this.props.cadenceInput) &&
      !_.isEmpty(this.props.cadenceInput.filters)
    ) {
      let filters = this.props.cadenceInput.filters;

      this.props.presetFilters(filters, true); //2nd argument to set a flag to call options api

      this.onHierarchyOneSelection([
        { label: filters.hierarchy1[0], value: filters.hierarchy1[0] },
      ]);
      this.setState({
        // customStoreGroup: _.map(filters.customStoreGroup, (item) => ({ value: item, label: item })),
        stores: _.map(filters.stores, (item) => ({ value: item, label: item })),
        hierarchy1: _.map(filters.hierarchy1, (item) => ({ value: item, label: item })),
        hierarchy2: _.map(filters.hierarchy2, (item) => ({ value: item, label: item })),
        hierarchy3: _.map(filters.hierarchy3, (item) => ({ value: item, label: item })),
        hierarchy4: _.map(filters.hierarchy4, (item) => ({ value: item, label: item })),
        hierarchy5: _.map(filters.hierarchy5, (item) => ({ value: item, label: item })),
        hierarchy7: _.map(filters.hierarchy7, (item) => ({ value: item, label: item })),
        hierarchy9: _.map(filters.hierarchy9, (item) => ({ value: item, label: item })),
        hierarchy10: _.map(filters.hierarchy10, (item) => ({ value: item, label: item })),
        hierarchy11: _.map(filters.hierarchy11, (item) => ({ value: item, label: item })),
        hierarchy12: _.map(filters.hierarchy12, (item) => ({ value: item, label: item })),
        // customStyleGroup: _.map(filters.customStyleGroup, (item) => ({ value: item, label: item })),
      });
      if (!_.isEmpty(this.props.customStoreGroup)) {
        let selectedCustomStore = [];
        _.map(this.props.customStoreGroup, (item) => {
          if (filters.customStoreGroup && filters.customStoreGroup.includes(item.value)) {
            selectedCustomStore.push(item);
          }
        });
        this.setState({
          customStoreGroup: selectedCustomStore,
        });
        this.setCustomStores(selectedCustomStore);
      }
      if (!_.isEmpty(this.props.customStyleGroup)) {
        let selectedCustomStyle = [];
        _.map(this.props.customStyleGroup, (item) => {
          if (
            filters.customStyleGroup &&
            typeof filters.customStyleGroup[0] === 'string' &&
            filters.customStyleGroup.includes(item.value)
          ) {
            selectedCustomStyle.push(item);
            return;
          }
          if (filters.customStyleGroup && filters.customStyleGroup.includes(item.id)) {
            selectedCustomStyle.push(item);
          }
        });
        this.setState({
          customStyleGroup: selectedCustomStyle,
        });
        this.setCustomStyleGroup(selectedCustomStyle);
      }
    }
    if (
      this.props.isEdit &&
      _.isEmpty(this.state.hierarchy1) &&
      this.props.editPlanDetails[0] &&
      !_.isEmpty(this.props.editPlanDetails[0] && this.props.editPlanDetails[0].request_obj)
    ) {
      let {
        hierarchy1,
        hierarchy2,
        hierarchy3,
        hierarchy4,
        hierarchy5,
        tiers,
        hierarchy7,
        hierarchy9,
        hierarchy10,
        hierarchy11,
        hierarchy12,
        // customStyleGroup,
        // customStoreGroup,
      } = this.props.editPlanDetails[0].request_obj.filters;

      let customStyleName =
        this.props.editPlanDetails[0] &&
        this.props.editPlanDetails[0].request_obj &&
        this.props.editPlanDetails[0].request_obj.customStyleName;

      // this.props.presetFilters([
      //   country,
      //   channel,
      //   storeCluster,
      //   customStoreGroup,
      //   store,
      //   hierarchy1,
      //   hierarchy2,
      //   hierarchy3,
      //   hierarchy4,
      //   hierarchy5,
      //   season,
      //   matrix,
      //   exclusionList,
      //   clearance,
      //   customStyleGroup,
      //   styleColor,
      // ]);
      this.props.presetFilters(this.props.editPlanDetails[0].request_obj.filters);
      this.setState({
        hierarchy1: _.map(hierarchy1, (item) => ({ value: item, label: item })),
        hierarchy2: _.map(hierarchy2, (item) => ({ value: item, label: item })),
        hierarchy3: _.map(hierarchy3, (item) => ({ value: item, label: item })),
        hierarchy4: _.map(hierarchy4, (item) => ({ value: item, label: item })),
        hierarchy5: _.map(hierarchy5, (item) => ({ value: item, label: item })),
        stores: _.map(tiers, (item) => ({ value: item, label: item })),
        hierarchy7: _.map(hierarchy7, (item) => ({ value: item, label: item })),
        hierarchy9: _.map(hierarchy9, (item) => ({ value: item, label: item })),
        hierarchy10: _.map(hierarchy10, (item) => ({ value: item, label: item })),
        hierarchy11: _.map(hierarchy11, (item) => ({ value: item, label: item })),
        hierarchy12: _.map(hierarchy12, (item) => ({ value: item, label: item })),
        // customStoreGroup: _.map(customStoreGroup, (item) => ({ value: item, label: item })),
        // customStyleGroup: _.map(customStyleGroup, (item) => ({ value: item, label: item })),
      });
    }
    // if (
    //   this.props.from !== 'styleConfig' &&
    //   this.props.from !== 'styleGroupConfig' &&
    //   !_.isEmpty(this.props.selectedHierarchy8) &&
    //   !_.isEqual(prevProps.selectedHierarchy8, this.props.selectedHierarchy8)
    // ) {
    //   let req = {
    //     filters: {
    //       hierarchy1: this.props.selectedHierarchy1,
    //       hierarchy2: this.props.selectedHierarchy2,
    //       hierarchy3: this.props.selectedHierarchy3,
    //       hierarchy4: this.props.selectedHierarchy4,
    //       hierarchy5: this.props.selectedHierarchy5,
    //       // hierarchy6: this.props.selectedHierarchy6,
    //       // hierarchy7: this.props.selectedHierarchy7,
    //       // hierarchy8: this.props.selectedHierarchy8,
    //     },
    //     disable_empty_flag:
    //       this.props.disable_empty_flag && !this.props.isEdit
    //         ? this.props.disable_empty_flag
    //         : false,
    //   };
    //   this.props.fetchStyleGroups(req);
    // }
  }
  onHierarchyOneSelection = (val) => {
    let hierarchyClone = _.cloneDeep(this.props.filtersOptions);
    let valiadtion = validations[val[0].label];
    let findIndex = (index, key) =>
      _.findIndex(hierarchyClone, {
        type: `${valiadtion[key][index]}`,
      }); //finding index num for swap case from validations
    if (valiadtion && !_.isEmpty(valiadtion.swap)) {
      //swaping filters
      let a = findIndex(0, 'swap'),
        b = findIndex(1, 'swap');
      let temp = hierarchyClone[a];

      hierarchyClone[a] = hierarchyClone[b];
      hierarchyClone[b] = temp;
    }
    if (valiadtion && !_.isEmpty(valiadtion.remove)) {
      //remove filters case
      _.map(valiadtion.remove, (item, i) => {
        _.remove(hierarchyClone, (itm, index) => index === findIndex(i, 'remove'));
      });
    }
    this.setState({ hierarchy: hierarchyClone });
    return hierarchyClone;
  };

  setSelectionHandler = async (val, actionName, name) => {
    if (name === 'customStoreGroup') {
      await this.props.setSelectedCustomStoreGroup(val.map((v) => v.value));
      return;
    }
    if (name === 'customStyleGroup') {
      this.props.setSelectedCustomGroup(
        val.map((v) => v.value),
        'name'
      );
      await this.props.setSelectedCustomGroup(
        val.map((v) => v.id),
        'id'
      );
      return;
    }
    await this.props[actionName](val.map((e) => e.label));
    return;
  };
  getHierarchyOptions = async (val, filterIndex, currFilterName) => {
    !Array.isArray(val) && (val = [val]);
    let hierarchy = _.cloneDeep(this.props.filtersOptions);

    let nameCapitalized =
      currFilterName && currFilterName.charAt(0).toUpperCase() + currFilterName.slice(1); //capitalizing the name to match action/reducer name
    let resetHierachies = {};
    _.map(hierarchy, (item, index) => {
      if (index > filterIndex) {
        //&& index < 5) {
        resetHierachies[item.type] = null; //all fiters value, which comes after selected filter becomes null(reset)
      }
    });
    this.setState({ [currFilterName]: val, ...resetHierachies }, async () => {
      let actionName = `setSelected${nameCapitalized}`;
      await this.props[actionName](val.map((e) => e.label)); //handles setSelected action calls

      let req = {};
      _.map(hierarchy, (item, index) => {
        if (index <= filterIndex) {
          let nameCapitalized = item.type && item.type.charAt(0).toUpperCase() + item.type.slice(1); //capitalizing the name to match action/reducer name
          // if (_.isEmpty(this.props[`selected${nameCapitalized}`])) return; //uncomment if u want empty filters removed

          req[item.type] = this.props[`selected${nameCapitalized}`]; //this goes as filters req obj in api calls
        }
      });
      this.setState({ filters: { ...req } });
    });
  };
  setDrop = (val) => {
    let req = {};
    let selItems = val.map((item) => item.label);
    req['drop'] = selItems;
    this.setState({ filters: { ...req }, drops: val }, () => {
      this.props.setSelectedDrops(val.map((v) => v.value));
    });
  };
  actionCallHandler = (func, isHierarchyNext, nextFilterName, req) => {
    console.log('filterName console actionCallHandler', nextFilterName);
    // let typeName = isHierarchyNext
    //   ? nextFilterName.toUpperCase()
    //   : labels[nextFilterName].toUpperCase();
    // if (nextFilterName === 'store') typeName = 'STORE_CODE';
    let typeName = filterType[nextFilterName];
    let reqObj = {
      filters: req,
      allowInactiveProducts: this.props.allowInactiveProducts || false,
    };
    if (isHierarchyNext) reqObj.hierarchyType = typeName;
    if (!isHierarchyNext) reqObj.filterType = typeName;
    if (nextFilterName === 'customStyleGroup') {
      let req = {
        filters: reqObj.filters,
        disable_empty_flag:
          this.props.disable_empty_flag && !this.props.isEdit
            ? this.props.disable_empty_flag
            : false,
      };
      reqObj = req;
      func = `fetchStyleGroups`;
    }
    if (nextFilterName === 'customStoreGroup') {
      reqObj = {};
      func = `getCustomStores`;
    }

    this.props[func](reqObj);
  };
  filterOnFocusHandler = (filterIndex, filterName, isHierarchy) => {
    let options = this.props[filterName];
    if (
      (filterName && options && !options.callApi && !_.isEmpty(this.props[filterName])) ||
      !this.validateFilters(filterIndex)
    )
      //if filter options already exists or if all of the mandatory filters that comes before this filter is not selected , return
      return;
    console.log(filterName, 'filterName');
    // if (filterName === 'stores') {
    //   this.props.getExtraFilterStore({
    //     // filters: {},
    //     filterType: 'TIER',
    //     allowInactiveProducts: this.props.allowInactiveProducts || false,
    //   });
    //   return;
    // }
    let func = isHierarchy ? `getHierarchyOptions` : `getExtraFiltersOptions`;
    let req = {};
    _.map(this.props.filtersOptions, (item, index) => {
      console.log(item, 'itemmmmmmmmmmmmmm');
      if (index < filterIndex) {
        let nameCapitalized = item.type && item.type.charAt(0).toUpperCase() + item.type.slice(1); //capitalizing the name to match action/reducer name
        let selected = this.props[`selected${nameCapitalized}`];
        // if (_.isEmpty(selected)) return; //uncomment if u want empty filters removed

        req[item.type] = selected; //this goes as filters req obj in api calls
      }
    });
    if (filterName && this.props[func]) {
      this.actionCallHandler(func, isHierarchy, filterName, req); //calles next filters action, to get it's options
    }
  };

  setCustomStyleGroup = (val) => {
    // let val = [item];
    let selItems = val.map((item) => item.label);
    // req['stores'] = selItems;
    let newFilters = { ...this.state.filters, customStyleGroup: selItems };
    this.setState({ filters: newFilters, customStyleGroup: val }, () => {
      // this.props.setSelectedCustomGroup(val.map((v) => v.id));
      this.props.setSelectedCustomGroup(
        val.map((v) => v.value),
        'name'
      );
      this.props.setSelectedCustomGroup(
        val.map((v) => v.id),
        'id'
      );
    });
  };
  setCustomStores = (val) => {
    let selItems = val.map((item) => item.label);
    let newFilters = { ...this.state.filters, customStoreGroup: selItems };
    this.setState({ filters: newFilters, customStoreGroup: val }, () => {
      this.props.setSelectedCustomStoreGroup(val.map((v) => v.value));
    });
  };
  reset = () => {
    this.props.onReset && this.props.onReset();
    this.setState(
      {
        drops: null,
        startDate: null,
        endDate: null,
        hierarchy1: null,
        hierarchy2: null,
        hierarchy3: null,
        hierarchy4: null,
        hierarchy5: null,
        stores: null,
        hierarchy7: null,
        hierarchy9: null,
        hierarchy10: null,
        hierarchy11: null,
        hierarchy12: null,
        // customStoreGroup: null,
        // customStyleGroup: null,
      },
      () => {
        if (this.props.from !== 'draft/status') this.props.resetFilters(); ////////// handling reset issues rising bcs of tabs, only reset this component state, not reducer
      }
    );
  };
  onDatesChange = ({ startDate, endDate }) => {
    this.setState(
      {
        startDate,
        endDate,
      },
      () => {
        let weeks = [];
        if (endDate) {
          for (let i = startDate.clone(); i <= endDate; i = i.add(7, 'd')) {
            weeks.push(i.week());
          }
        }

        this.props.setDateRange({
          startDate: startDate && startDate.format('YYYY-MM-DD'),
          endDate: endDate && endDate.format('YYYY-MM-DD'),
          weeks,
        });
      }
    );
  };

  validateFilters = (filterIndex, dateRange) => {
    let { hierarchy } = this.state;
    for (let i = 0; i < filterIndex; i++) {
      let type = hierarchy[i] && hierarchy[i].type;

      let nameCapitalized = type && type.charAt(0).toUpperCase() + type.slice(1); //capitalizing the name to match action/reducer name

      if (
        hierarchy[i] &&
        hierarchy[i].isMandatory &&
        _.isEmpty(this.props[`selected${nameCapitalized}`])
      ) {
        return false;
      }
    }
    if (dateRange && (_.isEmpty(this.state.startDate) || _.isEmpty(this.state.endDate)))
      return false;

    return true;
  };
  onFilterHandler = () => {
    if (!this.validateFilters(this.state.hierarchy.length, this.props.dateRange)) {
      toast.error(`Please select mandatory filters`, {
        autoClose: 15000,
        hideProgressBar: true,
      });
      return;
    }
    this.props.onFilter({
      filters: this.state.filters,
      startDate: this.state.startDate,
      endDate: this.state.endDate,
      customStyleGroup: this.state.customStyleGroup,
    });
  };
  render() {
    console.log(this.props.stores, 'stores');
    return (
      <React.Fragment>
        <div
          className={
            this.props.isEdit
              ? 'row d-flex ml-4 mr-4 edit-markdown-filters'
              : ' d-flex row ml-4 mr-4'
          }>
          {this.state.hierarchy &&
            this.state.hierarchy.map(
              (filter, i) =>
                labels[filter.type] && (
                  <div className="col-md-2 " key={filter.type}>
                    <label className="mb-1 mt-3" style={{ whiteSpace: 'nowrap' }}>
                      {labels[filter.type]}
                      {filter.isMandatory ? <span style={{ color: 'red' }}> * </span> : null}
                    </label>
                    <Select
                      name={filter.type}
                      options={this.props[filter.type]}
                      onFocus={(e) => this.filterOnFocusHandler(i, filter.type, filter.isHierarchy)}
                      onChange={(option) =>
                        this.getHierarchyOptions(
                          option,
                          i,
                          filter.type,
                          filter.isHierarchy,
                          this.state.hierarchy[i + 1] && this.state.hierarchy[i + 1].type,
                          this.state.hierarchy[i + 1] && this.state.hierarchy[i + 1].isHierarchy
                        )
                      }
                      value={this.state[filter.type]}
                      isMulti={filter.isMultiple}
                      isDisabled={
                        (i === 0 && this.props.styleGroupEdit) ||
                        (i === 2 && this.props.storeClusterDisable) ||
                        (i === 3 && this.props.storeClusterDisable) ||
                        (i === 4 && this.props.storeClusterDisable)
                          ? true
                          : false
                      }
                      // isDisabled={!(this.state[`hierarchy${i}`] && this.state[`hierarchy${i}`].length)}
                    />
                  </div>
                )
            )}

          {/* {this.props.customStyleGroups &&
            labels.customStyleGroup &&
            !this.props.hideCustomStyleGroup && (
              <div className="col-md-2 ">
                <label className="mb-1 mt-3">
                  Custom Style Group
                  {this.props.customStyleGrpMandatory ? (
                    <span style={{ color: 'red' }}> * </span>
                  ) : null}
                </label>
                <Select
                  options={this.props.customStyleGroup}
                  onChange={(option) => this.setCustomStyleGroup(option)}
                  value={this.state.customStyleGroup}
                  isMulti
                  // isDisabled={!(this.state[`hierarchy${i}`] && this.state[`hierarchy${i}`].length)}
                />
              </div>
            )}
          {this.props.customStoreGroup && labels.customStoreGroup && !this.props.hideCustomStores && (
            <div className="col-md-2 ">
              <label className="mb-1 mt-3">
                {labels.customStoreGroup}

              </label>
              <Select
                options={this.props.customStoreGroup}
                onChange={(option) => this.setCustomStores(option)}
                value={this.state.customStoreGroup}
                isMulti
                // isDisabled={!(this.state[`hierarchy${i}`] && this.state[`hierarchy${i}`].length)}
              />
            </div>
          )} */}
          {this.props.dateRange && (
            <div className="col-md-3">
              <label className="mb-1 mt-3">
                Date range <span style={{ color: 'red' }}> * </span>
              </label>
              <DateRangePicker
                startDate={this.state.startDate}
                startDateId="startDate_11"
                endDate={this.state.endDate}
                endDateId="endDate_11"
                onDatesChange={this.onDatesChange}
              />
            </div>
          )}
          {!this.props.hidefilterButton && (
            <div
              className={
                !this.props.dateRange && !this.props.hideCustomStores
                  ? 'col-md-2 mt-5 px-0' //'col-md-3 mt-4  ml-auto text-right'
                  : 'col-md-3 mt-5'
              }>
              <button
                disabled={this.props.isEdit}
                onClick={this.onFilterHandler}
                className={
                  !this.props.dateRange && !this.props.hideCustomStores
                    ? 'col-md-5 btn btn-primary btn-filter ml-2'
                    : 'col-md-4 btn btn-primary btn-filter'
                }>
                <span>
                  <i className="fa fa-filter"></i>&nbsp; Filter
                </span>
              </button>
              <button
                disabled={this.props.isEdit || this.props.styleGroupEdit}
                className={
                  !this.props.dateRange && !this.props.hideCustomStores
                    ? ' col-md-5 btn btn-primary btn-filter btn-filter-reset ml-3'
                    : 'col-md-4 offset-1 btn btn-primary btn-filter btn-filter-reset'
                }
                onClick={this.reset}>
                <span>
                  <i className="fa fa-repeat"></i>&nbsp; Reset
                </span>
              </button>
            </div>
          )}
        </div>
      </React.Fragment>
    );
  }
}

