import React, { Component } from 'react';
import { connect } from 'react-redux';
import ConfigComp from './StylesConfigContainers/StyleConfig';
import CreateStyle from './StylesConfigContainers/CreateStyle';
import CreateStyleFamily from './StylesConfigContainers/CreateStyleFamily';
import CreateStyleGroup from './StylesConfigContainers/CreateStyleGroup';

const CONFIG = 'config';
const CREATE_STYLE_GROUP = 'createStyleGroup';
const CREATE_STYLE_FAMILY = 'createStyleFamily';
const CREATE_STYLE = 'createStyle';
const stringLiterals = {
  Configurations: {
    __Style_details: "Style Details",
    __Style_ID: "Style ID",
    __Sub_department: "Sub Department",
    __Style_name: "Style Name",
    __Class: "Class",
    __Department: "Department",
    __Sub_class: "Sub Class",
    __Style_mapping: "Style Mapping",
    __Refence_style: "Refence Style",
    __Replacement_style: "Replacement Style",
    __Create: "Create",
    __Style_family_name: "Style Family Name",
    __All_styles: "All Styles",
    __Current: "Current",
    __No_of_styles: "No. of styles",
    __Style_description: "Style Description",
    __Style_group_name: "Style group name",
    __Store_group: "Store Group",
    __Create_store_group: "Create Store Group",
    __No_of_stores: "No of stores",
    __Edit_store_group: "Edit store group",
    __Right_Click_on_the_table_data_to_export: "Right Click on the table data to export",
    __store_group: "storeGroup",
    __Store_number: "Store Number",
    __Store_name: "Store Name",
    __Chain_description: "Chain Description",
    __Region_description: "Region Description"
  }
};
export class StyleConfiguration extends Component {
  state = {
    componentToRender: CONFIG,
    isEditStyle: false,
  };

  onStyleButtonClickHandlers = (type, isEditStyle = false) => {
    console.log('asdfadsnf');
    switch (type) {
      case 'style':
        this.setState({
          componentToRender: CREATE_STYLE,
          isEditStyle,
        });
        break;
      case 'styleGroup':
        this.setState({
          componentToRender: CREATE_STYLE_GROUP,
          isEditStyle,
        });
        break;
      case 'styleFamily':
        this.setState({
          componentToRender: CREATE_STYLE_FAMILY,
          isEditStyle,
        });
        break;
      default:
        return this.setState({
          componentToRender: CONFIG,
          isEditStyle,
        });
    }
  };

  onCancelButtonHandler = () => {
    this.setState({
      componentToRender: CONFIG,
    });
  };

  onCreateStyleHandler = payload => {
    console.log('asdfaksdfa', payload);
  };

  renderComponent = (compType, translation) => {
    switch (compType) {
      case CONFIG:
        return <ConfigComp onStyleButtonClickHandlers={this.onStyleButtonClickHandlers} />;
      case CREATE_STYLE:
        return (
          <CreateStyle
            onCreateStyleHandler={this.onCreateStyleHandler}
            onCancelButtonHandler={this.onCancelButtonHandler}
            isEditStyle={this.state.isEditStyle}
            translation={translation}
          />
        );
      case CREATE_STYLE_FAMILY:
        return (
          <CreateStyleFamily
            onCreateStyleHandler={this.onCreateStyleHandler}
            onCancelButtonHandler={this.onCancelButtonHandler}
            isEditStyle={this.state.isEditStyle}
            translation={translation}
          />
        );
      case CREATE_STYLE_GROUP:
        return (
          <CreateStyleGroup
            onCreateStyleHandler={this.onCreateStyleHandler}
            onCancelButtonHandler={this.onCancelButtonHandler}
            isEditStyle={this.state.isEditStyle}
            translation={translation}
          />
        );
      default:
        return null;
    }
  };

  render() {

    return this.renderComponent(this.state.componentToRender, stringLiterals)
  }
}

// const mapStateToProps = ({ stringLiterals }) => ({
//   stringLiterals:stringLiterals.Configurations
// });

export default connect(null, null)(StyleConfiguration);
