import React, { Component } from 'react';
import AddSku from '../createMarkdown/addSku';
import InventoryInfo from '../createMarkdown/inventoryInfo';
// import './createMarkdown.css'
import Constraints from '../createMarkdown/constraints';
import CreateCadence from '../createMarkdown/createCadence';
import MarkdownSimulation from '../createMarkdown/markdownSimulation';
import moment from 'moment';
import AgGridTableWithCheckbox from '../AgGridTableWithCheckbox';
import Modal from 'react-awesome-modal';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

class CreateInSeasonMarkdownFromHistorical extends Component {
  constructor(props) {
    super(props);
    this.state = {
      totalInventory: 40,
      currentInventory: 35,
      totalStores: 20,
      selectedStores: 15,
      showSimulationResult: false,
      scenariosList: [],
      selectedSkuCount: 0,
      showSkuModal: false,
      startDate: moment(),
      endDate: moment(),
      category: null,
      subCategory: null,
    };
    this.scenarioCount = 1;
    this.switchSkuModal = this.switchSkuModal.bind(this);
    this.prevYear = this.prevYear.bind(this);
    this.nextYear = this.nextYear.bind(this);
  }

  simulateMarkdown = () => {
    this.setState({ showSimulationResult: true });
  };
  switchSkuModal = () => {
    this.setState((prevState) => ({ showSkuModal: !prevState.showSkuModal }));
  };
  onExcludeStores = () => {
    this.setState({ showSKUListPopup: true });
  };
  addScenario = () => {
    let index = ++this.scenarioCount;
    let scenariosList = [...this.state.scenariosList];
    scenariosList.push(
      <div className="cadence-row row" key={index}>
        <div className="scenario-count col-md-1">{index}</div>
        <div className="col-md-3">
          <div className="input-group" style={{ width: '90%' }}>
            <input type="text" className="form-control border-style" />
            <div className="input-group-append">
              <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
                <i className="fa fa-percent" />
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="input-group" style={{ width: '90%' }}>
            <input type="text" className="form-control border-style" />
            <div className="input-group-append">
              <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
                <i className="fa fa-percent" />
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="input-group" style={{ width: '90%' }}>
            <input type="text" className="form-control border-style" />
            <div className="input-group-append">
              <button className="btn btn-side-percent" type="button" style={{ zIndex: 'unset' }}>
                <i className="fa fa-percent" />
              </button>
            </div>
          </div>
        </div>
        <button className="btn add-cadence-button ml-4">
          <i className="fa fa-plus" />
        </button>
      </div>
    );
    this.setState({ scenariosList });
  };
  prevYear = () => {
    let y = this.state.endDate.subtract(1, 'y');
    this.setState({ endDate: y });
  };
  nextYear = () => {
    let y = this.state.endDate.add(1, 'y');
    this.setState({ endDate: y });
  };
  render() {
    return (
      <React.Fragment>
        <div className="card" style={{ padding: '3px 1px' }}>
          <h4 class="font-size-14 mt-2" style={{ paddingLeft: '4px' }}>
            Markdown Optimization /
            <span class="font-bold color-blue ">Manually Create In-Season MDddds</span>
          </h4>
          <hr className="mt-1" />
          <AddSku
            showSkuSelect={true}
            selectedSkuCount={this.state.selectedSkuCount}
            showSkuModal={this.state.showSkuModal}
            switchSkuModal={this.switchSkuModal}
            startDate={this.state.startDate}
            endDate={this.state.endDate}
            nextYear={this.nextYear}
            prevYear={this.prevYear}
            category={this.state.category}
            subCategory={this.state.subCategory}
          />
          <hr className="mt-4 mb-2 dotted-line" />
          <InventoryInfo
            totalInventory={this.state.totalInventory}
            currentInventory={this.state.currentInventory}
            totalStores={this.state.totalStores}
            selectedStores={this.state.selectedStores}
            onExcludeStores={this.onExcludeStores}
          />
          <hr className="mt-4 mb-2 dotted-line" />
          <Constraints startDate={this.state.startDate} endDate={this.state.endDate} />
          <hr className="mt-4 mb-2 dotted-line" />
          <CreateCadence
            scenariosList={this.state.scenariosList}
            simulateMarkdown={this.simulateMarkdown}
            addScenario={this.addScenario}
          />
        </div>
        {this.state.showSimulationResult && (
          <div className="card mt-4 dotted-line">
            <MarkdownSimulation scenarioCount={this.scenarioCount} />
          </div>
        )}
        <Modal
          visible={this.state.showSKUListPopup}
          effect="fadeInDown"
          onClickAway={() => this.setState({ showSKUListPopup: false })}>
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">
                Select Stores
                <span
                  onClick={() => this.setState({ showSKUListPopup: false })}
                  className="pull-right select-store-span">
                  <i className="fa fa-times" aria-hidden="true" style={{ fontSize: '22px' }} />
                </span>
              </h5>
              <hr />
            </div>

            <div className="ai-pre-grid-table">
              <AgGridTableWithCheckbox />
            </div>
          </div>
        </Modal>
      </React.Fragment>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    // authentication: state.userLoginDetails,
    loader: state.calendar.loader,
    inseasonData: state.inseasonData,
  };
};

export default connect(
  mapStateToProps
  // mapActionToProps
)(withRouter(CreateInSeasonMarkdownFromHistorical));

// export default CreateInSeasonMarkdownFromHistoricals
