import React from 'react';
import {
  toPercentage,
  toPercentageWithDecimalDraft,
  toDollarWithlDraft,
  toUnitDraft,
  toFixedToTwoDraft,
} from '../../../helper/formatter';
import moment from 'moment';

function StatusCellStyling({ value }) {
  switch (value) {
    case 'rejected':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i className="fa fa-ban fa-lg" aria-hidden="true" style={{ color: '#D84039' }}></i>{' '}
          {value} &nbsp; &nbsp;{' '}
          <i
            className="fa fa-commenting fa-lg"
            aria-hidden="true"
            style={{ cursor: 'pointer' }}></i>
        </span>
      );
    case 'draft':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          {/* <i className="fa fa-ban fa-lg" aria-hidden="true" style={{ color: '#D84039' }}></i>{' '} */}
          {value} &nbsp; &nbsp;{' '}
        </span>
      );
    case 'pending':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i
            className="fa fa-exclamation-circle fa-lg"
            style={{ color: '#E0D337' }}
            aria-hidden="true"></i>{' '}
          {value}
        </span>
      );
    case 'approved':
      return (
        <span style={{ textTransform: 'capitalize' }}>
          <i
            className="fa fa-check-circle fa-lg"
            aria-hidden="true"
            style={{ color: '#59C770' }}></i>{' '}
          {value}
        </span>
      );

    default:
      return '-';
  }
}

export default () => {
  const colDef = [
    {
      Header: 'MD Plan',
      accessor: 'md_plan',
      disableGroupBy: true,
      showFilter: true,
      // isGroupBy: false,
      align: 'left',
      enableRowSpan: true,
      // aggregate: 'uniqueCount',
      // Aggregated: ({ value }) => `${value} scenario`,
    },
    {
      Header: 'Scenario',
      accessor: 'scenario',
      disableGroupBy: true,
      showFilter: true,
      enableRowSpan: true,

      // Cell: ({ row, rows }) => (row.original.scenario ? row.original.scenario : null),
    },
    {
      // Build our expander column
      id: 'expander', // Make sure it has an ID
      // Header: ({ getToggleAllRowsExpandedProps, isAllRowsExpanded }) => (
      //   <span {...getToggleAllRowsExpandedProps()}>{isAllRowsExpanded ? 'ðŸ‘‡' : 'ðŸ‘‰'}</span>
      // ),
      Header: 'Date Range',
      width: 100,
      Cell: ({ row }) => {
        // Use the row.canExpand and row.getToggleRowExpandedProps prop getter
        // to build the toggle for expanding a row
        console.log('czseferuuuuuuu', row);

        return row.canExpand ? (
          <span
            {...row.getToggleRowExpandedProps({
              style: {
                // We can even use the row.depth property
                // and paddingLeft to indicate the depth
                // of the row
                paddingLeft: `${row.depth * 2}rem`,
              },
            })}>
            {moment(row.original.start_date).format('YYYY/MM/DD')} -{' '}
            {moment(row.original.end_date).format('YYYY/MM/DD')}&nbsp;&nbsp;&nbsp;
            {row.isExpanded ? (
              <i class="fa fa-angle-down expanded-icon-size color-blue" aria-hidden="true"></i>
            ) : (
              <i class="fa fa-angle-right expanded-icon-size color-blue" aria-hidden="true"></i>
            )}
          </span>
        ) : null;
      },
    },
    {
      Header: 'Time Period',
      accessor: 'week_num',
      disableGroupBy: false,
      isGroupBy: false,

      // aggregate: 'uniqueCount',
      Aggregated: ({ value, row }) => `weeks (${row.subRows.length})`,
    },
    {
      Header: 'Sell through%',
      accessor: 'sell_through',
      disableGroupBy: true,

      Cell: (props) => <div> {toPercentageWithDecimalDraft(props)} </div>,
      Aggregated: (row) => {
        let val = row.row.original.overall_sell_through;
        return <span>{val ? parseFloat(val).toFixed(2) : '-'}</span>;
      },
    },
    {
      Header: 'Markdown %',
      accessor: 'markdown_perc',
      disableGroupBy: true,
      Cell: (props) => <div> {toPercentage(props)} </div>,
      Aggregated: ({ value }) => `-`,
    },

    {
      Header: 'Margin%',
      accessor: 'margin_perc',
      disableGroupBy: true,
      Cell: (props) => <div> {toPercentageWithDecimalDraft(props)} </div>,
      Aggregated: (row) => {
        let val = row.row.original.overall_margin;
        console.log(row.row.original.overall_margin);
        return <span>{val ? Number(val).toFixed(2) : '-'}</span>;
      },
    },
    {
      Header: 'AUM',
      accessor: 'aum',
      disableGroupBy: true,
      Cell: (props) => <div>{toFixedToTwoDraft(props)}</div>,
      Aggregated: (row) => {
        let val = row.row.original.overall_aum;
        return <span>{val ? Number(val).toFixed(2) : '-'}</span>;
      },
    },
    {
      Header: 'AUR',
      Cell: (props) => <div>{toFixedToTwoDraft(props)}</div>,
      accessor: 'aur',
      disableGroupBy: true,
      Aggregated: (row) => {
        let val = row.row.original.overall_aur;
        return <span>{val ? Number(val).toFixed(2) : '-'}</span>;
      },
    },
    {
      Header: 'Planned Units',
      accessor: 'plannedUnits',
      Cell: (props) => <div>{toUnitDraft(props)}</div>,

      disableGroupBy: true,
      Aggregated: (row) => {
        let val = row.row.original.overall_planned_units;
        return <span>{val ? Number(val).toLocaleString('en') : '-'}</span>;
      },
    },
    {
      Header: 'Actual Units',
      accessor: 'actualUnits',
      disableGroupBy: true,
      Cell: (props) => <div>{toUnitDraft(props)}</div>,

      Aggregated: (row) => {
        let val = row.row.original.overall_units;
        return <span>{val ? Number(val).toLocaleString('en') : '-'}</span>;
      },
    },
    {
      Header: 'Revenue',
      accessor: 'revenue$',
      disableGroupBy: true,
      Cell: (props) => <div> {toDollarWithlDraft(props)} </div>,
      Aggregated: (row) => {
        let val = row.row.original.overall_revenue$;
        return <span>{val ? parseInt(val).toLocaleString('en') : '-'}</span>;
      },
    },
    {
      Header: 'Status',
      accessor: 'status',
      disableGroupBy: true,
      showFilter: true,
      Cell: StatusCellStyling,
    },
  ];
  return colDef;
};
