        scaleLabel: {
          show: true,
          display: true,
          fontSize: 14,
          fontStyle: 'bold',
          fontColor: '#000',
          labelString: 'Sell Through %',
        },
      },
    ],
    xAxes: [
      {
        id: 'x-axis-0',
        categoryPercentage: 1.0,
        barPercentage: 1.0,
        gridLines: {
          display: false,
        },
        // stacked: true,
        ticks: {
          autoSkip: true,
          autoSkipPadding: 40,
          maxRotation: 0,
          suggestedMin: 0,
        },
        scaleLabel: {
          display: true,
          labelString: 'Time (week)',
          fontWeight: 900,
          fontStyle: 'bold',
        },
      },
    ],
  },
};
class MarkdownAnalysis extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    const borderColors = [
      '#44677B',
      '#6F9FC3',
      '#83BE8C',
      '#88ad5f',
      '#b06d58',
      '#86588a',
      '#17a69c',
      '#b0a819',
      '#d97c30',
      '#5d5b85',
    ];
