import React, { Component } from 'react';
import { connect } from 'react-redux';
import DraftAction from '../../../actions/draftStausFinalizeAction';
import Filters from '../../../services/filters';
import DataTable from '../../../services/reactTable';
import Modal from '../../../services/modal';
import ColumnSelection from '../../columnSelection';
import StatusListCount from './statusListCount';
import Select from '../../../utils/reactDropDownSelect';
import statusColDef from '../status/coldef';
import styled from 'styled-components';
import _ from 'lodash';
import { cloneDeep } from 'lodash';
import NoData from '../../noTableData';
import moment from 'moment';

class Status extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectStatusValues: [],
      comment: '',
      isComment: false,
      rowDetails: {},
      selectedItems: [],
      columnOptions: [],
      selectedLables: [],
      columns: [],
    };
  }

  onCellClick = (row) => {
    if (row.value === 'rejected') {
      this.setState({
        isComment: true,
        rowDetails: row.row.original,
      });
    }
  };
  setComment = (e) => {
    this.setState({
      comment: e.target.value,
    });
  };

  onCloseComment = () => {
    this.setState({
      isComment: false,
      rowDetails: {},
    });
  };
  onSaveComment = () => {
    let tableData = cloneDeep(this.props.markdowns);
    tableData.forEach((row) => {
      if (
        this.state.rowDetails.md_plan === row['md_plan'] &&
        this.state.rowDetails.scenario === row['scenario']
      ) {
        row.comment = this.state.comment;
      }
    });
    this.props.setStatusData(tableData);
    this.setState({
      isComment: false,
      rowDetails: {},
    });
  };
  componentDidMount() {
    // this.props.getMarkdowns();
    this.setState({
      columns: statusColDef(),
    });
  }
  findStatusColor = (status, color) => {
    switch (status) {
      case 'high':
        return color.high;
      case 'medium':
        return color.medium;
      case 'low':
        return color.low;
      default:
        break;
    }
  };

  bgmCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let bgCol = {
      high: '#F8E8E8',
      medium: '#FCF3D1',
      low: '#E8F8F5',
    };
    return this.findStatusColor(status, bgCol);
  };
  txtCol = (cellInfo) => {
    if (cellInfo.column.id === 'md_status') var status = cellInfo.row.original.md_status;
    let txtCol = {
      high: '#D38485',
      medium: '#F0C53A',
      low: '#66CFBA',
    };
    return this.findStatusColor(status, txtCol);
  };

  onFilter = () => {
    let payload = {
      filters: {
        hierarchy1: this.props.selectedHierarchy1,
        hierarchy2: this.props.selectedHierarchy2,
        hierarchy3: this.props.selectedHierarchy3,
        hierarchy4: this.props.selectedHierarchy4,
        hierarchy5: this.props.selectedHierarchy5,
        hierarchy6: this.props.selectedHierarchy6,
        hierarchy7: this.props.selectedHierarchy7,
        hierarchy8: this.props.selectedHierarchy8,
        tiers: this.props.selectedStores,
      },
      startDate: moment(this.props.startDate).format('YYYY-MM-DD'),
      endDate: moment(this.props.endDate).format('YYYY-MM-DD'),
      //  status: 'pending',
    };
    console.log(payload, 'finalizepayl');
    this.props.getMarkdowns(payload);
  };

  onHandleCheckBox = (e, label, index) => {
    console.log(':huihi');
    //adding
    if (e.target.checked) {
      if (this.state.selectedLables.indexOf(label) === -1) {
        let labels = [...this.state.selectedLables, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selectedLables: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selectedLables.indexOf(label);
      if (index !== -1) {
        let selectedLabel = cloneDeep(this.state.selectedLables);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let options = columns.map((label) => {
            return {
              label: label,
              value: label,
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selectedLables: selectedLabel,
          columnOptions: tempArr,
        });
      }
    }
  };
  onHandleSelect = (selOptions) => {
    console.log('sattusoption:', selOptions);
    let headers = cloneDeep(this.state.columns);
    console.log(headers, 'selheader');
    selOptions.forEach((item) => {
      console.log(item, 'itemitemitemitemd');
      let newTableCols = headers.filter((a) => {
        return a.accessor === item.value;
      });
      console.log(newTableCols, 'neww');
      if (newTableCols.length == 0) {
        headers.push({
          Header: item.label,
          accessor: item.value,
        });
      }
    });
    console.log(headers, 'headeererererer');
    this.setState({
      columns: headers,
    });
  };
  render() {
    // console.log(dummyTableData1, 'StatusReducer');
    return (
      <div className="createMd pl-2 pr-2">
        <div className="card">
          <div>
            <h4 className="font-size-14  pad-10 markdown-heading">
              Markdown Optimization /<span className="font-bold color-blue">Status</span>
            </h4>
          </div>
          <hr
            className="mb-2"
            style={{
              marginTop: '-4px',
            }}
          />
          <div className="filterCol mb-4">
            <Filters
              drop
              stores
              dateRange
              onFilter={this.onFilter}
              hierarchy={[
                {
                  type: 'hierarchy1',
                  isMandatory: true,
                  isMultiple: false,
                },
                {
                  type: 'hierarchy2',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy3',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy4',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy5',
                  isMandatory: true,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy6',
                  isMandatory: false,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy7',
                  isMandatory: false,
                  isMultiple: true,
                },
                {
                  type: 'hierarchy8',
                  isMandatory: false,
                  isMultiple: true,
                },
              ]}
            />
          </div>
        </div>
        {this.props.markdowns && this.props.markdowns.length ? (
          <div className="card pl-3 pr-3 mt-3">
            <ColSelBtn>
              {this.props.getColumnHeaders && Object.keys(this.props.getColumnHeaders).length && (
                <ColumnSelection
                  onHandleCheckBox={this.onHandleCheckBox}
                  columnHeaders={this.props.getColumnHeaders}
                  onHandleSelect={this.onHandleSelect}
                  columnOptions={this.state.columnOptions}
                  selectedItems={this.state.selectedItems}
                  selLabels={this.state.selectedLables}
                />
              )}
              <div style={{ padding: '1rem', textTransform: 'capitalize' }} className="flexRow">
                {this.props.statusCounts && Object.keys(this.props.statusCounts).length ? (
                  <StatusListCount statusCounts={this.props.statusCounts} />
                ) : (
                  ''
                )}
              </div>
            </ColSelBtn>

            <DataTable
              isExpanded={true}
              isSort={true}
              // isColumnFilter={true}
              columns={this.state.columns}
              data={
                // dummyTableData1
                this.props.markdowns
              }
              isPagination={true}
              // visibleColumns="markDownStatus"
              getColumnProps={(column) => ({
                // onClick: () => this.onColumnClick(column),
              })}
              getCellProps={(cellInfo) => ({
                onClick: () => this.onCellClick(cellInfo),
                style: {
                  backgroundColor: this.bgmCol(cellInfo),
                  color: this.txtCol(cellInfo),
                  margin: '4px 4px',
                  borderRadius: '2px',
                },
              })}
            />
          </div>
        ) : (
          <NoData description="Please select filters to show Markdown plans." />
        )}
        <Modal
          size="sm"
          show={this.state.isComment}
          header="Add Comment"
          onHide={this.onCloseComment}
          onSaveComment={this.onSaveComment}>
          <textarea id="commentTextArea" rows="2" cols="20" onChange={this.setComment}></textarea>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = (store) => {
  return {
    markdowns: store.draftStatusReducer.markdowns,
    getColumnHeaders: store.draftStatusReducer.columnHeaders,
    getStatusOptions: store.draftStatusReducer.statusOptions,
    selectedHierarchy1: store.filtersReducer.selectedHierarchy1,
    selectedHierarchy2: store.filtersReducer.selectedHierarchy2,
    selectedHierarchy3: store.filtersReducer.selectedHierarchy3,
    selectedHierarchy4: store.filtersReducer.selectedHierarchy4,
    selectedHierarchy5: store.filtersReducer.selectedHierarchy5,
    selectedHierarchy6: store.filtersReducer.selectedHierarchy6,
    selectedHierarchy7: store.filtersReducer.selectedHierarchy7,
    selectedHierarchy8: store.filtersReducer.selectedHierarchy8,
    selectedStores: store.filtersReducer.selectedStores,
    startDate: store.filtersReducer.startDate,
    endDate: store.filtersReducer.endDate,
  };
};
const mapActionToProps = {
  // getMarkdowns: DraftAction.getMarkdowns,
};

export default connect(mapStateToProps, mapActionToProps)(Status);

const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;

  .flexRow {
    display: flex;
    flex-direction: row;
  }
`;
